	if (!start || !end) {
		throw new Error('"start" and "end" must be provided');
	}
	const total = await Messages.findAllNumberOfTransferredRooms({
		start,
		end,
		departmentId,
		onlyCount: true,
	}).toArray();
	return {
		departments: await Messages.findAllNumberOfTransferredRooms({
			start,
