import { FieldElement } from '../types';

export default (element: FieldElement): element is HTMLInputElement =>
  element.type === 'checkbox';
export default (data: unknown) => data instanceof Date;
export default (value: unknown): value is null | undefined => value == null;
import isDateObject from './isDateObject';
import isNullOrUndefined from './isNullOrUndefined';

export const isObjectType = (value: unknown) => typeof value === 'object';

export default <T extends object>(value: unknown): value is T =>
  !isNullOrUndefined(value) &&
  !Array.isArray(value) &&
  isObjectType(value) &&
  !isDateObject(value);
import isCheckBoxInput from '../utils/isCheckBoxInput';
import isObject from '../utils/isObject';

type Event = { target: any };

export default (event: unknown) =>
  isObject(event) && (event as Event).target
    ? isCheckBoxInput((event as Event).target)
      ? (event as Event).target.checked
      : (event as Event).target.value
    : event;
export default (name: string) => name.substring(0, name.search(/.\d/)) || name;
import { InternalFieldName } from '../types';

import getNodeParentName from './getNodeParentName';

export default (names: Set<InternalFieldName>, name: InternalFieldName) =>
  [...names].some((current) => getNodeParentName(name) === current);
export default (value: any[]) => (value || []).filter(Boolean);
export default (val: unknown): val is undefined => val === undefined;
import compact from './compact';
import isNullOrUndefined from './isNullOrUndefined';
import isObject from './isObject';
import isUndefined from './isUndefined';

export default <T>(obj: T, path: string, defaultValue?: unknown) => {
  if (isObject(obj) && path) {
    const result = compact(path.split(/[,[\].]+?/)).reduce(
      (result, key) => (isNullOrUndefined(result) ? result : result[key]),
      obj,
    );

    return isUndefined(result) || result === obj
      ? isUndefined(obj[path as keyof T])
        ? defaultValue
        : obj[path as keyof T]
      : result;
  }

  return undefined;
};
import { ValidationMode } from './types';

export const EVENTS = {
  BLUR: 'blur',
  CHANGE: 'change',
};

export const VALIDATION_MODE: ValidationMode = {
  onBlur: 'onBlur',
  onChange: 'onChange',
  onSubmit: 'onSubmit',
  onTouched: 'onTouched',
  all: 'all',
};

export const INPUT_VALIDATION_RULES = {
  max: 'max',
  min: 'min',
  maxLength: 'maxLength',
  minLength: 'minLength',
  pattern: 'pattern',
  required: 'required',
  validate: 'validate',
};
export default <Key extends string, T extends Record<Key, any>>(
  source: T,
  key: Key,
): Omit<T, Key> => {
  const copy = { ...source };
  delete copy[key];

  return copy;
};
import React from 'react';

import omit from './utils/omit';
