        ["banners.".concat(banner.id)]: banner
      }
    };
    return this.updateOne(query, update);
  } // Voip functions


  findOneByAgentUsername(username, options) {
    const query = {
      username,
      roles: 'livechat-agent'
    };
    return this.findOne(query, options);
  }

  findOneByExtension(extension, options) {
    const query = {
      extension
    };
    return this.findOne(query, options);
  }

  findByExtensions(extensions, options) {
    const query = {
      extension: {
        $in: extensions
      }
    };
    return this.find(query, options);
  }

  getVoipExtensionByUserId(userId, options) {
    const query = {
      _id: userId,
      extension: {
        $exists: true
      }
    };
    return this.findOne(query, options);
  }

  setExtension(userId, extension) {
    const query = {
      _id: userId
    };
    const update = {
      $set: {
        extension
      }
    };
    return this.update(query, update);
  }

  unsetExtension(userId) {
    const query = {
      _id: userId
    };
    const update = {
      $unset: {
        extension: true
      }
    };
    return this.update(query, update);
  }

  getAvailableAgentsIncludingExt(includeExt, text, options) {
    const query = {
      roles: {
        $in: ['livechat-agent', 'livechat-manager', 'livechat-monitor']
      },
      $and: [{
        $or: [...(includeExt ? [{
          extension: includeExt
        }] : []), {
          extension: {
            $exists: false
          }
        }]
      }, ...(text && text.trim() ? [{
        $or: [{
          username: escapeRegExp(text)
        }, {
          name: escapeRegExp(text)
        }]
      }] : [])]
    };
    return this.find(query, options);
  }

