      if (!defaultBusinessHour) {
        return options;
      }

      const businessHourToOpen = Promise.await(filterBusinessHoursThatMustBeOpened([defaultBusinessHour]));

      if (!businessHourToOpen.length) {
        return options;
      }

      Promise.await(this.UsersRepository.addBusinessHourByAgentIds(agentIdsWithoutDepartment, defaultBusinessHour._id));
      return options;
    });
  }

  openBusinessHour(businessHour) {
    return Promise.asyncApply(() => {
      return openBusinessHour(businessHour);
    });
  }

  removeBusinessHourFromRemovedDepartmentsUsersIfNeeded(businessHourId, departmentsToRemove) {
    return Promise.asyncApply(() => {
      if (!departmentsToRemove.length) {
        return;
      }

      const agentIds = Promise.await(this.DepartmentsAgentsRepository.findByDepartmentIds(departmentsToRemove).toArray()).map(dept => dept.agentId);
      Promise.await(removeBusinessHourByAgentIds(agentIds, businessHourId));
    });
  }

  closeBusinessHour(businessHour) {
    return Promise.asyncApply(() => {
      closeBusinessHour(businessHour);
    });
  }

}

