	return {
		...license,
		modules: flatModules(license.modules),
	};
}

API.v1.addRoute(
	'licenses.get',
	{ authRequired: true },
	{
		get() {
			if (!hasPermission(this.userId, 'view-privileged-setting')) {
				return API.v1.unauthorized();
