			const licenses = getLicenses()
				.filter(({ valid }) => valid)
				.map(({ license }) => licenseTransform(license));

			return API.v1.success({ licenses });
		},
	},
);

API.v1.addRoute(
	'licenses.add',
	{ authRequired: true },
	{
		post() {
			check(this.bodyParams, {
				license: String,
			});

			if (!hasPermission(this.userId, 'edit-privileged-setting')) {
				return API.v1.unauthorized();
