				{ _id },
				{
					$set: { ...settingOverwrittenProps },
					...(removedKeys.length && {
						$unset: removedKeys.reduce((unset, key) => ({ ...unset, [key]: 1 }), {}),
					}),
				},
			);
			return;
		}

