module.export({
  LDAPConnection: () => LDAPConnection
});
let ldapjs;
module.link("ldapjs", {
  default(v) {
    ldapjs = v;
  }

}, 0);
let settings;
module.link("../../../app/settings/server", {
  settings(v) {
    settings = v;
  }

}, 1);
let logger, connLogger, searchLogger, authLogger, bindLogger, mapLogger;
module.link("./Logger", {
  logger(v) {
    logger = v;
  },

  connLogger(v) {
    connLogger = v;
  },

  searchLogger(v) {
    searchLogger = v;
  },

  authLogger(v) {
    authLogger = v;
  },

  bindLogger(v) {
    bindLogger = v;
  },

  mapLogger(v) {
    mapLogger = v;
