		return restrictionFlag;
	},
};

proxyquire.noCallThru().load('../../../../server/patches/airGappedRestrictionsWrapper.ts', {
	'@rocket.chat/license': {
		AirGappedRestriction: airgappedModule,
	},
	'../../../app/license/server/airGappedRestrictionsWrapper': {
		applyAirGappedRestrictionsValidation,
	},
});

describe('#airGappedRestrictionsWrapper()', () => {
	it('should throw an error when the workspace is restricted', async () => {
		await expect(applyAirGappedRestrictionsValidation(sinon.stub())).to.be.rejectedWith('restricted-workspace');
	});
	it('should NOT throw an error when the workspace is not restricted', async () => {
		restrictionFlag = false;
		const spy = sinon.stub();
		await expect(applyAirGappedRestrictionsValidation(spy)).to.eventually.equal(undefined);
		expect(spy.calledOnce).to.be.true;
	});
});

import { makeFunction } from '@rocket.chat/patch-injection';

export const applyAirGappedRestrictionsValidation = makeFunction(async <T>(fn: () => Promise<T>): Promise<T> => {
	return fn();
});

import { AirGappedRestriction } from '@rocket.chat/license';

import { applyAirGappedRestrictionsValidation } from '../../../app/license/server/airGappedRestrictionsWrapper';

applyAirGappedRestrictionsValidation.patch(async <T>(_: any, fn: () => Promise<T>): Promise<T> => {
	if (AirGappedRestriction.restricted) {
		throw new Error('restricted-workspace');
	}
	return fn();
});

import { RoomType } from '@rocket.chat/apps-engine/definition/rooms';
import { expect } from 'chai';
import proxyquire from 'proxyquire';
import sinon from 'sinon';

const { FederatedRoomEE } = proxyquire.noCallThru().load('../../../../../../../../server/local-services/federation/domain/FederatedRoom', {
	mongodb: {
		'ObjectId': class ObjectId {
			toHexString(): string {
				return 'hexString';
			}
		},
		'@global': true,
	},
});
const { FederatedUserEE } = proxyquire.noCallThru().load('../../../../../../../../server/local-services/federation/domain/FederatedUser', {
	mongodb: {
		'ObjectId': class ObjectId {
			toHexString(): string {
				return 'hexString';
			}
		},
		'@global': true,
	},
});
const { FederationDirectMessageRoomServiceSender } = proxyquire
	.noCallThru()
	.load('../../../../../../../../server/local-services/federation/application/room/sender/DirectMessageRoomServiceSender', {
		mongodb: {
			'ObjectId': class ObjectId {
				toHexString(): string {
					return 'hexString';
				}
			},
			'@global': true,
		},
	});

describe('FederationEE - Application - FederationDirectMessageRoomServiceSender', () => {
	let service: typeof FederationDirectMessageRoomServiceSender;
	const roomAdapter = {
		getFederatedRoomByInternalId: sinon.stub(),
		updateFederatedRoomByInternalRoomId: sinon.stub(),
	};
	const userAdapter = {
		getFederatedUserByExternalId: sinon.stub(),
		getFederatedUserByInternalId: sinon.stub(),
		createFederatedUser: sinon.stub(),
		getInternalUserById: sinon.stub(),
		getFederatedUserByInternalUsername: sinon.stub(),
		createLocalUser: sinon.stub(),
		getInternalUserByUsername: sinon.stub(),
	};
	const settingsAdapter = {
		getHomeServerDomain: sinon.stub().returns('localDomain'),
	};
	const bridge = {
		getUserProfileInformation: sinon.stub().resolves({}),
		extractHomeserverOrigin: sinon.stub(),
		createUser: sinon.stub(),
		createDirectMessageRoom: sinon.stub(),
		joinRoom: sinon.stub(),
	};
	const invitees = [
		{
			inviteeUsernameOnly: 'marcos.defendi',
			normalizedInviteeId: 'marcos.defendi:matrix.com',
			rawInviteeId: '@marcos.defendi:matrix.com',
		},
	];

	beforeEach(() => {
		service = new FederationDirectMessageRoomServiceSender(
			roomAdapter as any,
			userAdapter as any,
			{} as any,
			settingsAdapter as any,
			bridge as any,
		);
	});

	afterEach(() => {
		roomAdapter.getFederatedRoomByInternalId.reset();
		roomAdapter.updateFederatedRoomByInternalRoomId.reset();
		userAdapter.getFederatedUserByInternalId.reset();
		userAdapter.getInternalUserById.reset();
		userAdapter.createFederatedUser.reset();
		userAdapter.getFederatedUserByInternalUsername.reset();
		userAdapter.getFederatedUserByExternalId.reset();
		userAdapter.createLocalUser.reset();
		userAdapter.getInternalUserByUsername.reset();
		bridge.extractHomeserverOrigin.reset();
		bridge.createUser.reset();
		bridge.createDirectMessageRoom.reset();
		bridge.joinRoom.reset();
	});

	describe('#onDirectMessageRoomCreation()', () => {
		const user = FederatedUserEE.createInstance('externalInviterId', {
			name: 'normalizedInviterId',
			username: 'normalizedInviterId',
			existsOnlyOnProxyServer: true,
		});
		const room = FederatedRoomEE.createInstance('externalRoomId', 'normalizedRoomId', user, RoomType.CHANNEL, 'externalRoomName');

		it('should NOT create the inviter user both externally and internally if it already exists', async () => {
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			expect(bridge.createUser.called).to.be.false;
		});

		it('should create the inviter user both externally and internally if it does not exists', async () => {
			userAdapter.getFederatedUserByInternalId.onCall(0).resolves(undefined);
			userAdapter.getFederatedUserByInternalId.onCall(1).resolves(user);
			userAdapter.getInternalUserById.resolves({ username: 'username', name: 'name' } as any);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			bridge.createUser.resolves('externalInviterId');
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			const inviter = FederatedUserEE.createInstance('externalInviterId', {
				name: 'name',
				username: 'username',
				existsOnlyOnProxyServer: true,
			});
			expect(bridge.createUser.calledWith('username', 'name', 'localDomain')).to.be.true;
			expect(userAdapter.createFederatedUser.calledWith(inviter)).to.be.true;
		});

		it('should throw an error if the inviter does not exists', async () => {
			userAdapter.getFederatedUserByInternalId.resolves(undefined);
			userAdapter.getInternalUserById.resolves({ username: 'username', name: 'name' } as any);

			await expect(
				service.onDirectMessageRoomCreation({ invitees, internalInviterId: 'internalInviterId', internalRoomId: 'internalRoomId' } as any),
			).to.be.rejectedWith('User with internalId internalInviterId not found');
		});

		it('should create the external room with all (the external) the invitees, the inviter is from the same homeserver and at least one invitee is external', async () => {
			bridge.extractHomeserverOrigin.onCall(0).returns('matrix.com');
			bridge.extractHomeserverOrigin.returns('localDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(undefined);
			bridge.createDirectMessageRoom.resolves('externalRoomId');

			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			expect(
				bridge.createDirectMessageRoom.calledWith(
					user.getExternalId(),
					invitees.map((invitee) => invitee.rawInviteeId),
				),
			).to.be.true;
			expect(roomAdapter.updateFederatedRoomByInternalRoomId.calledWith('internalRoomId', 'externalRoomId')).to.be.true;
		});

		it('should automatically join all the invitees who are original from the same homeserver', async () => {
			bridge.extractHomeserverOrigin.onCall(0).returns('matrix.com');
			bridge.extractHomeserverOrigin.returns('localDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(undefined);
			bridge.createDirectMessageRoom.resolves('externalRoomId');

			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			invitees.forEach((invitee) => expect(bridge.joinRoom.calledWith('externalRoomId', invitee.rawInviteeId)).to.be.true);
		});

		it('should NOT create the external room with any invitee when all of them are local only and the inviter is from the same homeserver', async () => {
			bridge.extractHomeserverOrigin.returns('localDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(undefined);
			bridge.createDirectMessageRoom.resolves('externalRoomId');

			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			expect(bridge.createDirectMessageRoom.called).to.be.false;
			expect(roomAdapter.updateFederatedRoomByInternalRoomId.called).to.be.false;
		});

		it('should NOT create the external room with all the invitees when the inviter is NOT from the same homeserver', async () => {
			bridge.extractHomeserverOrigin.returns('externalDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(undefined);
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			expect(bridge.createDirectMessageRoom.called).to.be.false;
			expect(roomAdapter.updateFederatedRoomByInternalRoomId.called).to.be.false;
		});

		it('should create the invitee user if it does not exists and it is from the same home server, but he is not the only one, there is also an external invitee', async () => {
			bridge.extractHomeserverOrigin.onCall(0).returns('matrix.com');
			bridge.extractHomeserverOrigin.returns('localDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			userAdapter.getFederatedUserByInternalUsername.onCall(0).resolves(undefined);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			const invitee = FederatedUserEE.createInstance(invitees[0].rawInviteeId, {
				name: invitees[0].inviteeUsernameOnly,
				username: invitees[0].inviteeUsernameOnly,
				existsOnlyOnProxyServer: true,
			});

			expect(userAdapter.createFederatedUser.calledWith(invitee)).to.be.true;
		});

		it('should create the invitee user if it does not exists and it is NOT from the same home server', async () => {
			bridge.extractHomeserverOrigin.returns('externalDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			userAdapter.getFederatedUserByInternalUsername.onCall(0).resolves(undefined);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			const invitee = FederatedUserEE.createInstance(invitees[0].rawInviteeId, {
				name: invitees[0].normalizedInviteeId,
				username: invitees[0].normalizedInviteeId,
				existsOnlyOnProxyServer: false,
			});

			expect(userAdapter.createFederatedUser.calledWith(invitee)).to.be.true;
		});

		it('should NOT create the invitee user if it already exists', async () => {
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			expect(userAdapter.createFederatedUser.called).to.be.false;
		});

		it('should NOT create the user on the proxy homeserver if it is NOT from the same homeserver, which means is a external user', async () => {
			bridge.extractHomeserverOrigin.returns('externalDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			expect(bridge.createUser.called).to.be.false;
		});

		it('should throw an error if the invitee is from the same home server but the federated user does not exists and also there is at least one external user', async () => {
			bridge.extractHomeserverOrigin.onCall(0).returns('matrix.com');
			bridge.extractHomeserverOrigin.returns('localDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			userAdapter.getFederatedUserByInternalUsername.resolves(undefined);

			await expect(
				service.onDirectMessageRoomCreation({ invitees, internalInviterId: 'internalInviterId', internalRoomId: 'internalRoomId' } as any),
			).to.be.rejectedWith(`User with internalUsername ${invitees[0].inviteeUsernameOnly} not found`);
		});

		it('should NOT create the user on the proxy homeserver if it is from the same home server AND already exists on it', async () => {
			bridge.extractHomeserverOrigin.returns('localDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			bridge.getUserProfileInformation.resolves({});
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			expect(bridge.createUser.called).to.be.false;
		});

		it('should create the user on the proxy home server if it is from the same home server AND does not exists there yet', async () => {
			bridge.extractHomeserverOrigin.returns('externalDomain');
			userAdapter.getFederatedUserByInternalId.resolves(user);
			roomAdapter.getFederatedRoomByInternalId.resolves(room);
			userAdapter.getFederatedUserByInternalUsername.resolves(user);
			bridge.getUserProfileInformation.resolves(undefined);
			await service.onDirectMessageRoomCreation({
				invitees,
				internalInviterId: 'internalInviterId',
				internalRoomId: 'internalRoomId',
			} as any);

			const invitee = FederatedUserEE.createInstance(invitees[0].rawInviteeId, {
				name: invitees[0].normalizedInviteeId,
				username: invitees[0].normalizedInviteeId,
				existsOnlyOnProxyServer: false,
			});

			expect(bridge.createUser.calledWith(invitees[0].inviteeUsernameOnly, invitee.getUsername(), 'localDomain')).to.be.false;
		});
	});

	describe('#beforeDirectMessageRoomCreation()', () => {
		it('should create the invitee locally for each external user', async () => {
			bridge.extractHomeserverOrigin.onCall(0).returns('externalDomain');
			bridge.extractHomeserverOrigin.onCall(1).returns('localDomain');
			await service.beforeDirectMessageRoomCreation({
				invitees: [
					...invitees,
					{
						inviteeUsernameOnly: 'marcos.defendiNotToBeInvited',
						normalizedInviteeId: 'marcos.defendi:matrix.comNotToBeInvited',
						rawInviteeId: '@marcos.defendi:matrix.comNotToBeInvited',
					},
				],
			} as any);

			const invitee = FederatedUserEE.createLocalInstanceOnly({
				name: invitees[0].normalizedInviteeId,
				username: invitees[0].normalizedInviteeId,
				existsOnlyOnProxyServer: false,
			});

			expect(userAdapter.createLocalUser.calledOnceWithExactly(invitee)).to.be.true;
		});
	});
});

import { RoomType } from '@rocket.chat/apps-engine/definition/rooms';
import type { IDirectMessageRoom, IMessage, IRoom } from '@rocket.chat/core-typings';
import { ObjectId } from 'mongodb'; // This should not be in the domain layer, but its a known "problem"

import type { FederatedUser } from './FederatedUser';

export const isAnInternalIdentifier = (fromOriginName: string, localOriginName: string): boolean => {
	return fromOriginName === localOriginName;
};

export abstract class AbstractFederatedRoom {
	protected externalId: string;

	protected internalId: string;

	protected internalReference: Partial<IRoom>;

	protected constructor({ externalId, internalReference }: { externalId: string; internalReference: Partial<IRoom> }) {
		this.externalId = externalId;
		this.internalReference = internalReference;
		this.internalId = internalReference._id || new ObjectId().toHexString();
	}

	protected static generateTemporaryName(normalizedExternalId: string): string {
		return `Federation-${normalizedExternalId}`;
	}

	public abstract isDirectMessage(): boolean;

	public getExternalId(): string {
		return this.externalId;
	}

	public getRoomType(): RoomType {
		return this.internalReference.t as RoomType;
	}

	public getInternalId(): string {
		return this.internalId;
	}

	public getDisplayName(): string | undefined {
		return this.internalReference.fname;
	}

	public getName(): string | undefined {
		return this.internalReference.name;
	}

	public getTopic(): string | undefined {
		return this.internalReference.topic;
	}

	public static isOriginalFromTheProxyServer(fromOriginName: string, proxyServerOriginName: string): boolean {
		return isAnInternalIdentifier(fromOriginName, proxyServerOriginName);
	}

	public getInternalReference(): Readonly<Partial<IRoom>> {
		return Object.freeze({
			...this.internalReference,
			_id: this.internalId,
		});
	}

	public getCreatorUsername(): string | undefined {
		return this.internalReference.u?.username;
	}

	public isTheCreator(userId: string): boolean {
		return this.internalReference.u?._id === userId;
	}

	public getCreatorId(): string | undefined {
		return this.internalReference.u?._id;
	}

	public changeRoomType(type: RoomType): void {
		if (this.isDirectMessage()) {
			throw new Error('Its not possible to change a direct message type');
		}
		this.internalReference.t = type;
	}

	public changeDisplayRoomName(displayName: string): void {
		if (this.isDirectMessage()) {
			throw new Error('Its not possible to change a direct message name');
		}
		this.internalReference.fname = displayName;
	}

	public shouldUpdateRoomName(aRoomName: string): boolean {
		return this.internalReference?.name !== aRoomName && !this.isDirectMessage();
	}

	public changeRoomName(name: string): void {
		if (this.isDirectMessage()) {
			throw new Error('Its not possible to change a direct message name');
		}
		this.internalReference.name = name;
	}

	public changeRoomTopic(topic: string): void {
		if (this.isDirectMessage()) {
			throw new Error('Its not possible to change a direct message topic');
		}
		this.internalReference.topic = topic;
	}

	public shouldUpdateDisplayRoomName(aRoomName: string): boolean {
		return this.internalReference?.fname !== aRoomName && !this.isDirectMessage();
	}

	public shouldUpdateRoomTopic(aRoomTopic: string): boolean {
		return this.internalReference?.topic !== aRoomTopic && !this.isDirectMessage();
	}

	public static shouldUpdateMessage(newMessageText: string, originalMessage: IMessage): boolean {
		return originalMessage.msg !== newMessageText;
	}
}

export class FederatedRoom extends AbstractFederatedRoom {
	protected constructor({ externalId, internalReference }: { externalId: string; internalReference: Partial<IRoom> }) {
		super({ externalId, internalReference });
	}

	public static createInstance(
		externalId: string,
		normalizedExternalId: string,
		creator: FederatedUser,
		type: RoomType,
		displayName?: string,
		name?: string,
	): FederatedRoom {
		if (type === RoomType.DIRECT_MESSAGE) {
			throw new Error('For DMs please use the specific class');
		}
		const roomName = displayName || FederatedRoom.generateTemporaryName(normalizedExternalId);
		return new FederatedRoom({
			externalId,
			internalReference: {
				t: type,
				fname: roomName,
				name,
				u: creator.getInternalReference(),
			},
		});
	}

	public static createWithInternalReference(externalId: string, internalReference: IRoom): FederatedRoom {
		if (internalReference.t === RoomType.DIRECT_MESSAGE) {
			throw new Error('For DMs please use the specific class');
		}
		return new FederatedRoom({
			externalId,
			internalReference,
		});
	}

	public isDirectMessage(): boolean {
		return false;
	}
}

export class DirectMessageFederatedRoom extends AbstractFederatedRoom {
	public members: FederatedUser[];

	protected constructor({
		externalId,
		internalReference,
		members,
	}: {
		externalId: string;
		internalReference: Partial<IRoom>;
		members: FederatedUser[];
	}) {
		super({ externalId, internalReference });
		this.members = members;
	}

	public static createInstance(externalId: string, creator: FederatedUser, members: FederatedUser[]): DirectMessageFederatedRoom {
		return new DirectMessageFederatedRoom({
			externalId,
			members,
			internalReference: {
				t: RoomType.DIRECT_MESSAGE,
				u: creator.getInternalReference(),
			},
		});
	}

	public static createWithInternalReference<T extends IRoom | IDirectMessageRoom>(
		externalId: string,
		internalReference: T,
		members: FederatedUser[],
	): DirectMessageFederatedRoom {
		return new DirectMessageFederatedRoom({
			externalId,
			internalReference,
			members,
		});
	}

	public getMembersUsernames(): string[] {
		return this.members.map((user) => user.getUsername() || '').filter(Boolean);
	}

	public getMembers(): FederatedUser[] {
		return this.members;
	}

	public addMember(member: FederatedUser): void {
		this.members.push(member);
	}

	public isDirectMessage(): boolean {
		return true;
	}

	public isUserPartOfTheRoom(federatedUser: FederatedUser): boolean {
		if (!federatedUser.getUsername()) {
			return false;
		}
		if (!this.internalReference?.usernames) {
			return false;
		}
		return this.internalReference.usernames.includes(federatedUser.getUsername() as string);
	}
}

import type { IUser } from '@rocket.chat/core-typings';
import { UserStatus } from '@rocket.chat/core-typings';
import { ObjectId } from 'mongodb'; // This should not be in the domain layer, but its a known "problem"

import { isAnInternalIdentifier } from './FederatedRoom';

interface IFederatedUserCreationParams {
	name: string;
	username: string;
	existsOnlyOnProxyServer: boolean;
}

export class FederatedUser {
	protected externalId: string;

	protected internalId: string;

	protected existsOnlyOnProxyServer: boolean;

	protected internalReference: IUser;

	protected constructor({
		externalId,
		internalReference,
		existsOnlyOnProxyServer,
	}: {
		externalId: string;
		internalReference: IUser;
		existsOnlyOnProxyServer: boolean;
	}) {
		this.externalId = externalId;
		this.existsOnlyOnProxyServer = existsOnlyOnProxyServer;
		this.internalReference = internalReference;
		this.internalId = internalReference._id || new ObjectId().toHexString();
	}

	public static createInstance(externalId: string, params: IFederatedUserCreationParams): FederatedUser {
		return new FederatedUser({
			externalId,
			existsOnlyOnProxyServer: params.existsOnlyOnProxyServer,
			internalReference: FederatedUser.createLocalInstanceOnly(params),
		});
	}

	public static createLocalInstanceOnly(params: IFederatedUserCreationParams): IUser {
		return {
			username: params.username,
			name: params.name,
			type: 'user',
			status: UserStatus.ONLINE,
			active: true,
			roles: ['user'],
			requirePasswordChange: false,
			federated: !params.existsOnlyOnProxyServer,
		} as unknown as IUser;
	}

	public static createWithInternalReference(externalId: string, existsOnlyOnProxyServer: boolean, internalReference: IUser): FederatedUser {
		return new FederatedUser({
			externalId,
			existsOnlyOnProxyServer,
			internalReference,
		});
	}

	public getInternalReference(): Readonly<IUser> {
		return Object.freeze({
			...this.internalReference,
			_id: this.internalId,
		});
	}

	public getStorageRepresentation(): Readonly<IUser> {
		return {
			_id: this.internalId,
			username: this.internalReference.username || '',
			type: this.internalReference.type,
			status: this.internalReference.status,
			active: this.internalReference.active,
			roles: this.internalReference.roles,
			name: this.internalReference.name,
			requirePasswordChange: this.internalReference.requirePasswordChange,
			createdAt: new Date(),
			_updatedAt: new Date(),
			federated: this.isRemote(),
		};
	}

	public getUsername(): string | undefined {
		return this.internalReference?.username;
	}

	public getName(): string | undefined {
		return this.internalReference?.name;
	}

	public static isOriginalFromTheProxyServer(fromOriginName: string, localOriginName: string): boolean {
		return isAnInternalIdentifier(fromOriginName, localOriginName);
	}

	public getExternalId(): string {
		return this.externalId;
	}

	public isRemote(): boolean {
		return !this.existsOnlyOnProxyServer;
	}

	public shouldUpdateFederationAvatar(federationAvatarUrl: string): boolean {
		return this.internalReference.federation?.avatarUrl !== federationAvatarUrl;
	}

	public shouldUpdateDisplayName(displayName: string): boolean {
		return this.internalReference.name !== displayName;
	}

	public getInternalId(): string {
		return this.internalId;
	}
}

import type { RocketChatFileAdapter } from '../../../../../../../server/services/federation/infrastructure/rocket-chat/adapters/File';
import type { RocketChatSettingsAdapter } from '../../../../../../../server/services/federation/infrastructure/rocket-chat/adapters/Settings';
import { FederatedUserEE } from '../../../domain/FederatedUser';
import type { IFederationBridgeEE } from '../../../domain/IFederationBridge';
import type { RocketChatRoomAdapterEE } from '../../../infrastructure/rocket-chat/adapters/Room';
import type { RocketChatUserAdapterEE } from '../../../infrastructure/rocket-chat/adapters/User';
import { AbstractFederationApplicationServiceEE } from '../../AbstractFederationApplicationServiceEE';
import type {
	FederationBeforeDirectMessageRoomCreationDto,
	FederationCreateDirectMessageDto,
	FederationOnDirectMessageRoomCreationDto,
	FederationRoomInviteUserDto,
} from './input/RoomSenderDto';

export class FederationDirectMessageRoomServiceSender extends AbstractFederationApplicationServiceEE {
	constructor(
		protected internalRoomAdapter: RocketChatRoomAdapterEE,
		protected internalUserAdapter: RocketChatUserAdapterEE,
		protected internalFileAdapter: RocketChatFileAdapter,
		protected internalSettingsAdapter: RocketChatSettingsAdapter,
		protected bridge: IFederationBridgeEE,
	) {
		super(bridge, internalUserAdapter, internalFileAdapter, internalSettingsAdapter);
	}

	public async onDirectMessageRoomCreation(dmRoomOnCreationInput: FederationOnDirectMessageRoomCreationDto): Promise<void> {
		const { internalRoomId, internalInviterId, invitees, inviteComesFromAnExternalHomeServer } = dmRoomOnCreationInput;

		const atLeastOneExternalUser = invitees.some(
			(invitee) =>
				!FederatedUserEE.isOriginalFromTheProxyServer(
					this.bridge.extractHomeserverOrigin(invitee.rawInviteeId),
					this.internalHomeServerDomain,
				),
		);
		if (invitees.length === 0 || inviteComesFromAnExternalHomeServer || !atLeastOneExternalUser) {
			return;
		}
		await this.createExternalDirectMessageRoomAndInviteUsers({
			internalInviterId,
			internalRoomId,
			invitees,
			inviteComesFromAnExternalHomeServer,
		});
	}

	public async beforeDirectMessageRoomCreation(dmBeforeRoomCreationInput: FederationBeforeDirectMessageRoomCreationDto): Promise<void> {
		const { invitees = [] } = dmBeforeRoomCreationInput;

		if (invitees.length === 0) {
			return;
		}

		await this.createUsersLocallyOnly(invitees);
	}

	public async createInternalLocalDirectMessageRoom(dmRoomCreateInput: FederationCreateDirectMessageDto): Promise<void> {
		const { internalInviterId, invitees } = dmRoomCreateInput;

		await this.internalRoomAdapter.createLocalDirectMessageRoom(invitees, internalInviterId);
	}

	private async createExternalDirectMessageRoomAndInviteUsers(
		dmRoomOnCreationInput: FederationOnDirectMessageRoomCreationDto,
	): Promise<void> {
		const { internalRoomId, internalInviterId, invitees } = dmRoomOnCreationInput;

		const inviterUser = await this.internalUserAdapter.getFederatedUserByInternalId(internalInviterId);
		if (!inviterUser) {
			const internalUser = await this.internalUserAdapter.getInternalUserById(internalInviterId);
			const username = internalUser.username || internalInviterId;
			const name = internalUser.name || internalInviterId;
			const existsOnlyOnProxyServer = true;
			const externalInviterId = await this.bridge.createUser(username, name, this.internalHomeServerDomain);

			await this.createFederatedUserInternallyOnly(externalInviterId, username, existsOnlyOnProxyServer, name);
		}

		const federatedInviterUser = inviterUser || (await this.internalUserAdapter.getFederatedUserByInternalId(internalInviterId));
		if (!federatedInviterUser) {
			throw new Error(`User with internalId ${internalInviterId} not found`);
		}

		const isInviterFromTheSameHomeServer = FederatedUserEE.isOriginalFromTheProxyServer(
			this.bridge.extractHomeserverOrigin(federatedInviterUser.getExternalId()),
			this.internalHomeServerDomain,
		);
		const internalFederatedRoom = await this.internalRoomAdapter.getFederatedRoomByInternalId(internalRoomId);

		await Promise.all(
			invitees.map((member) =>
				this.createUserForDirectMessageRoom({
					internalInviterId,
					internalRoomId,
					inviteeUsernameOnly: member.inviteeUsernameOnly,
					normalizedInviteeId: member.normalizedInviteeId,
					rawInviteeId: member.rawInviteeId,
				}),
			),
		);

		if (internalFederatedRoom || !isInviterFromTheSameHomeServer) {
			return;
		}

		const allInviteeExternalIds = invitees.map((invitee) => invitee.rawInviteeId);
		const externalRoomId = await this.bridge.createDirectMessageRoom(federatedInviterUser.getExternalId(), allInviteeExternalIds, {
			internalRoomId,
		});
		const inviteesFromTheSameHomeServer = invitees.filter((invitee) =>
			FederatedUserEE.isOriginalFromTheProxyServer(
				this.bridge.extractHomeserverOrigin(invitee.rawInviteeId),
				this.internalHomeServerDomain,
			),
		);
		await Promise.all(inviteesFromTheSameHomeServer.map((invitee) => this.bridge.joinRoom(externalRoomId, invitee.rawInviteeId)));
		await this.internalRoomAdapter.updateFederatedRoomByInternalRoomId(internalRoomId, externalRoomId);
	}

	private async createUserForDirectMessageRoom(roomInviteUserInput: FederationRoomInviteUserDto): Promise<void> {
		const { normalizedInviteeId, inviteeUsernameOnly, rawInviteeId } = roomInviteUserInput;

		const isInviteeFromTheSameHomeServer = FederatedUserEE.isOriginalFromTheProxyServer(
			this.bridge.extractHomeserverOrigin(rawInviteeId),
			this.internalHomeServerDomain,
		);

		const username = isInviteeFromTheSameHomeServer ? inviteeUsernameOnly : normalizedInviteeId;
		const existsOnlyOnProxyServer = isInviteeFromTheSameHomeServer;
		const inviteeUser = await this.internalUserAdapter.getFederatedUserByInternalUsername(username);
		if (!inviteeUser) {
			await this.createFederatedUserInternallyOnly(rawInviteeId, username, existsOnlyOnProxyServer);
		}

		if (!isInviteeFromTheSameHomeServer) {
			return;
		}

		const federatedInviteeUser = inviteeUser || (await this.internalUserAdapter.getFederatedUserByInternalUsername(inviteeUsernameOnly));
		if (!federatedInviteeUser) {
			throw new Error(`User with internalUsername ${inviteeUsernameOnly} not found`);
		}

		const profile = await this.bridge.getUserProfileInformation(federatedInviteeUser.getExternalId());
		if (profile) {
			return;
		}

		await this.bridge.createUser(
			inviteeUsernameOnly,
			federatedInviteeUser.getName() || federatedInviteeUser.getUsername() || inviteeUsernameOnly,
			this.internalHomeServerDomain,
		);
	}
}

import { AbstractFederationApplicationService } from '../../../../../server/services/federation/application/AbstractFederationApplicationService';
import type { RocketChatFileAdapter } from '../../../../../server/services/federation/infrastructure/rocket-chat/adapters/File';
import type { RocketChatSettingsAdapter } from '../../../../../server/services/federation/infrastructure/rocket-chat/adapters/Settings';
import { FederatedUserEE } from '../domain/FederatedUser';
import type { IFederationBridgeEE } from '../domain/IFederationBridge';
import type { RocketChatUserAdapterEE } from '../infrastructure/rocket-chat/adapters/User';
import type { IFederationInviteeDto } from './room/sender/input/RoomSenderDto';

export abstract class AbstractFederationApplicationServiceEE extends AbstractFederationApplicationService {
	constructor(
		protected bridge: IFederationBridgeEE,
		protected internalUserAdapter: RocketChatUserAdapterEE,
		protected internalFileAdapter: RocketChatFileAdapter,
		protected internalSettingsAdapter: RocketChatSettingsAdapter,
	) {
		super(bridge, internalUserAdapter, internalFileAdapter, internalSettingsAdapter);
	}

	protected async createUsersLocallyOnly(invitees: IFederationInviteeDto[]): Promise<void> {
		const externalUsersToBeCreatedLocally = invitees.filter(
			(invitee) =>
				!FederatedUserEE.isOriginalFromTheProxyServer(
					this.bridge.extractHomeserverOrigin(invitee.rawInviteeId),
					this.internalHomeServerDomain,
				),
		);

		for await (const invitee of externalUsersToBeCreatedLocally) {
			const externalUserProfileInformation = await this.bridge.getUserProfileInformation(invitee.rawInviteeId);

			const name = externalUserProfileInformation?.displayName || invitee.normalizedInviteeId;
			const username = invitee.normalizedInviteeId;
			const existsOnlyOnProxyServer = false;

			await this.internalUserAdapter.createLocalUser(
				FederatedUserEE.createLocalInstanceOnly({
					username,
					name,
					existsOnlyOnProxyServer,
				}),
			);

			const federatedUser = await this.internalUserAdapter.getFederatedUserByExternalId(invitee.rawInviteeId);
			if (!federatedUser) {
				return;
			}
			await this.updateUserAvatarInternally(federatedUser, externalUserProfileInformation?.avatarUrl);
			await this.updateUserDisplayNameInternally(federatedUser, externalUserProfileInformation?.displayName);
		}
	}
}

import type { IUser } from '@rocket.chat/core-typings';

import { FederatedUser } from '../domain/FederatedUser';
import type { IFederationBridge } from '../domain/IFederationBridge';
import type { RocketChatFileAdapter } from '../infrastructure/rocket-chat/adapters/File';
import type { RocketChatSettingsAdapter } from '../infrastructure/rocket-chat/adapters/Settings';
import type { RocketChatUserAdapter } from '../infrastructure/rocket-chat/adapters/User';

export abstract class AbstractFederationApplicationService {
	protected internalHomeServerDomain: string;

	constructor(
		protected bridge: IFederationBridge,
		protected internalUserAdapter: RocketChatUserAdapter,
		protected internalFileAdapter: RocketChatFileAdapter,
		protected internalSettingsAdapter: RocketChatSettingsAdapter,
	) {
		this.internalHomeServerDomain = this.internalSettingsAdapter.getHomeServerDomain();
	}

	protected async createFederatedUserInternallyOnly(
		externalUserId: string,
		username: string,
		existsOnlyOnProxyServer = false,
		providedName?: string,
	): Promise<void> {
		const internalUser = await this.internalUserAdapter.getInternalUserByUsername(username);
		const externalUserProfileInformation = await this.bridge.getUserProfileInformation(externalUserId);
		let federatedUser;
		if (internalUser) {
			federatedUser = FederatedUser.createWithInternalReference(externalUserId, existsOnlyOnProxyServer, internalUser);
		} else {
			const name = externalUserProfileInformation?.displayName || providedName || username;
			federatedUser = FederatedUser.createInstance(externalUserId, {
				name,
				username,
				existsOnlyOnProxyServer,
			});
		}
		await this.internalUserAdapter.createFederatedUser(federatedUser);
		const insertedUser = await this.internalUserAdapter.getFederatedUserByExternalId(externalUserId);
		if (!insertedUser) {
			return;
		}
		await this.updateUserAvatarInternally(insertedUser, externalUserProfileInformation?.avatarUrl);
		await this.updateUserDisplayNameInternally(insertedUser, externalUserProfileInformation?.displayName);
	}

	protected async updateUserAvatarInternally(federatedUser: FederatedUser, avatarUrl?: string): Promise<void> {
		if (!avatarUrl) {
			return;
		}
		if (!federatedUser.isRemote()) {
			return;
		}
		if (federatedUser.shouldUpdateFederationAvatar(avatarUrl)) {
			await this.internalUserAdapter.setAvatar(
				federatedUser,
				await this.bridge.convertMatrixUrlToHttp(federatedUser.getExternalId(), avatarUrl),
			);
			await this.internalUserAdapter.updateFederationAvatar(federatedUser.getInternalId(), avatarUrl);
		}
	}

	protected async updateUserDisplayNameInternally(federatedUser: FederatedUser, displayName?: string): Promise<void> {
		if (!displayName) {
			return;
		}
		if (!federatedUser.isRemote()) {
			return;
		}
		if (federatedUser.shouldUpdateDisplayName(displayName)) {
			await this.internalUserAdapter.updateRealName(federatedUser.getInternalReference(), displayName);
		}
	}

	protected async createFederatedUserIncludingHomeserverUsingLocalInformation(internalInviterId: string): Promise<string> {
		const internalUser = await this.internalUserAdapter.getInternalUserById(internalInviterId);
		if (!internalUser || !internalUser?.username) {
			throw new Error(`Could not find user id for ${internalInviterId}`);
		}
		const name = internalUser.name || internalUser.username;
		const externalInviterId = await this.bridge.createUser(internalUser.username, name, this.internalHomeServerDomain);
		const existsOnlyOnProxyServer = true;
		await this.createFederatedUserInternallyOnly(externalInviterId, internalUser.username, existsOnlyOnProxyServer, name);
		await this.updateUserAvatarExternally(
			internalUser,
			(await this.internalUserAdapter.getFederatedUserByExternalId(externalInviterId)) as FederatedUser,
		);

		return externalInviterId;
	}

	protected async updateUserAvatarExternally(internalUser: IUser, externalInviter: FederatedUser): Promise<void> {
		if (!internalUser.username) {
			return;
		}
		const buffer = await this.internalFileAdapter.getBufferForAvatarFile(internalUser.username);
		if (!buffer) {
			return;
		}
		const avatarFileRecord = await this.internalFileAdapter.getFileMetadataForAvatarFile(internalUser.username);
		if (!avatarFileRecord?.type || !avatarFileRecord?.name) {
			return;
		}
		const externalFileUri = await this.bridge.uploadContent(externalInviter.getExternalId(), buffer, {
			type: avatarFileRecord.type,
			name: avatarFileRecord.name,
		});
		if (!externalFileUri) {
			return;
		}
		await this.internalUserAdapter.updateFederationAvatar(internalUser._id, externalFileUri);
		await this.bridge.setUserAvatar(externalInviter.getExternalId(), externalFileUri);
	}
}

