		it('should return undefined if the command is valid but there is no string params', async () => {
			expect(await executeSlashCommand('federation', '', {}, validCommands)).to.be.equal(undefined);
		});

		it('should return undefined if there is no currentUserId', async () => {
			expect(await executeSlashCommand('federation', 'params', {}, validCommands)).to.be.equal(undefined);
		});

		it('should return undefined if the provided command is invalid/inexistent', async () => {
			expect(await executeSlashCommand('federation', 'params', {}, validCommands)).to.be.equal(undefined);
		});

		it('should throw an error if the provided invitee is invalid (without any ":")', async () => {
			try {
				await executeSlashCommand('federation', 'dm @user.server.com', {}, validCommands, currentUserId);
			} catch (e: any) {
				expect(e.message).to.be.equal('At least one user must be external');
			}
		});

		it('should throw an error if the inviter does not exist', async () => {
			try {
				await executeSlashCommand('federation', 'dm @user:server.com', {}, validCommands, currentUserId);
			} catch (e: any) {
				expect(e.message).to.be.equal('At least one user must be external');
			}
		});

		it('should throw an error if there is no external users', async () => {
			try {
				await executeSlashCommand('federation', 'dm @user1 @user2', {}, validCommands, currentUserId);
			} catch (e: any) {
				expect(e.message).to.be.equal('At least one user must be external');
			}
		});

		it('should throw an error if the LOCAL inviter is trying to invite another LOCAL user', async () => {
			try {
				await executeSlashCommand('federation', 'dm @normalUser:server.com', {}, validCommands, currentUserId);
			} catch (e: any) {
				expect(e.message).to.be.equal('At least one user must be external');
			}
		});

		it('should call the command function without any error', async () => {
			await executeSlashCommand('federation', 'dm @external:server.com', { rid: 'roomId' }, validCommands, currentUserId);
			expect(command.calledWith(currentUserId, 'roomId', ['@external:server.com'])).to.be.true;
		});
	});
});

import { Users } from '@rocket.chat/models';

import { normalizeExternalInviteeId } from '../../../../../../../server/services/federation/infrastructure/rocket-chat/slash-commands/action';

const validateInvitees = async (invitees: string[], inviterId: string): Promise<void> => {
	const atLeastOneExternal = invitees.some((invitee) => invitee.includes(':'));
	const inviter = await Users.findOneById(inviterId);
	const isInviterExternal = inviter?.federated === true || inviter?.username?.includes(':');
	if (!atLeastOneExternal && !isInviterExternal) {
		throw new Error('At least one user must be external');
	}
};

export const executeSlashCommand = async (
	providedCommand: string,
	stringParams: string | undefined,
	item: Record<string, any>,
	commands: Record<string, (currentUserId: string, roomId: string, invitees: string[]) => Promise<void>>,
	currentUserId?: string | null,
): Promise<void> => {
	if (providedCommand !== 'federation' || !stringParams) {
		return;
	}
	const [command, ...externalUserIdsToInvite] = stringParams.trim().split(' ');
	if (!currentUserId || !commands[command]) {
		return;
	}

	await validateInvitees(externalUserIdsToInvite, currentUserId);

	const invitees = externalUserIdsToInvite.map((rawUserId) => normalizeExternalInviteeId(rawUserId));

	const { rid: roomId } = item;

	await commands[command](currentUserId, roomId, invitees);
};

import { Users } from '@rocket.chat/models';

export const normalizeExternalInviteeId = (rawInviteeId: string): string => `@${rawInviteeId.replace(/@/g, '')}`;

const validateExternalInviteeIdFormat = async (rawInviteeId: string, inviterId: string): Promise<void> => {
	const inviter = await Users.findOneById(inviterId);
	const isInviterExternal = inviter?.federated === true || inviter?.username?.includes(':');
	const localUserInvitingAnotherLocalUser = !rawInviteeId.includes(':') && !isInviterExternal;
	if (localUserInvitingAnotherLocalUser) {
		throw new Error('Invalid userId format for federation command.');
	}
};

export const executeSlashCommand = async (
	providedCommand: string,
	stringParams: string | undefined,
	item: Record<string, any>,
	commands: Record<string, (currentUserId: string, roomId: string, invitee: string) => Promise<void>>,
	currentUserId?: string | null,
): Promise<void> => {
	if (providedCommand !== 'federation' || !stringParams) {
		return;
	}

	const [command, ...params] = stringParams.trim().split(' ');
	const [rawUserId] = params;
	if (!currentUserId || !commands[command]) {
		return;
	}

	await validateExternalInviteeIdFormat(rawUserId, currentUserId);

	const invitee = normalizeExternalInviteeId(rawUserId);

	const { rid: roomId } = item;

	await commands[command](currentUserId, roomId, invitee);
};

import type { IUser } from '@rocket.chat/core-typings';

import { hasAllPermissionAsync, hasAtLeastOnePermissionAsync } from '../../authorization/server/functions/hasPermission';
import { apiDeprecationLogger } from '../../lib/server/lib/deprecationWarningLogger';

type RequestMethod = 'GET' | 'POST' | 'PUT' | 'DELETE' | '*';
export type PermissionsPayload = {
	[key in RequestMethod]?: {
		operation: 'hasAll' | 'hasAny';
		permissions: string[];
	};
};

type PermissionsPayloadLight = {
	[key in RequestMethod]?: string[];
};

type PermissionsRequiredKey = string[] | PermissionsPayload | PermissionsPayloadLight;

const isLegacyPermissionsPayload = (permissionsPayload: PermissionsRequiredKey): permissionsPayload is string[] => {
	return Array.isArray(permissionsPayload);
};

const isLightPermissionsPayload = (permissionsPayload: PermissionsRequiredKey): permissionsPayload is PermissionsPayloadLight => {
	return (
		typeof permissionsPayload === 'object' &&
		Object.keys(permissionsPayload).some((key) => ['GET', 'POST', 'PUT', 'DELETE', '*'].includes(key.toUpperCase())) &&
		Object.values(permissionsPayload).every((value) => Array.isArray(value))
	);
};

const isPermissionsPayload = (permissionsPayload: PermissionsRequiredKey): permissionsPayload is PermissionsPayload => {
	return (
		typeof permissionsPayload === 'object' &&
		Object.keys(permissionsPayload).some((key) => ['GET', 'POST', 'PUT', 'DELETE', '*'].includes(key.toUpperCase())) &&
		Object.values(permissionsPayload).every((value) => typeof value === 'object' && value.operation && value.permissions)
	);
};

export async function checkPermissionsForInvocation(
	userId: IUser['_id'],
	permissionsPayload: PermissionsPayload,
	requestMethod: RequestMethod,
): Promise<boolean> {
	const permissions = permissionsPayload[requestMethod] || permissionsPayload['*'];

	if (!permissions) {
		// how we reached here in the first place?
		return false;
	}

	if (permissions.permissions.length === 0) {
		// You can pass an empty array of permissions to allow access to the method
		return true;
	}

	if (permissions.operation === 'hasAll') {
		return hasAllPermissionAsync(userId, permissions.permissions);
	}

	if (permissions.operation === 'hasAny') {
		return hasAtLeastOnePermissionAsync(userId, permissions.permissions);
	}

	return false;
}

// We'll assume options only contains permissionsRequired, as we don't care of the other elements
export function checkPermissions(options: { permissionsRequired?: PermissionsRequiredKey }) {
	if (!options.permissionsRequired) {
		return false;
	}

	if (isPermissionsPayload(options.permissionsRequired)) {
		// No modifications needed
		return true;
	}

	if (isLegacyPermissionsPayload(options.permissionsRequired)) {
		options.permissionsRequired = {
			'*': {
				operation: 'hasAll',
				permissions: options.permissionsRequired,
			},
		};
		return true;
	}

	if (isLightPermissionsPayload(options.permissionsRequired)) {
		Object.keys(options.permissionsRequired).forEach((method) => {
			const methodKey = method as RequestMethod;
			// @ts-expect-error -- we know the type of the value but ts refuses to infer it
			options.permissionsRequired[methodKey] = {
				operation: 'hasAll',
				// @ts-expect-error -- we know the type of the value but ts refuses to infer it
				permissions: options.permissionsRequired[methodKey],
			};
		});
		return true;
	}

	// If reached here, options.permissionsRequired contained an invalid payload
	return false;
}

export function parseDeprecation(methodThis: any, { alternatives, version }: { version: string; alternatives?: string[] }): void {
	const infoMessage = alternatives?.length ? ` Please use the alternative(s): ${alternatives.join(',')}` : '';
	apiDeprecationLogger.endpoint(methodThis.request.route, version, methodThis.response, infoMessage);
}

