import { removeDangerousProps } from './cleanQuery';

type Query = { [k: string]: any };

export const isValidQuery: {
	(query: Query, allowedAttributes: string[], allowedOperations: string[]): boolean;
	errors: string[];
} = Object.assign(
	(query: Query, allowedAttributes: string[], allowedOperations: string[]): boolean => {
		isValidQuery.errors = [];
		if (!(query instanceof Object)) {
			throw new Error('query must be an object');
		}

		// eslint-disable-next-line @typescript-eslint/no-use-before-define
		return verifyQuery(query, allowedAttributes, allowedOperations);
	},
	{
		errors: [],
	},
);

const verifyQuery = (query: Query, allowedAttributes: string[], allowedOperations: string[], parent = ''): boolean => {
	return Object.entries(removeDangerousProps(query)).every(([key, value]) => {
		const path = parent ? `${parent}.${key}` : key;
		if (parent === '' && path.startsWith('$')) {
			if (!allowedOperations.includes(path)) {
				isValidQuery.errors.push(`Invalid operation: ${path}`);
				return false;
			}
			if (!Array.isArray(value)) {
				isValidQuery.errors.push(`Invalid parameter for operation: ${path} : ${value}`);
				return false;
			}
			return value.every((v) => verifyQuery(v, allowedAttributes, allowedOperations));
		}

		if (
			!allowedAttributes.some((allowedAttribute) => {
				if (allowedAttribute.endsWith('.*')) {
					return path.startsWith(allowedAttribute.replace('.*', ''));
				}
				if (allowedAttribute.endsWith('*') && !allowedAttribute.endsWith('.*')) {
					return true;
				}
				return path === allowedAttribute;
			})
		) {
			isValidQuery.errors.push(`Invalid attribute: ${path}`);
			return false;
		}

		if (value instanceof Object) {
			return verifyQuery(value, allowedAttributes, allowedOperations, path);
		}
		return true;
	});
};

type Query = { [k: string]: any };

const denyList = ['constructor', '__proto__', 'prototype'];

export const removeDangerousProps = (v: Query): Query => {
	const query = Object.create(null);
	for (const key in v) {
		if (v.hasOwnProperty(key) && !denyList.includes(key)) {
			query[key] = v[key];
		}
	}

	return query;
};
/* @deprecated */
export function clean(v: Query, allowList: string[] = []): Query {
	const typedParam = removeDangerousProps(v);
	if (v instanceof Object) {
		for (const key in typedParam) {
			if (key.startsWith('$') && !allowList.includes(key)) {
				delete typedParam[key];
			} else {
				clean(typedParam[key], allowList);
			}
		}
	}
	return typedParam;
}

import type { ISubscription, IUser, IRoom } from '@rocket.chat/core-typings';
import { Rooms, Subscriptions, Users } from '@rocket.chat/models';
import type { AnyBulkWriteOperation } from 'mongodb';

import { notifyOnRoomChanged, notifyOnSubscriptionChanged } from '../../../lib/server/lib/notifyListener';

export async function resetRoomKey(roomId: string, userId: string, newRoomKey: string, newRoomKeyId: string) {
	const user = await Users.findOneById<Pick<IUser, 'e2e'>>(userId, { projection: { e2e: 1 } });
	if (!user) {
		throw new Error('error-user-not-found');
	}

	if (!user.e2e?.private_key || !user.e2e?.public_key) {
		throw new Error('error-user-has-no-keys');
	}

	const room = await Rooms.findOneById<Pick<IRoom, 'e2eKeyId'>>(roomId, { projection: { e2eKeyId: 1 } });
	if (!room) {
		throw new Error('error-room-not-found');
	}

	if (!room.e2eKeyId) {
		throw new Error('error-room-not-encrypted');
	}

	// We will update the subs of everyone who has a key for the room. For the ones that don't have, we will do nothing
	const notifySubs = [];
	const updateOps: AnyBulkWriteOperation<ISubscription>[] = [];
	const e2eQueue: IRoom['usersWaitingForE2EKeys'] = [];

	for await (const sub of Subscriptions.find({
		rid: roomId,
		$or: [{ E2EKey: { $exists: true } }, { E2ESuggestedKey: { $exists: true } }],
	})) {
		// This replicates the oldRoomKeys array modifications allowing us to have the changes locally without finding them again
		// which allows for quicker notifying
		const keys = replicateMongoSlice(room.e2eKeyId, sub);
		delete sub.E2ESuggestedKey;
		delete sub.E2EKey;
		delete sub.suggestedOldRoomKeys;

		const updateSet = {
			$set: {
				...(keys && { oldRoomKeys: keys }),
			},
		};
		updateOps.push({
			updateOne: {
				filter: { _id: sub._id },
				update: {
					$unset: { E2EKey: 1, E2ESuggestedKey: 1, suggestedOldRoomKeys: 1 },
					...(Object.keys(updateSet.$set).length && updateSet),
				},
			},
		});

		if (userId !== sub.u._id) {
			// Avoid notifying requesting user as notify will happen at the end
			notifySubs.push({
				...sub,
				...(keys && { oldRoomKeys: keys }),
			});

			// This is for allowing the key distribution process to start inmediately
			pushToLimit(e2eQueue, { userId: sub.u._id, ts: new Date() });
		}

		if (updateOps.length >= 100) {
			await writeAndNotify(updateOps, notifySubs);
		}
	}

	if (updateOps.length > 0) {
		await writeAndNotify(updateOps, notifySubs);
	}

	// after the old keys have been moved to the new prop, store room key on room + the e2e queue so key can be exchanged
	// todo move to model method
	const roomResult = await Rooms.resetRoomKeyAndSetE2EEQueueByRoomId(roomId, newRoomKeyId, e2eQueue);
	// And set the new key to the user that called the func
	const result = await Subscriptions.setE2EKeyByUserIdAndRoomId(userId, roomId, newRoomKey);

	if (result.value) {
		void notifyOnSubscriptionChanged(result.value);
	}
	if (roomResult.value) {
		void notifyOnRoomChanged(roomResult.value);
	}
}

export function pushToLimit(
	arr: NonNullable<IRoom['usersWaitingForE2EKeys']>,
	item: NonNullable<IRoom['usersWaitingForE2EKeys']>[number],
	limit = 50,
) {
	if (arr.length < limit) {
		arr.push(item);
	}
}

async function writeAndNotify(updateOps: AnyBulkWriteOperation<ISubscription>[], notifySubs: ISubscription[]) {
	await Subscriptions.col.bulkWrite([...updateOps]);
	notifySubs.forEach((sub) => {
		void notifyOnSubscriptionChanged(sub);
	});
	notifySubs.length = 0;
	updateOps.length = 0;
}

export function replicateMongoSlice(keyId: string, sub: ISubscription) {
	if (!sub.E2EKey) {
		return;
	}

	if (!sub.oldRoomKeys) {
		return [{ e2eKeyId: keyId, ts: new Date(), E2EKey: sub.E2EKey }];
	}

	const sortedKeys = sub.oldRoomKeys.toSorted((a, b) => b.ts.getTime() - a.ts.getTime());
	sortedKeys.unshift({ e2eKeyId: keyId, ts: new Date(), E2EKey: sub.E2EKey });

	return sortedKeys.slice(0, 10);
}

import type { ServerMethods } from '@rocket.chat/ddp-client';
import { Subscriptions, Rooms } from '@rocket.chat/models';
import { Meteor } from 'meteor/meteor';

import { methodDeprecationLogger } from '../../../lib/server/lib/deprecationWarningLogger';
import {
	notifyOnSubscriptionChangedById,
	notifyOnSubscriptionChanged,
	notifyOnRoomChangedById,
} from '../../../lib/server/lib/notifyListener';

declare module '@rocket.chat/ddp-client' {
	// eslint-disable-next-line @typescript-eslint/naming-convention
	interface ServerMethods {
		'e2e.updateGroupKey'(rid: string, uid: string, key: string): Promise<void>;
	}
}

export async function updateGroupKey(rid: string, uid: string, key: string, callerUserId: string) {
	// I have a subscription to this room
	const mySub = await Subscriptions.findOneByRoomIdAndUserId(rid, callerUserId);

	if (mySub) {
		// Setting the key to myself, can set directly to the final field
		if (callerUserId === uid) {
			const setGroupE2EKeyResponse = await Subscriptions.setGroupE2EKey(mySub._id, key);
			// Case: I create an encrypted room before setting up my keys, and I reset the e2e keys
			// Next login, I'll create the keys for the room, and set them here.
			// However as I reset my keys, I'm on the `usersWaitingForKeys` queue
			// So I need to remove myself from the queue and notify the time i reach here
			// This way, I can provide the keys to other users
			const { modifiedCount } = await Rooms.removeUsersFromE2EEQueueByRoomId(mySub.rid, [callerUserId]);
			if (setGroupE2EKeyResponse.modifiedCount) {
				void notifyOnSubscriptionChangedById(mySub._id);
			}
			if (modifiedCount) {
				void notifyOnRoomChangedById(mySub.rid);
			}
			return;
		}

		// uid also has subscription to this room
		const { value } = await Subscriptions.setGroupE2ESuggestedKey(uid, rid, key);
		if (value) {
			void notifyOnSubscriptionChanged(value);
		}
	}
}

Meteor.methods<ServerMethods>({
	async 'e2e.updateGroupKey'(rid, uid, key) {
		const userId = Meteor.userId();
		if (!userId) {
			throw new Meteor.Error('error-invalid-user', 'Invalid user', { method: 'e2e.acceptSuggestedGroupKey' });
		}

		methodDeprecationLogger.method('e2e.updateGroupKey', '8.0.0');
		return updateGroupKey(rid, uid, key, userId);
	},
});

import { escapeRegExp } from '@rocket.chat/string-helpers';

import type { EmojiCategory, EmojiItem } from '.';
import { emoji } from './lib';

export const CUSTOM_CATEGORY = 'rocket';

export const createPickerEmojis = (
	customItemsLimit: number,
	actualTone: number,
	recentEmojis: string[],
	setRecentEmojis: (emojis: string[]) => void,
) => {
	const categories = getCategoriesList();

	const mappedCategories = categories.map((category) => ({
		key: category.key,
		i18n: category.i18n,
		emojis: {
			list: createEmojiList(category.key, actualTone, recentEmojis, setRecentEmojis),
			limit: category.key === CUSTOM_CATEGORY ? customItemsLimit : null,
		},
	}));

	return mappedCategories;
};

export const createEmojiList = (
	category: string,
	actualTone: number | null,
	recentEmojis: string[],
	setRecentEmojis: (emojis: string[]) => void,
) => {
	const emojiList: EmojiItem[] = [];
	const emojiPackages = Object.values(emoji.packages);

	emojiPackages.forEach((emojiPackage) => {
		if (!emojiPackage.emojisByCategory?.[category]) {
			return;
		}

		const total = emojiPackage.emojisByCategory[category].length;

		for (let i = 0; i < total; i++) {
			const current = emojiPackage.emojisByCategory[category][i];

			const tone = actualTone && actualTone > 0 && emojiPackage.toneList.hasOwnProperty(current) ? `_tone${actualTone}` : '';

			const emojiToRender = `:${current}${tone}:`;

			if (!emoji.list[emojiToRender]) {
				removeFromRecent(emojiToRender, recentEmojis, setRecentEmojis);
				return;
			}

			const image = emojiPackage.renderPicker(emojiToRender);
			if (!image) {
				continue;
			}
			emojiList.push({ emoji: current, image });
		}
	});

	return emojiList;
};

export const getCategoriesList = () => {
	let categoriesList: EmojiCategory[] = [];

	for (const emojiPackage of Object.values(emoji.packages)) {
		if (emojiPackage.emojiCategories) {
			if (typeof emojiPackage.categoryIndex !== 'undefined') {
				categoriesList.splice(emojiPackage.categoryIndex, 0, ...emojiPackage.emojiCategories);
			} else {
				categoriesList = categoriesList.concat(emojiPackage.emojiCategories);
			}
		}
	}

	const rocketPosition = categoriesList.findIndex((category) => category.key === CUSTOM_CATEGORY);
	const rocketCategory = categoriesList.splice(rocketPosition, 1)[0];
	categoriesList.push(rocketCategory);

	return categoriesList;
};

export const getEmojisBySearchTerm = (
	searchTerm: string,
	actualTone: number,
	recentEmojis: string[],
	setRecentEmojis: (emojis: string[]) => void,
) => {
	const emojis = [];
	const searchRegExp = new RegExp(escapeRegExp(searchTerm.replace(/:/g, '')), 'i');

	for (let current in emoji.list) {
		if (!emoji.list.hasOwnProperty(current)) {
			continue;
		}

		if (searchRegExp.test(current)) {
			const emojiObject = emoji.list[current];
			const { emojiPackage, shortnames = [] } = emojiObject;
			let tone = '';
			current = current.replace(/:/g, '');
			const alias = shortnames[0] !== undefined ? shortnames[0].replace(/:/g, '') : shortnames[0];

			if (actualTone > 0 && emoji.packages[emojiPackage].toneList.hasOwnProperty(current)) {
				tone = `_tone${actualTone}`;
			}

			let emojiFound = false;

			for (const key in emoji.packages[emojiPackage].emojisByCategory) {
				if (emoji.packages[emojiPackage].emojisByCategory.hasOwnProperty(key)) {
					const contents = emoji.packages[emojiPackage].emojisByCategory[key];
					const searchValArray = alias !== undefined ? alias.replace(/:/g, '').split('_') : alias;
					if (contents.indexOf(current) !== -1 || searchValArray?.includes(searchTerm)) {
						emojiFound = true;
						break;
					}
				}
			}

			if (emojiFound) {
				const emojiToRender = `:${current}${tone}:`;

				if (!emoji.list[emojiToRender]) {
					removeFromRecent(emojiToRender, recentEmojis, setRecentEmojis);
					break;
				}

				emojis.push({ emoji: current, image: emoji.packages[emojiPackage].renderPicker(emojiToRender) });
			}
		}
	}

	return emojis;
};

export const removeFromRecent = (emoji: string, recentEmojis: string[], setRecentEmojis?: (emojis: string[]) => void) => {
	const _emoji = emoji.replace(/(^:|:$)/g, '');
	const pos = recentEmojis.indexOf(_emoji as never);

	if (pos === -1) {
		return;
	}
	recentEmojis.splice(pos, 1);
	setRecentEmojis?.(recentEmojis);
};

export const updateRecent = (recentList: string[]) => {
	const recentPkgList: string[] = emoji.packages.base.emojisByCategory.recent;
	recentList?.forEach((_emoji) => {
		!recentPkgList.includes(_emoji) && recentPkgList.push(_emoji);
	});
};

export const replaceEmojiInRecent = ({ oldEmoji, newEmoji }: { oldEmoji: string; newEmoji: string }) => {
	const recentPkgList: string[] = emoji.packages.base.emojisByCategory.recent;
	const pos = recentPkgList.indexOf(oldEmoji);

	if (pos !== -1) {
		recentPkgList[pos] = newEmoji;
	}
};

const getEmojiRender = (emojiName: string) => {
	const emojiPackageName = emoji.list[emojiName]?.emojiPackage;
	const emojiPackage = emoji.packages[emojiPackageName];
	return emojiPackage?.render(emojiName);
};

export const getFrequentEmoji = (frequentEmoji: string[]) => {
	return frequentEmoji?.map((frequentEmoji) => {
		return { emoji: frequentEmoji, image: getEmojiRender(`:${frequentEmoji}:`) };
	});
};

