import type { IImportMessageRecord, IMessage as IDBMessage, IImportMessage, IImportMessageReaction } from '@rocket.chat/core-typings';
import { Rooms } from '@rocket.chat/models';
import limax from 'limax';

import { insertMessage } from '../../../../lib/server/functions/insertMessage';
import type { IConversionCallbacks } from '../../definitions/IConversionCallbacks';
import type { UserIdentification, MentionedChannel } from './ConverterCache';
import { RecordConverter } from './RecordConverter';

export type MessageConversionCallbacks = IConversionCallbacks & { afterImportAllMessagesFn?: (roomIds: string[]) => Promise<void> };

type MessageObject = Record<string, any>;

type MentionedUser = {
	_id: string;
	username: string;
	name?: string;
};

type IMessageReaction = {
	name: string;
	usernames: string[];
};

type IMessageReactions = Record<string, IMessageReaction>;

export class MessageConverter extends RecordConverter<IImportMessageRecord> {
	private rids: string[] = [];

	async convertData({ afterImportAllMessagesFn, ...callbacks }: MessageConversionCallbacks = {}): Promise<void> {
		this.rids = [];
		await super.convertData(callbacks);

		await this.resetLastMessages();
		if (afterImportAllMessagesFn) {
			await afterImportAllMessagesFn(this.rids);
		}
	}

	protected async resetLastMessages(): Promise<void> {
		for await (const rid of this.rids) {
			try {
				await Rooms.resetLastMessageById(rid, null);
			} catch (e) {
				this._logger.warn(`Failed to update last message of room ${rid}`);
				this._logger.error(e);
			}
		}
	}

	protected async insertMessage(data: IImportMessage): Promise<void> {
		if (!data.ts || isNaN(data.ts as unknown as number)) {
			throw new Error('importer-message-invalid-timestamp');
		}

		const creator = await this._cache.findImportedUser(data.u._id);
		if (!creator) {
			this._logger.warn(`Imported user not found: ${data.u._id}`);
			throw new Error('importer-message-unknown-user');
		}
		const rid = await this._cache.findImportedRoomId(data.rid);
		if (!rid) {
			throw new Error('importer-message-unknown-room');
		}
		if (!this.rids.includes(rid)) {
			this.rids.push(rid);
		}

		const msgObj = await this.buildMessageObject(data, rid, creator);

		try {
			await insertMessage(creator, msgObj as unknown as IDBMessage, rid, true);
		} catch (e) {
			this._logger.warn(`Failed to import message with timestamp ${String(msgObj.ts)} to room ${rid}`);
			this._logger.error(e);
		}
	}

	protected async convertRecord(record: IImportMessageRecord): Promise<boolean> {
		await this.insertMessage(record.data);
		return true;
	}

	protected async buildMessageObject(data: IImportMessage, rid: string, creator: UserIdentification): Promise<MessageObject> {
		// Convert the mentions and channels first because these conversions can also modify the msg in the message object
		const mentions = data.mentions && (await this.convertMessageMentions(data));
		const channels = data.channels && (await this.convertMessageChannels(data));

		return {
			rid,
			u: {
				_id: creator._id,
				username: creator.username,
			},
			msg: data.msg,
			ts: data.ts,
			t: data.t || undefined,
			groupable: data.groupable,
			tmid: data.tmid,
			tlm: data.tlm,
			tcount: data.tcount,
			replies: data.replies && (await this.convertMessageReplies(data.replies)),
			editedAt: data.editedAt,
			editedBy: data.editedBy && ((await this._cache.findImportedUser(data.editedBy)) || undefined),
			mentions,
			channels,
			_importFile: data._importFile,
			url: data.url,
			attachments: data.attachments,
			bot: data.bot,
			emoji: data.emoji,
			alias: data.alias,
			...(data._id ? { _id: data._id } : {}),
			...(data.reactions ? { reactions: await this.convertMessageReactions(data.reactions) } : {}),
		};
	}

	protected async convertMessageChannels(message: IImportMessage): Promise<MentionedChannel[] | undefined> {
		const { channels } = message;
		if (!channels) {
			return;
		}

		const result: MentionedChannel[] = [];
		for await (const importId of channels) {
			const { name, _id } = (await this.getMentionedChannelData(importId)) || {};

			if (!_id || !name) {
				this._logger.warn(`Mentioned room not found: ${importId}`);
				continue;
			}

			message.msg = message.msg.replace(new RegExp(`\#${importId}`, 'gi'), `#${name}`);

			result.push({
				_id,
				name,
			});
		}

		return result;
	}

	protected async convertMessageMentions(message: IImportMessage): Promise<MentionedUser[] | undefined> {
		const { mentions } = message;
		if (!mentions) {
			return undefined;
		}

		const result: MentionedUser[] = [];
		for await (const importId of mentions) {
			if (importId === ('all' as 'string') || importId === 'here') {
				result.push({
					_id: importId,
					username: importId,
				});
				continue;
			}

			// Loading the name will also store the remaining data on the cache if it's missing, so this won't run two queries
			const name = await this._cache.findImportedUserDisplayName(importId);
			const data = await this._cache.findImportedUser(importId);

			if (!data) {
				this._logger.warn(`Mentioned user not found: ${importId}`);
				continue;
			}

			if (!data.username) {
				this._logger.debug(importId);
				throw new Error('importer-message-mentioned-username-not-found');
			}

			message.msg = message.msg.replace(new RegExp(`\@${importId}`, 'gi'), `@${data.username}`);

			result.push({
				_id: data._id,
				username: data.username as 'string',
				name,
			});
		}
		return result;
	}

	protected async convertMessageReactions(
		importedReactions: Record<string, IImportMessageReaction>,
	): Promise<undefined | IMessageReactions> {
		const reactions: IMessageReactions = {};

		for await (const name of Object.keys(importedReactions)) {
			if (!importedReactions.hasOwnProperty(name)) {
				continue;
			}
			const { users } = importedReactions[name];

			if (!users.length) {
				continue;
			}

			const reaction: IMessageReaction = {
				name,
				usernames: [],
			};

			for await (const importId of users) {
				const username = await this._cache.findImportedUsername(importId);
				if (username && !reaction.usernames.includes(username)) {
					reaction.usernames.push(username);
				}
			}

			if (reaction.usernames.length) {
				reactions[name] = reaction;
			}
		}

		if (Object.keys(reactions).length > 0) {
			return reactions;
		}
	}

	protected async convertMessageReplies(replies: string[]): Promise<string[]> {
		const result: string[] = [];
		for await (const importId of replies) {
			const userId = await this._cache.findImportedUserId(importId);
			if (userId && !result.includes(userId)) {
				result.push(userId);
			}
		}
		return result;
	}

	protected async getMentionedChannelData(importId: string): Promise<MentionedChannel | undefined> {
		// loading the name will also store the id on the cache if it's missing, so this won't run two queries
		const name = await this._cache.findImportedRoomName(importId);
		const _id = await this._cache.findImportedRoomId(importId);

		if (name && _id) {
			return {
				name,
				_id,
			};
		}

		// If the importId was not found, check if we have a room with that name
		const roomName = limax(importId.trim(), { maintainCase: true });

		const room = await Rooms.findOneByNonValidatedName(roomName, { projection: { name: 1 } });
		if (room?.name) {
			this._cache.addRoom(importId, room._id);
			this._cache.addRoomName(importId, room.name);

			return {
				name: room.name,
				_id: room._id,
			};
		}
	}

	protected getDataType(): 'message' {
		return 'message';
	}
}

import type { IImportRecord } from '@rocket.chat/core-typings';
import { Logger } from '@rocket.chat/logger';
import { ImportData } from '@rocket.chat/models';
import { Random } from '@rocket.chat/random';
import { type FindCursor, ObjectId } from 'mongodb';

import type { IConversionCallbacks } from '../../definitions/IConversionCallbacks';
import { ConverterCache } from './ConverterCache';

export type RecordConverterOptions = {
	workInMemory?: boolean;
	deleteDbData?: boolean;
};

export class RecordConverter<R extends IImportRecord, T extends RecordConverterOptions = RecordConverterOptions> {
	protected _logger: Logger;

	protected _cache: ConverterCache;

	protected _converterOptions: RecordConverterOptions;

	protected _options: Omit<T, keyof RecordConverterOptions>;

	protected _records: R[];

	protected skippedCount = 0;

	protected failedCount = 0;

	public aborted = false;

	constructor(options?: T, logger?: Logger, cache?: ConverterCache) {
		const { workInMemory = false, deleteDbData = false, ...customOptions } = options || ({} as T);
		this._converterOptions = {
			workInMemory,
			deleteDbData,
		};
		this._options = customOptions;

		this._logger = logger || new Logger(`Data Importer - ${this.constructor.name}`);
		this._cache = cache || new ConverterCache();
		this._records = [];
	}

	private skipMemoryRecord(_id: string): void {
		const record = this.getMemoryRecordById(_id);
		if (!record) {
			return;
		}

		record.skipped = true;
	}

	private async skipDatabaseRecord(_id: string): Promise<void> {
		await ImportData.updateOne(
			{
				_id,
			},
			{
				$set: {
					skipped: true,
				},
			},
		);
	}

	protected async skipRecord(_id: string): Promise<void> {
		this.skippedCount++;
		this.skipMemoryRecord(_id);
		if (!this._converterOptions.workInMemory) {
			return this.skipDatabaseRecord(_id);
		}
	}

	private saveErrorToMemory(importId: string, error: Error): void {
		const record = this.getMemoryRecordById(importId);

		if (!record) {
			return;
		}

		if (!record.errors) {
			record.errors = [];
		}

		record.errors.push({
			message: error.message,
			stack: error.stack,
		});
	}

	private async saveErrorToDatabase(importId: string, error: Error): Promise<void> {
		await ImportData.updateOne(
			{
				_id: importId,
			},
			{
				$push: {
					errors: {
						message: error.message,
						stack: error.stack,
					},
				},
			},
		);
	}

	protected async saveError(importId: string, error: Error): Promise<void> {
		this._logger.error(error);
		this.saveErrorToMemory(importId, error);

		if (!this._converterOptions.workInMemory) {
			return this.saveErrorToDatabase(importId, error);
		}
	}

	public async clearImportData(): Promise<void> {
		this._records = [];

		// On regular import operations this data will be deleted by the importer class with one single operation for all dataTypes (aka with no filter)
		if (!this._converterOptions.workInMemory && this._converterOptions.deleteDbData) {
			await ImportData.col.deleteMany({ dataType: this.getDataType() });
		}
	}

	public async clearSuccessfullyImportedData(): Promise<void> {
		this._records = this._records.filter((record) => !record.errors?.length);

		// On regular import operations this data will be deleted by the importer class with one single operation for all dataTypes (aka with no filter)
		if (!this._converterOptions.workInMemory && this._converterOptions.deleteDbData) {
			await ImportData.col.deleteMany({ dataType: this.getDataType(), error: { $exists: false } });
		}
	}

	private getMemoryRecordById(id: string): R | undefined {
		for (const record of this._records) {
			if (record._id === id) {
				return record;
			}
		}

		return undefined;
	}

	protected getDataType(): R['dataType'] {
		throw new Error('Unspecified type');
	}

	protected async addObjectToDatabase(data: R['data'], options: R['options'] = {}): Promise<void> {
		await ImportData.col.insertOne({
			_id: new ObjectId().toHexString(),
			data,
			dataType: this.getDataType(),
			options,
		});
	}

	public addObjectToMemory(data: R['data'], options: R['options'] = {}): void {
		this._records.push({
			_id: Random.id(),
			data,
			dataType: this.getDataType(),
			options,
		} as R);
	}

	public async addObject(data: R['data'], options: R['options'] = {}): Promise<void> {
		if (this._converterOptions.workInMemory) {
			return this.addObjectToMemory(data, options);
		}

		return this.addObjectToDatabase(data, options);
	}

	protected getDatabaseDataToImport(): Promise<R[]> {
		return (ImportData.find({ dataType: this.getDataType() }) as FindCursor<R>).toArray();
	}

	protected async getDataToImport(): Promise<R[]> {
		if (this._converterOptions.workInMemory) {
			return this._records;
		}

		const dbRecords = await this.getDatabaseDataToImport();
		if (this._records.length) {
			return [...this._records, ...dbRecords];
		}

		return dbRecords;
	}

	protected async iterateRecords({
		beforeImportFn,
		afterImportFn,
		onErrorFn,
		processRecord,
	}: IConversionCallbacks & { processRecord?: (record: R) => Promise<boolean | undefined> } = {}): Promise<void> {
		const records = await this.getDataToImport();

		this.skippedCount = 0;
		this.failedCount = 0;

		for await (const record of records) {
			const { _id } = record;
			if (this.aborted) {
				return;
			}

			try {
				if (beforeImportFn && !(await beforeImportFn(record))) {
					await this.skipRecord(_id);
					continue;
				}

				const isNew = await (processRecord || this.convertRecord).call(this, record);

				if (typeof isNew === 'boolean' && afterImportFn) {
					await afterImportFn(record, isNew);
				}
			} catch (e) {
				this.failedCount++;
				await this.saveError(_id, e instanceof Error ? e : new Error(String(e)));
				if (onErrorFn) {
					await onErrorFn();
				}
			}
		}
	}

	async convertData(callbacks: IConversionCallbacks = {}): Promise<void> {
		return this.iterateRecords(callbacks);
	}

	protected async convertRecord(_record: R): Promise<boolean | undefined> {
		return undefined;
	}
}

import type { IImportUser } from '@rocket.chat/core-typings';
import { Rooms, Users } from '@rocket.chat/models';

export type UserIdentification = {
	_id: string;
	username: string | undefined;
};

export type MentionedChannel = {
	_id: string;
	name: string;
};

export class ConverterCache {
	private _userCache = new Map<string, UserIdentification>();

	// display name uses a different cache because it's only used on mentions so we don't need to load it every time we load an user
	private _userDisplayNameCache = new Map<string, string>();

	private _roomCache = new Map<string, string>();

	private _roomNameCache = new Map<string, string>();

	addUser(importId: string, _id: string, username: string | undefined): UserIdentification {
		const cache = {
			_id,
			username,
		};

		this._userCache.set(importId, cache);
		return cache;
	}

	addUserDisplayName(importId: string, name: string): string {
		this._userDisplayNameCache.set(importId, name);
		return name;
	}

	addRoom(importId: string, rid: string): string {
		this._roomCache.set(importId, rid);
		return rid;
	}

	addRoomName(importId: string, name: string): string {
		this._roomNameCache.set(importId, name);
		return name;
	}

	addUserData(userData: IImportUser): void {
		if (!userData._id) {
			return;
		}
		if (!userData.importIds.length) {
			return;
		}

		this.addUser(userData.importIds[0], userData._id, userData.username);
	}

	async findImportedRoomId(importId: string): Promise<string | null> {
		if (this._roomCache.has(importId)) {
			return this._roomCache.get(importId) as string;
		}

		const options = {
			projection: {
				_id: 1,
			},
		};

		const room = await Rooms.findOneByImportId(importId, options);
		if (room) {
			return this.addRoom(importId, room._id);
		}

		return null;
	}

	async findImportedRoomName(importId: string): Promise<string | undefined> {
		if (this._roomNameCache.has(importId)) {
			return this._roomNameCache.get(importId) as string;
		}

		const options = {
			projection: {
				_id: 1,
				name: 1,
			},
		};

		const room = await Rooms.findOneByImportId(importId, options);
		if (room) {
			if (!this._roomCache.has(importId)) {
				this.addRoom(importId, room._id);
			}
			if (room?.name) {
				return this.addRoomName(importId, room.name);
			}
		}
	}

	async findImportedUser(importId: string): Promise<UserIdentification | null> {
		if (importId === 'rocket.cat') {
			return {
				_id: 'rocket.cat',
				username: 'rocket.cat',
			};
		}

		const options = {
			projection: {
				_id: 1,
				username: 1,
			},
		};

		if (this._userCache.has(importId)) {
			return this._userCache.get(importId) as UserIdentification;
		}

		const user = await Users.findOneByImportId(importId, options);
		if (user) {
			return this.addUser(importId, user._id, user.username);
		}

		return null;
	}

	async findImportedUserId(_id: string): Promise<string | undefined> {
		const data = await this.findImportedUser(_id);
		return data?._id;
	}

	async findImportedUsername(_id: string): Promise<string | undefined> {
		const data = await this.findImportedUser(_id);
		return data?.username;
	}

	async findImportedUserDisplayName(importId: string): Promise<string | undefined> {
		const options = {
			projection: {
				_id: 1,
				name: 1,
				username: 1,
			},
		};

		if (this._userDisplayNameCache.has(importId)) {
			return this._userDisplayNameCache.get(importId);
		}

		const user =
			importId === 'rocket.cat' ? await Users.findOneById('rocket.cat', options) : await Users.findOneByImportId(importId, options);
		if (user) {
			if (!this._userCache.has(importId)) {
				this.addUser(importId, user._id, user.username);
			}

			if (!user.name) {
				return;
			}

			return this.addUserDisplayName(importId, user.name);
		}
	}

	async convertImportedIdsToUsernames(importedIds: Array<string>, idToRemove: string | undefined = undefined): Promise<Array<string>> {
		return (
			await Promise.all(
				importedIds.map(async (user) => {
					if (user === 'rocket.cat') {
						return user;
					}

					if (this._userCache.has(user)) {
						const cache = this._userCache.get(user);
						if (cache) {
							return cache.username;
						}
					}

					const obj = await Users.findOneByImportId(user, { projection: { _id: 1, username: 1 } });
					if (obj) {
						this.addUser(user, obj._id, obj.username);

						if (idToRemove && obj._id === idToRemove) {
							return false;
						}

						return obj.username;
					}

					return false;
				}),
			)
		).filter((user) => user) as string[];
	}
}

import type { IImportChannel, IImportChannelRecord, IRoom } from '@rocket.chat/core-typings';
import { Subscriptions, Rooms, Users } from '@rocket.chat/models';
import limax from 'limax';

import { createDirectMessage } from '../../../../../server/methods/createDirectMessage';
import { saveRoomSettings } from '../../../../channel-settings/server/methods/saveRoomSettings';
import { notifyOnSubscriptionChangedByRoomId } from '../../../../lib/server/lib/notifyListener';
import { createChannelMethod } from '../../../../lib/server/methods/createChannel';
import { createPrivateGroupMethod } from '../../../../lib/server/methods/createPrivateGroup';
import type { IConversionCallbacks } from '../../definitions/IConversionCallbacks';
import { RecordConverter } from './RecordConverter';

export class RoomConverter extends RecordConverter<IImportChannelRecord> {
	public startedByUserId: string;

	async convertChannels(startedByUserId: string, callbacks: IConversionCallbacks = {}): Promise<void> {
		this.startedByUserId = startedByUserId;

		return this.convertData(callbacks);
	}

	protected async convertRecord(record: IImportChannelRecord): Promise<boolean> {
		const { data } = record;

		if (!data.name && data.t !== 'd') {
			throw new Error('importer-channel-missing-name');
		}

		data.importIds = data.importIds.filter((item) => item);
		data.users = [...new Set(data.users)];

		if (!data.importIds.length) {
			throw new Error('importer-channel-missing-import-id');
		}

		const existingRoom = await this.findExistingRoom(data);
		await this.insertOrUpdateRoom(existingRoom, data, this.startedByUserId);

		return !existingRoom;
	}

	async insertOrUpdateRoom(existingRoom: IRoom | null, data: IImportChannel, startedByUserId: string): Promise<void> {
		if (existingRoom) {
			await this.updateRoom(existingRoom, data, startedByUserId);
		} else {
			await this.insertRoom(data, startedByUserId);
		}

		if (data.archived && data._id) {
			await this.archiveRoomById(data._id);
		}
	}

	async findExistingRoom(data: IImportChannel): Promise<IRoom | null> {
		if (data._id && data._id.toUpperCase() === 'GENERAL') {
			const room = await Rooms.findOneById('GENERAL', {});
			// Prevent the importer from trying to create a new general
			if (!room) {
				throw new Error('importer-channel-general-not-found');
			}

			return room;
		}

		if (data.t === 'd') {
			const users = await this._cache.convertImportedIdsToUsernames(data.users);
			if (users.length !== data.users.length) {
				throw new Error('importer-channel-missing-users');
			}

			return Rooms.findDirectRoomContainingAllUsernames(users, {});
		}

		if (!data.name) {
			return null;
		}

		// Imported room names always allow special chars
		const roomName = limax(data.name.trim(), { maintainCase: true });
		return Rooms.findOneByNonValidatedName(roomName, {});
	}

	async updateRoom(room: IRoom, roomData: IImportChannel, startedByUserId: string): Promise<void> {
		roomData._id = room._id;

		if ((roomData._id as string).toUpperCase() === 'GENERAL' && roomData.name !== room.name) {
			await saveRoomSettings(startedByUserId, 'GENERAL', 'roomName', roomData.name);
		}

		await this.updateRoomId(room._id, roomData);
	}

	async insertRoom(roomData: IImportChannel, startedByUserId: string): Promise<void> {
		// Find the rocketchatId of the user who created this channel
		const creatorId = await this.getRoomCreatorId(roomData, startedByUserId);
		const members = await this._cache.convertImportedIdsToUsernames(roomData.users, roomData.t !== 'd' ? creatorId : undefined);

		if (roomData.t === 'd') {
			if (members.length < roomData.users.length) {
				this._logger.warn(`One or more imported users not found: ${roomData.users}`);
				throw new Error('importer-channel-missing-users');
			}
		}

		// Create the channel
		try {
			let roomInfo;
			if (roomData.t === 'd') {
				roomInfo = await createDirectMessage(members, startedByUserId, true);
			} else {
				if (!roomData.name) {
					return;
				}
				if (roomData.t === 'p') {
					const user = await Users.findOneById(creatorId);
					if (!user) {
						throw new Error('importer-channel-invalid-creator');
					}
					roomInfo = await createPrivateGroupMethod(user, roomData.name, members, false, {}, {});
				} else {
					roomInfo = await createChannelMethod(creatorId, roomData.name, members, false, {}, {});
				}
			}

			roomData._id = roomInfo.rid;
		} catch (e) {
			this._logger.warn({ msg: 'Failed to create new room', name: roomData.name, members });
			this._logger.error(e);
			throw e;
		}

		await this.updateRoomId(roomData._id as 'string', roomData);
	}

	async archiveRoomById(rid: string) {
		const responses = await Promise.all([Rooms.archiveById(rid), Subscriptions.archiveByRoomId(rid)]);

		if (responses[1]?.modifiedCount) {
			void notifyOnSubscriptionChangedByRoomId(rid);
		}
	}

	async updateRoomId(_id: string, roomData: IImportChannel): Promise<void> {
		const set = {
			ts: roomData.ts,
			topic: roomData.topic,
			description: roomData.description,
		};

		const roomUpdate: { $set?: Record<string, any>; $addToSet?: Record<string, any> } = {};

		if (Object.keys(set).length > 0) {
			roomUpdate.$set = set;
		}

		if (roomData.importIds.length) {
			roomUpdate.$addToSet = {
				importIds: {
					$each: roomData.importIds,
				},
			};
		}

		if (roomUpdate.$set || roomUpdate.$addToSet) {
			await Rooms.updateOne({ _id: roomData._id }, roomUpdate);
		}
	}

	async getRoomCreatorId(roomData: IImportChannel, startedByUserId: string): Promise<string> {
		if (roomData.u) {
			const creatorId = await this._cache.findImportedUserId(roomData.u._id);
			if (creatorId) {
				return creatorId;
			}

			if (roomData.t !== 'd') {
				return startedByUserId;
			}

			throw new Error('importer-channel-invalid-creator');
		}

		if (roomData.t === 'd') {
			for await (const member of roomData.users) {
				const userId = await this._cache.findImportedUserId(member);
				if (userId) {
					return userId;
				}
			}
		}

		throw new Error('importer-channel-invalid-creator');
	}

	protected getDataType(): 'channel' {
		return 'channel';
	}
}

import type { IImportUser, IImportUserRecord, IUser, IUserEmail } from '@rocket.chat/core-typings';
import { Users } from '@rocket.chat/models';
import { Random } from '@rocket.chat/random';
import { SHA256 } from '@rocket.chat/sha256';
import { hash as bcryptHash } from 'bcrypt';
import { Accounts } from 'meteor/accounts-base';

import { callbacks as systemCallbacks } from '../../../../../lib/callbacks';
import { addUserToDefaultChannels } from '../../../../lib/server/functions/addUserToDefaultChannels';
import { generateUsernameSuggestion } from '../../../../lib/server/functions/getUsernameSuggestion';
import { saveUserIdentity } from '../../../../lib/server/functions/saveUserIdentity';
import { setUserActiveStatus } from '../../../../lib/server/functions/setUserActiveStatus';
import { notifyOnUserChange } from '../../../../lib/server/lib/notifyListener';
import type { IConversionCallbacks } from '../../definitions/IConversionCallbacks';
import { RecordConverter, type RecordConverterOptions } from './RecordConverter';

export type UserConverterOptions = {
	flagEmailsAsVerified?: boolean;
	skipExistingUsers?: boolean;
	skipNewUsers?: boolean;
	skipUserCallbacks?: boolean;
	skipDefaultChannels?: boolean;

	quickUserInsertion?: boolean;
	enableEmail2fa?: boolean;
};

export type ConvertUsersResult = {
	inserted: string[];
	updated: string[];
	skipped: number;
	failed: number;
};

export class UserConverter extends RecordConverter<IImportUserRecord, UserConverterOptions & RecordConverterOptions> {
	private insertedIds = new Set<IUser['_id']>();

	private updatedIds = new Set<IUser['_id']>();

	protected async convertRecord(record: IImportUserRecord): Promise<boolean | undefined> {
		const { data, _id } = record;

		data.importIds = data.importIds.filter((item) => item);

		if (!data.emails.length && !data.username) {
			throw new Error('importer-user-missing-email-and-username');
		}

		const existingUser = await this.findExistingUser(data);
		if (existingUser && this._options.skipExistingUsers) {
			await this.skipRecord(_id);
			return;
		}
		if (!existingUser && this._options.skipNewUsers) {
			await this.skipRecord(_id);
			return;
		}

		await this.insertOrUpdateUser(existingUser, data);
		return !existingUser;
	}

	async convertData(userCallbacks: IConversionCallbacks = {}): Promise<void> {
		this.insertedIds.clear();
		this.updatedIds.clear();

		if (this._options.quickUserInsertion) {
			await this.batchConversion(userCallbacks);
		} else {
			await super.convertData(userCallbacks);
		}

		await systemCallbacks.run('afterUserImport', {
			inserted: [...this.insertedIds],
			updated: [...this.updatedIds],
			skipped: this.skippedCount,
			failed: this.failedCount,
		});
	}

	public async batchConversion({ afterBatchFn, ...callbacks }: IConversionCallbacks = {}): Promise<void> {
		const batchToInsert = new Set<IImportUser>();

		await this.iterateRecords({
			...callbacks,
			processRecord: async (record: IImportUserRecord) => {
				const { data } = record;

				data.importIds = data.importIds.filter((item) => item);

				if (!data.emails.length && !data.username) {
					throw new Error('importer-user-missing-email-and-username');
				}

				batchToInsert.add(data);

				if (batchToInsert.size >= 50) {
					const usersToInsert = await this.buildUserBatch([...batchToInsert]);
					batchToInsert.clear();

					const newIds = await this.insertUserBatch(usersToInsert, { afterBatchFn });
					newIds.forEach((id) => this.insertedIds.add(id));
				}

				return undefined;
			},
		});

		if (batchToInsert.size > 0) {
			const usersToInsert = await this.buildUserBatch([...batchToInsert]);
			const newIds = await this.insertUserBatch(usersToInsert, { afterBatchFn });
			newIds.forEach((id) => this.insertedIds.add(id));
		}
	}

	private async insertUserBatch(users: IUser[], { afterBatchFn }: IConversionCallbacks): Promise<string[]> {
		let newIds: string[] | null = null;

		try {
			newIds = Object.values((await Users.insertMany(users, { ordered: false })).insertedIds);
			if (afterBatchFn) {
				await afterBatchFn(newIds.length, 0);
			}
		} catch (e: any) {
			newIds = (e.result?.result?.insertedIds || []) as string[];
			const errorCount = users.length - (e.result?.result?.nInserted || 0);

			if (afterBatchFn) {
				await afterBatchFn(Math.min(newIds.length, users.length - errorCount), errorCount);
			}
		}

		return newIds;
	}

	async findExistingUser(data: IImportUser): Promise<IUser | undefined> {
		if (data.emails.length) {
			const emailUser = await Users.findOneByEmailAddress(data.emails[0], {});

			if (emailUser) {
				return emailUser;
			}
		}

		// If we couldn't find one by their email address, try to find an existing user by their username
		if (data.username) {
			return Users.findOneByUsernameIgnoringCase(data.username, {});
		}
	}

	addUserImportId(updateData: Record<string, any>, userData: IImportUser): void {
		if (userData.importIds?.length) {
			updateData.$addToSet = {
				importIds: {
					$each: userData.importIds,
				},
			};
		}
	}

	addUserEmails(updateData: Record<string, any>, userData: IImportUser, existingEmails: Array<IUserEmail>): void {
		if (!userData.emails?.length) {
			return;
		}

		const verifyEmails = Boolean(this._options.flagEmailsAsVerified);
		const newEmailList: Array<IUserEmail> = [];

		for (const email of userData.emails) {
			const verified = verifyEmails || existingEmails.find((ee) => ee.address === email)?.verified || false;

			newEmailList.push({
				address: email,
				verified,
			});
		}

		updateData.$set.emails = newEmailList;
	}

	addUserServices(updateData: Record<string, any>, userData: IImportUser): void {
		if (!userData.services) {
			return;
		}

		for (const serviceKey in userData.services) {
			if (!userData.services[serviceKey]) {
				continue;
			}

			const service = userData.services[serviceKey];

			for (const key in service) {
				if (!service[key]) {
					continue;
				}

				updateData.$set[`services.${serviceKey}.${key}`] = service[key];
			}
		}
	}

	addCustomFields(updateData: Record<string, any>, userData: IImportUser): void {
		if (!userData.customFields) {
			return;
		}

		const subset = (source: Record<string, any>, currentPath: string): void => {
			for (const key in source) {
				if (!source.hasOwnProperty(key)) {
					continue;
				}

				const keyPath = `${currentPath}.${key}`;
				if (typeof source[key] === 'object' && !Array.isArray(source[key])) {
					subset(source[key], keyPath);
					continue;
				}

				updateData.$set = {
					...updateData.$set,
					...{ [keyPath]: source[key] },
				};
			}
		};

		subset(userData.customFields, 'customFields');
	}

	async insertOrUpdateUser(existingUser: IUser | undefined, data: IImportUser): Promise<void> {
		if (!data.username && !existingUser?.username) {
			const emails = data.emails.filter(Boolean).map((email) => ({ address: email }));
			data.username = await generateUsernameSuggestion({
				name: data.name,
				emails,
			});
		}

		if (existingUser) {
			await this.updateUser(existingUser, data);
			this.updatedIds.add(existingUser._id);
		} else {
			if (!data.name && data.username) {
				data.name = this.guessNameFromUsername(data.username);
			}

			const userId = await this.insertUser(data);
			data._id = userId;
			this.insertedIds.add(userId);

			if (!this._options.skipDefaultChannels) {
				const insertedUser = await Users.findOneById(userId, {});
				if (!insertedUser) {
					throw new Error(`User not found: ${userId}`);
				}

				await addUserToDefaultChannels(insertedUser, true);
			}
		}
	}

	async updateUser(existingUser: IUser, userData: IImportUser): Promise<void> {
		const { _id } = existingUser;
		if (!_id) {
			return;
		}

		userData._id = _id;

		if (!userData.roles && !existingUser.roles) {
			userData.roles = ['user'];
		}
		if (!userData.type && !existingUser.type) {
			userData.type = 'user';
		}

		const updateData: Record<string, any> = Object.assign(Object.create(null), {
			$set: Object.assign(Object.create(null), {
				...(userData.roles && { roles: userData.roles }),
				...(userData.type && { type: userData.type }),
				...(userData.statusText && { statusText: userData.statusText }),
				...(userData.bio && { bio: userData.bio }),
				...(userData.services?.ldap && { ldap: true }),
				...(userData.avatarUrl && { _pendingAvatarUrl: userData.avatarUrl }),
			}),
		});

		this.addCustomFields(updateData, userData);
		this.addUserServices(updateData, userData);
		this.addUserImportId(updateData, userData);
		this.addUserEmails(updateData, userData, existingUser.emails || []);

		if (Object.keys(updateData.$set).length === 0) {
			delete updateData.$set;
		}
		if (Object.keys(updateData).length > 0) {
			await Users.updateOne({ _id }, updateData);
		}

		if (userData.utcOffset) {
			await Users.setUtcOffset(_id, userData.utcOffset);
		}

		if (userData.name || userData.username) {
			await saveUserIdentity({ _id, name: userData.name, username: userData.username } as Parameters<typeof saveUserIdentity>[0]);
		}

		if (userData.importIds.length) {
			this._cache.addUser(userData.importIds[0], existingUser._id, existingUser.username || userData.username);
		}

		// Deleted users are 'inactive' users in Rocket.Chat
		if (userData.deleted && existingUser?.active) {
			await setUserActiveStatus(_id, false, true);
		} else if (userData.deleted === false && existingUser?.active === false) {
			await setUserActiveStatus(_id, true);
		}

		void notifyOnUserChange({ clientAction: 'updated', id: _id, diff: updateData.$set });
	}

	private async hashPassword(password: string): Promise<string> {
		return bcryptHash(SHA256(password), Accounts._bcryptRounds());
	}

	private generateTempPassword(userData: IImportUser): string {
		return `${Date.now()}${userData.name || ''}${userData.emails.length ? userData.emails[0].toUpperCase() : ''}`;
	}

	private async buildNewUserObject(userData: IImportUser): Promise<Partial<IUser>> {
		return {
			type: userData.type || 'user',
			...(userData.username && { username: userData.username }),
			...(userData.emails.length && {
				emails: userData.emails.map((email) => ({ address: email, verified: !!this._options.flagEmailsAsVerified })),
			}),
			...(userData.statusText && { statusText: userData.statusText }),
			...(userData.name && { name: userData.name }),
			...(userData.bio && { bio: userData.bio }),
			...(userData.avatarUrl && { _pendingAvatarUrl: userData.avatarUrl }),
			...(userData.utcOffset !== undefined && { utcOffset: userData.utcOffset }),
			...{
				services: {
					// Add a password service if there's a password string, or if there's no service at all
					...((!!userData.password || !userData.services || !Object.keys(userData.services).length) && {
						password: { bcrypt: await this.hashPassword(userData.password || this.generateTempPassword(userData)) },
					}),
					...(userData.services || {}),
				},
			},
			...(userData.services?.ldap && { ldap: true }),
			...(userData.importIds?.length && { importIds: userData.importIds }),
			...(!!userData.customFields && { customFields: userData.customFields }),
			...(userData.deleted !== undefined && { active: !userData.deleted }),
			...(userData.voipExtension !== undefined && { freeSwitchExtension: userData.voipExtension }),
		};
	}

	private async buildUserBatch(usersData: IImportUser[]): Promise<IUser[]> {
		return Promise.all(
			usersData.map(async (userData) => {
				const user = await this.buildNewUserObject(userData);
				return {
					createdAt: new Date(),
					_id: Random.id(),

					status: 'offline',
					...user,
					roles: userData.roles?.length ? userData.roles : ['user'],
					active: !userData.deleted,
					services: {
						...user.services,
						...(this._options.enableEmail2fa
							? {
									email2fa: {
										enabled: true,
										changedAt: new Date(),
									},
								}
							: {}),
					},
				} as IUser;
			}),
		);
	}

	async insertUser(userData: IImportUser): Promise<IUser['_id']> {
		const user = await this.buildNewUserObject(userData);

		return Accounts.insertUserDoc(
			{
				joinDefaultChannels: false,
				skipEmailValidation: true,
				skipAdminCheck: true,
				skipAdminEmail: true,
				skipOnCreateUserCallback: this._options.skipUserCallbacks,
				skipBeforeCreateUserCallback: this._options.skipUserCallbacks,
				skipAfterCreateUserCallback: this._options.skipUserCallbacks,
				skipDefaultAvatar: true,
				skipAppsEngineEvent: !!process.env.IMPORTER_SKIP_APPS_EVENT,
			},
			{
				...user,
				...(userData.roles?.length ? { globalRoles: userData.roles } : {}),
			},
		);
	}

	protected guessNameFromUsername(username: string): string {
		return username
			.replace(/\W/g, ' ')
			.replace(/\s(.)/g, (u) => u.toUpperCase())
			.replace(/^(.)/, (u) => u.toLowerCase())
			.replace(/^\w/, (u) => u.toUpperCase());
	}

	protected getDataType(): 'user' {
		return 'user';
	}
}

import { AppStatus } from '@rocket.chat/apps-engine/definition/AppStatus';
import type { LicenseImp } from '@rocket.chat/license';

import { i18n } from '../../../../server/lib/i18n';
import { sendMessagesToAdmins } from '../../../../server/lib/sendMessagesToAdmins';
import { Apps } from '../../apps';

type OnModuleCallbackParameter = Parameters<Parameters<LicenseImp['onModule']>[0]>[0];

export async function _disableAppsWithAddonsCallback(
	deps: { Apps: typeof Apps; sendMessagesToAdmins: typeof sendMessagesToAdmins },
	{ module, external, valid }: OnModuleCallbackParameter,
) {
	if (!external || valid) return;

	const enabledApps = await deps.Apps.installedApps({ enabled: true });

	if (!enabledApps) return;

	const affectedApps: string[] = [];

	await Promise.all(
		enabledApps.map(async (app) => {
			if (app.getInfo().addon !== module) return;

			affectedApps.push(app.getName());

			return deps.Apps.getManager()?.disable(app.getID(), AppStatus.DISABLED, false);
		}),
	);

	if (!affectedApps.length) return;

	await deps.sendMessagesToAdmins({
		msgs: async ({ adminUser }) => ({
			msg: i18n.t('App_has_been_disabled_addon_message', {
				lng: adminUser.language || 'en',
				count: affectedApps.length,
				appNames: affectedApps,
			}),
		}),
	});
}

export const disableAppsWithAddonsCallback = (ctx: OnModuleCallbackParameter) =>
	_disableAppsWithAddonsCallback({ Apps, sendMessagesToAdmins }, ctx);

import { api } from '@rocket.chat/core-services';
import type { IUser } from '@rocket.chat/core-typings';
import { Invites, Users } from '@rocket.chat/models';
import { Accounts } from 'meteor/accounts-base';
import { Meteor } from 'meteor/meteor';
import _ from 'underscore';

import { callbacks } from '../../../../lib/callbacks';
import { SystemLogger } from '../../../../server/lib/logger/system';
import { hasPermissionAsync } from '../../../authorization/server/functions/hasPermission';
import { settings } from '../../../settings/server';
import { RateLimiter } from '../lib';
import { notifyOnUserChange } from '../lib/notifyListener';
import { addUserToRoom } from './addUserToRoom';
import { checkUsernameAvailability } from './checkUsernameAvailability';
import { getAvatarSuggestionForUser } from './getAvatarSuggestionForUser';
import { joinDefaultChannels } from './joinDefaultChannels';
import { saveUserIdentity } from './saveUserIdentity';
import { setUserAvatar } from './setUserAvatar';
import { validateUsername } from './validateUsername';

export const setUsernameWithValidation = async (userId: string, username: string, joinDefaultChannelsSilenced?: boolean): Promise<void> => {
	if (!username) {
		throw new Meteor.Error('error-invalid-username', 'Invalid username', { method: 'setUsername' });
	}

	const user = await Users.findOneById(userId);

	if (!user) {
		throw new Meteor.Error('error-invalid-user', 'Invalid user', { method: 'setUsername' });
	}

	if (user.username && !settings.get('Accounts_AllowUsernameChange')) {
		throw new Meteor.Error('error-not-allowed', 'Not allowed');
	}

	if (user.username === username || (user.username && user.username.toLowerCase() === username.toLowerCase())) {
		return;
	}

	if (!validateUsername(username)) {
		throw new Meteor.Error(
			'username-invalid',
			`${_.escape(username)} is not a valid username, use only letters, numbers, dots, hyphens and underscores`,
		);
	}

	if (!(await checkUsernameAvailability(username))) {
		throw new Meteor.Error('error-field-unavailable', `<strong>${_.escape(username)}</strong> is already in use :(`, {
			method: 'setUsername',
			field: username,
		});
	}

	if (!(await saveUserIdentity({ _id: user._id, username }))) {
		throw new Meteor.Error('error-could-not-change-username', 'Could not change username', {
			method: 'setUsername',
		});
	}

	if (!user.username) {
		await joinDefaultChannels(user._id, joinDefaultChannelsSilenced);
		setImmediate(async () => callbacks.run('afterCreateUser', user));
	}

	void notifyOnUserChange({ clientAction: 'updated', id: user._id, diff: { username } });
};

export const _setUsername = async function (userId: string, u: string, fullUser: IUser): Promise<unknown> {
	const username = u.trim();

	if (!userId || !username) {
		return false;
	}

	if (!validateUsername(username)) {
		return false;
	}

	const user = fullUser || (await Users.findOneById(userId));
	// User already has desired username, return
	if (user.username === username) {
		return user;
	}
	const previousUsername = user.username;
	// Check username availability or if the user already owns a different casing of the name
	if (!previousUsername || !(username.toLowerCase() === previousUsername.toLowerCase())) {
		if (!(await checkUsernameAvailability(username))) {
			return false;
		}
	}
	// If first time setting username, send Enrollment Email
	try {
		if (!previousUsername && user.emails && user.emails.length > 0 && settings.get('Accounts_Enrollment_Email')) {
			setImmediate(() => {
				Accounts.sendEnrollmentEmail(user._id);
			});
		}
	} catch (e: any) {
		SystemLogger.error(e);
	}
	// Set new username*
	await Users.setUsername(user._id, username);
	user.username = username;

	if (!previousUsername && settings.get('Accounts_SetDefaultAvatar') === true) {
		const avatarSuggestions = await getAvatarSuggestionForUser(user);
		let avatarData;
		let serviceName = 'gravatar';

		for (const service of Object.keys(avatarSuggestions)) {
			avatarData = avatarSuggestions[service];
			if (service !== 'gravatar') {
				serviceName = service;
				break;
			}
		}

		if (avatarData) {
			await setUserAvatar(user, avatarData.blob, avatarData.contentType, serviceName);
		}
	}

	// If it's the first username and the user has an invite Token, then join the invite room
	if (!previousUsername && user.inviteToken) {
		const inviteData = await Invites.findOneById(user.inviteToken);
		if (inviteData?.rid) {
			await addUserToRoom(inviteData.rid, user);
		}
	}

	void api.broadcast('user.nameChanged', {
		_id: user._id,
		name: user.name,
		username: user.username,
	});

	return user;
};

export const setUsername = RateLimiter.limitFunction(_setUsername, 1, 60000, {
	async 0() {
		const userId = Meteor.userId();
		return !userId || !(await hasPermissionAsync(userId, 'edit-other-user-info'));
	},
});

