import type { MessageTypesValues } from '@rocket.chat/core-typings';

export const isMutedUnmuted = (messageType: string): boolean => {
	return messageType === 'user-muted' || messageType === 'user-unmuted';
};

export const shouldHideSystemMessage = (messageType: MessageTypesValues, hideSystemMessage?: MessageTypesValues[]): boolean => {
	if (!hideSystemMessage?.length) {
		return false;
	}

	return hideSystemMessage.includes(messageType) || (isMutedUnmuted(messageType) && hideSystemMessage.includes('mute_unmute'));
};

import { settings } from '../../../settings/server';

export const validateUsername = (username: string): boolean => {
	const settingsRegExp = settings.get('UTF8_User_Names_Validation');
	const defaultPattern = /^[0-9a-zA-Z-_.]+$/;

	let usernameRegExp: RegExp;
	try {
		usernameRegExp = settingsRegExp ? new RegExp(`^${settingsRegExp}$`) : defaultPattern;
	} catch (e) {
		usernameRegExp = defaultPattern;
	}

	return usernameRegExp.test(username);
};

import type { MessageTypesValues, IRoom } from '@rocket.chat/core-typings';

export const getHiddenSystemMessages = (room: IRoom, hiddenSystemMessages: MessageTypesValues[]): MessageTypesValues[] => {
	const hiddenTypes = hiddenSystemMessages.reduce((array, value): MessageTypesValues[] => {
		const newValue: MessageTypesValues[] = value === 'mute_unmute' ? ['user-muted', 'user-unmuted'] : [value];
		return [...array, ...newValue];
	}, [] as MessageTypesValues[]);

	return Array.isArray(room?.sysMes) ? room.sysMes : hiddenTypes;
};

import type { IAppStorageItem } from '@rocket.chat/apps-engine/server/storage';
import { Apps } from '@rocket.chat/core-services';
import type { LicenseModule } from '@rocket.chat/core-typings';
import { License, type LicenseImp } from '@rocket.chat/license';

import { getInstallationSourceFromAppStorageItem } from '../../../../lib/apps/getInstallationSourceFromAppStorageItem';

type _canEnableAppDependencies = {
	Apps: typeof Apps;
	License: LicenseImp;
};

export const _canEnableApp = async ({ Apps, License }: _canEnableAppDependencies, app: IAppStorageItem): Promise<void> => {
	if (!(await Apps.isInitialized())) {
		throw new Error('apps-engine-not-initialized');
	}

	// Migrated apps were installed before the validation was implemented
	// so they're always allowed to be enabled
	if (app.migrated) {
		return;
	}

	if (app.info.addon && !License.hasModule(app.info.addon as LicenseModule)) {
		throw new Error('app-addon-not-valid');
	}

	const source = getInstallationSourceFromAppStorageItem(app);
	switch (source) {
		case 'private':
			if (await License.shouldPreventAction('privateApps')) {
				throw new Error('license-prevented');
			}

			break;
		default:
			if (await License.shouldPreventAction('marketplaceApps')) {
				throw new Error('license-prevented');
			}

			if (app.marketplaceInfo?.isEnterpriseOnly && !License.hasValidLicense()) {
				throw new Error('invalid-license');
			}

			break;
	}
};

export const canEnableApp = async (app: IAppStorageItem): Promise<void> => _canEnableApp({ Apps, License }, app);

import type { IAppStorageItem } from '@rocket.chat/apps-engine/server/storage';
import type { LicenseAppSources } from '@rocket.chat/core-typings';

/**
 * There have been reports of apps not being correctly migrated from versions prior to 6.0
 *
 * This function is a workaround to get the installation source of an app from the app storage item
 * even if the installationSource property is not set.
 */
export function getInstallationSourceFromAppStorageItem(item: IAppStorageItem): LicenseAppSources {
	return item.installationSource || ('marketplaceInfo' in item ? 'marketplace' : 'private');
}

