export { filterBusinessHoursThatMustBeOpened };

export const filterBusinessHoursThatMustBeOpenedByDay = async (
	businessHours: ILivechatBusinessHour[],
	day: string, // Format: moment.format('dddd')
): Promise<Pick<ILivechatBusinessHour, '_id' | 'type'>[]> => {
	return filterBusinessHoursThatMustBeOpened(
		businessHours.filter((businessHour) =>
			businessHour.workHours.some((workHour) => workHour.start.utc.dayOfWeek === day || workHour.finish.utc.dayOfWeek === day),
		),
	);
};

export const openBusinessHourDefault = async (): Promise<void> => {
	await Users.removeBusinessHoursFromAllUsers();
	const currentTime = moment(moment().format('dddd:HH:mm'), 'dddd:HH:mm');
	const day = currentTime.format('dddd');
	const activeBusinessHours = await LivechatBusinessHours.findDefaultActiveAndOpenBusinessHoursByDay(day, {
		projection: {
			workHours: 1,
			timezone: 1,
			type: 1,
			active: 1,
		},
	});

	const businessHoursToOpenIds = (await filterBusinessHoursThatMustBeOpened(activeBusinessHours)).map((businessHour) => businessHour._id);
	businessHourLogger.debug({ msg: 'Opening default business hours', businessHoursToOpenIds });
	await Users.openAgentsBusinessHoursByBusinessHourId(businessHoursToOpenIds);
	if (businessHoursToOpenIds.length) {
		await makeOnlineAgentsAvailable();
	}
	await makeAgentsUnavailableBasedOnBusinessHour();
};

export const createDefaultBusinessHourIfNotExists = async (): Promise<void> => {
	if ((await LivechatBusinessHours.col.countDocuments({ type: LivechatBusinessHourTypes.DEFAULT })) === 0) {
		await LivechatBusinessHours.insertOne(createDefaultBusinessHourRow());
	}
};

export async function makeAgentsUnavailableBasedOnBusinessHour(agentIds: string[] | null = null) {
	const results = await Users.findAgentsAvailableWithoutBusinessHours(agentIds).toArray();

	const update = await Users.updateLivechatStatusByAgentIds(
		results.map(({ _id }) => _id),
		ILivechatAgentStatus.NOT_AVAILABLE,
	);

	if (update.modifiedCount === 0) {
		return;
	}

	void notifyOnUserChangeAsync(async () =>
		results.map(({ _id, openBusinessHours }) => {
			return {
				id: _id,
				clientAction: 'updated',
				diff: {
					statusLivechat: 'not-available',
					openBusinessHours,
				},
			};
		}),
	);
}

export async function makeOnlineAgentsAvailable(agentIds: string[] | null = null) {
	const results = await Users.findOnlineButNotAvailableAgents(agentIds).toArray();

	const update = await Users.updateLivechatStatusByAgentIds(
		results.map(({ _id }) => _id),
		ILivechatAgentStatus.AVAILABLE,
	);

	if (update.modifiedCount === 0) {
		return;
	}

	void notifyOnUserChangeAsync(async () =>
		results.map(({ _id, openBusinessHours }) => {
			return {
				id: _id,
				clientAction: 'updated',
				diff: {
					statusLivechat: 'available',
					openBusinessHours,
				},
			};
		}),
	);
}

import type { ILivechatBusinessHour } from '@rocket.chat/core-typings';
import { LivechatBusinessHourTypes } from '@rocket.chat/core-typings';
import moment from 'moment-timezone';
import { ObjectId } from 'mongodb';

export const createDefaultBusinessHourRow = (): ILivechatBusinessHour => {
	const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
	const closedDays = ['Saturday', 'Sunday'];
	return {
		_id: new ObjectId().toHexString(),
		name: '',
		active: true,
		type: LivechatBusinessHourTypes.DEFAULT,
		ts: new Date(),
		workHours: days.map((day, index) => ({
			day,
			start: {
				time: '08:00',
				utc: {
					dayOfWeek: moment(`${day}:08:00`, 'dddd:HH:mm').utc().format('dddd'),
					time: moment(`${day}:08:00`, 'dddd:HH:mm').utc().format('HH:mm'),
				},
				cron: {
					dayOfWeek: moment(`${day}:08:00`, 'dddd:HH:mm').format('dddd'),
					time: moment(`${day}:08:00`, 'dddd:HH:mm').format('HH:mm'),
				},
			},
			finish: {
				time: '20:00',
				utc: {
					dayOfWeek: moment(`${day}:20:00`, 'dddd:HH:mm').utc().format('dddd'),
					time: moment(`${day}:20:00`, 'dddd:HH:mm').utc().format('HH:mm'),
				},
				cron: {
					dayOfWeek: moment(`${day}:20:00`, 'dddd:HH:mm').format('dddd'),
					time: moment(`${day}:20:00`, 'dddd:HH:mm').format('HH:mm'),
				},
			},
			code: index + 1,
			open: !closedDays.includes(day),
		})),
		timezone: {
			name: moment.tz.guess(),
			utc: String(moment().utcOffset() / 60),
		},
	};
};

import type { ILivechatBusinessHour } from '@rocket.chat/core-typings';
import moment from 'moment';

export const filterBusinessHoursThatMustBeOpened = async (
	businessHours: ILivechatBusinessHour[],
): Promise<Pick<ILivechatBusinessHour, '_id' | 'type'>[]> => {
	const currentTime = moment(moment().format('dddd:HH:mm:ss'), 'dddd:HH:mm:ss');

	return businessHours
		.filter(
			(businessHour) =>
				businessHour.active &&
				businessHour.workHours
					.filter((hour) => hour.open)
					.some((hour) => {
						const localTimeStart = moment(`${hour.start.cron.dayOfWeek}:${hour.start.cron.time}:00`, 'dddd:HH:mm:ss');
						const localTimeFinish = moment(`${hour.finish.cron.dayOfWeek}:${hour.finish.cron.time}:00`, 'dddd:HH:mm:ss');

						// The way we create the instances sunday will be the first day of the current week not the next one, that way it will never met isBefore
						if (localTimeFinish.isBefore(localTimeStart)) {
							localTimeFinish.add(1, 'week');
						}

						return currentTime.isSameOrAfter(localTimeStart) && currentTime.isBefore(localTimeFinish);
					}),
		)
		.map((businessHour) => ({
			_id: businessHour._id,
			type: businessHour.type,
		}));
};

import type { IUser } from '@rocket.chat/core-typings';
import { LivechatDepartment, LivechatDepartmentAgents, Users } from '@rocket.chat/models';

const getAllAgentIdsWithoutDepartment = async (): Promise<string[]> => {
	// Fetch departments with agents excluding archived ones (disabled ones still can be tied to business hours)
	// Then find the agents that are not in any of those departments

	const departmentIds = (await LivechatDepartment.findNotArchived({ projection: { _id: 1 } }).toArray()).map(({ _id }) => _id);

	const agentIdsWithDepartment = await LivechatDepartmentAgents.findAllAgentsConnectedToListOfDepartments(departmentIds);

	const agentIdsWithoutDepartment = (
		await Users.findUsersInRolesWithQuery(
			'livechat-agent',
			{
				_id: { $nin: agentIdsWithDepartment },
			},
			{ projection: { _id: 1 } },
		).toArray()
	).map((user) => user._id);

	return agentIdsWithoutDepartment;
};

const getAllAgentIdsWithDepartmentNotConnectedToBusinessHour = async (): Promise<string[]> => {
	const activeDepartmentsWithoutBusinessHour = (
		await LivechatDepartment.findActiveDepartmentsWithoutBusinessHour({
			projection: { _id: 1 },
		}).toArray()
	).map((dept) => dept._id);

	const agentIdsWithDepartmentNotConnectedToBusinessHour = await LivechatDepartmentAgents.findAllAgentsConnectedToListOfDepartments(
		activeDepartmentsWithoutBusinessHour,
	);
	return agentIdsWithDepartmentNotConnectedToBusinessHour;
};

export const getAgentIdsForBusinessHour = async (): Promise<IUser['_id'][]> => {
	const [withoutDepartment, withDepartmentNotConnectedToBusinessHour] = await Promise.all([
		getAllAgentIdsWithoutDepartment(),
		getAllAgentIdsWithDepartmentNotConnectedToBusinessHour(),
	]);

	return [...new Set([...withoutDepartment, ...withDepartmentNotConnectedToBusinessHour])];
};

import type { IOmnichannelRoom, IMessage, IBusinessHourWorkHour, ILivechatDepartment } from '@rocket.chat/core-typings';
import { isOmnichannelRoom } from '@rocket.chat/core-typings';
import { LivechatBusinessHours, LivechatDepartment, Messages, LivechatRooms } from '@rocket.chat/models';
import moment from 'moment';

import { callbacks } from '../../../../lib/callbacks';
import { settings } from '../../../settings/server';
import { businessHourManager } from '../business-hour';
import type { CloseRoomParams } from '../lib/localTypes';

export const getSecondsWhenOfficeHoursIsDisabled = (room: IOmnichannelRoom, agentLastMessage: IMessage) =>
	moment(new Date(room.closedAt || new Date())).diff(moment(new Date(agentLastMessage.ts)), 'seconds');

export const parseDays = (
	acc: Record<string, { start: { day: string; time: string }; finish: { day: string; time: string }; open: boolean }>,
	day: IBusinessHourWorkHour,
) => {
	acc[day.day] = {
		start: { day: day.start.utc.dayOfWeek, time: day.start.utc.time },
		finish: { day: day.finish.utc.dayOfWeek, time: day.finish.utc.time },
		open: day.open,
	};
	return acc;
};

export const getSecondsSinceLastAgentResponse = async (room: IOmnichannelRoom, agentLastMessage: IMessage) => {
	if (!settings.get('Livechat_enable_business_hours')) {
		return getSecondsWhenOfficeHoursIsDisabled(room, agentLastMessage);
	}
	let officeDays;
	const department = room.departmentId
		? await LivechatDepartment.findOneById<Pick<ILivechatDepartment, 'businessHourId'>>(room.departmentId, {
				projection: { businessHourId: 1 },
			})
		: null;
	if (department?.businessHourId) {
		const businessHour = await LivechatBusinessHours.findOneById(department.businessHourId);
		if (!businessHour) {
			return getSecondsWhenOfficeHoursIsDisabled(room, agentLastMessage);
		}

		officeDays = (await businessHourManager.getBusinessHour(businessHour._id, businessHour.type))?.workHours.reduce(parseDays, {});
	} else {
		officeDays = (await businessHourManager.getBusinessHour())?.workHours.reduce(parseDays, {});
	}

	// Empty object we assume invalid config
	if (!officeDays || !Object.keys(officeDays).length) {
		return getSecondsWhenOfficeHoursIsDisabled(room, agentLastMessage);
	}

	let totalSeconds = 0;
	const endOfConversation = moment.utc(new Date(room.closedAt || new Date()));
	const startOfInactivity = moment.utc(new Date(agentLastMessage.ts));
	const daysOfInactivity = endOfConversation.clone().startOf('day').diff(startOfInactivity.clone().startOf('day'), 'days');
	const inactivityDay = moment.utc(new Date(agentLastMessage.ts));

	for (let index = 0; index <= daysOfInactivity; index++) {
		const today = inactivityDay.clone().format('dddd');
		const officeDay = officeDays[today];

		if (!officeDay) {
			inactivityDay.add(1, 'days');
			continue;
		}

		if (!officeDay.open) {
			inactivityDay.add(1, 'days');
			continue;
		}

		if (!officeDay?.start?.time || !officeDay?.finish?.time) {
			inactivityDay.add(1, 'days');
			continue;
		}

		const [officeStartHour, officeStartMinute] = officeDay.start.time.split(':');
		const [officeCloseHour, officeCloseMinute] = officeDay.finish.time.split(':');
		// We should only take in consideration the time where the office is open and the conversation was inactive
		const todayStartOfficeHours = inactivityDay
			.clone()
			.set({ hour: parseInt(officeStartHour, 10), minute: parseInt(officeStartMinute, 10) });
		const todayEndOfficeHours = inactivityDay.clone().set({ hour: parseInt(officeCloseHour, 10), minute: parseInt(officeCloseMinute, 10) });

		// 1: Room was inactive the whole day, we add the whole time BH is inactive
		if (startOfInactivity.isBefore(todayStartOfficeHours) && endOfConversation.isAfter(todayEndOfficeHours)) {
			totalSeconds += todayEndOfficeHours.diff(todayStartOfficeHours, 'seconds');
		}

		// 2: Room was inactive before start but was closed before end of BH, we add the inactive time
		if (startOfInactivity.isBefore(todayStartOfficeHours) && endOfConversation.isBefore(todayEndOfficeHours)) {
			totalSeconds += endOfConversation.diff(todayStartOfficeHours, 'seconds');
		}

		// 3: Room was inactive after start and ended after end of BH, we add the inactive time
		if (startOfInactivity.isAfter(todayStartOfficeHours) && endOfConversation.isAfter(todayEndOfficeHours)) {
			totalSeconds += todayEndOfficeHours.diff(startOfInactivity, 'seconds');
		}

		// 4: Room was inactive after start and before end of BH, we add the inactive time
		if (startOfInactivity.isAfter(todayStartOfficeHours) && endOfConversation.isBefore(todayEndOfficeHours)) {
			totalSeconds += endOfConversation.diff(startOfInactivity, 'seconds');
		}

		inactivityDay.add(1, 'days');
	}
	return totalSeconds;
};

export const onCloseRoom = async (params: { room: IOmnichannelRoom; options: CloseRoomParams['options'] }) => {
	const { room } = params;

	if (!isOmnichannelRoom(room)) {
		return params;
	}

	const closedByAgent = room.closer !== 'visitor';
	const wasTheLastMessageSentByAgent = room.lastMessage && !room.lastMessage.token;
	if (!closedByAgent || !wasTheLastMessageSentByAgent) {
		return params;
	}

	if (!room.v?.lastMessageTs) {
		return params;
	}

	const agentLastMessage = await Messages.findAgentLastMessageByVisitorLastMessageTs(room._id, room.v.lastMessageTs);
	if (!agentLastMessage) {
		return params;
	}
	const secondsSinceLastAgentResponse = await getSecondsSinceLastAgentResponse(room, agentLastMessage);
	await LivechatRooms.setVisitorInactivityInSecondsById(room._id, secondsSinceLastAgentResponse);

	return params;
};

callbacks.add('livechat.closeRoom', onCloseRoom, callbacks.priority.HIGH, 'process-room-abandonment');

import type {
	AtLeast,
	ILivechatContact,
	ILivechatContactChannel,
	ILivechatCustomField,
	ILivechatVisitor,
	IOmnichannelRoom,
	IUser,
} from '@rocket.chat/core-typings';
import type { InsertionModel } from '@rocket.chat/model-typings';
import {
	LivechatVisitors,
	Users,
	LivechatRooms,
	LivechatCustomField,
	LivechatInquiry,
	Rooms,
	Subscriptions,
	LivechatContacts,
} from '@rocket.chat/models';
import type { PaginatedResult, VisitorSearchChatsResult } from '@rocket.chat/rest-typings';
import { check } from 'meteor/check';
import { Meteor } from 'meteor/meteor';
import type { MatchKeysAndValues, OnlyFieldsOfType, FindOptions, Sort } from 'mongodb';

import { callbacks } from '../../../../lib/callbacks';
