import {
	notifyOnRoomChangedById,
	notifyOnSubscriptionChangedByRoomId,
	notifyOnLivechatInquiryChangedByRoom,
} from '../../../lib/server/lib/notifyListener';
import { i18n } from '../../../utils/lib/i18n';

type RegisterContactProps = {
	_id?: string;
	token: string;
	name: string;
	username?: string;
	email?: string;
	phone?: string;
	customFields?: Record<string, unknown | string>;
	contactManager?: {
		username: string;
	};
};

type CreateContactParams = {
	name: string;
	emails?: string[];
	phones?: string[];
	unknown: boolean;
	customFields?: Record<string, string | unknown>;
	contactManager?: string;
	channels?: ILivechatContactChannel[];
};

type UpdateContactParams = {
	contactId: string;
	name?: string;
	emails?: string[];
	phones?: string[];
	customFields?: Record<string, unknown>;
	contactManager?: string;
	channels?: ILivechatContactChannel[];
};

type GetContactsParams = {
	searchText?: string;
	count: number;
	offset: number;
	sort: Sort;
};

type GetContactHistoryParams = {
	contactId: string;
	source?: string;
	count: number;
	offset: number;
	sort: Sort;
};

export const Contacts = {
	async registerContact({
		token,
		name,
		email = '',
		phone,
		username,
		customFields = {},
		contactManager,
	}: RegisterContactProps): Promise<string> {
		check(token, String);

		const visitorEmail = email.trim().toLowerCase();

		if (contactManager?.username) {
			// verify if the user exists with this username and has a livechat-agent role
			const user = await Users.findOneByUsername(contactManager.username, { projection: { roles: 1 } });
			if (!user) {
				throw new Meteor.Error('error-contact-manager-not-found', `No user found with username ${contactManager.username}`);
			}
			if (!user.roles || !Array.isArray(user.roles) || !user.roles.includes('livechat-agent')) {
				throw new Meteor.Error('error-invalid-contact-manager', 'The contact manager must have the role "livechat-agent"');
			}
		}

		let contactId;

		const user = await LivechatVisitors.getVisitorByToken(token, { projection: { _id: 1 } });

		if (user) {
			contactId = user._id;
		} else {
			if (!username) {
				username = await LivechatVisitors.getNextVisitorUsername();
			}

			let existingUser = null;

			if (visitorEmail !== '' && (existingUser = await LivechatVisitors.findOneGuestByEmailAddress(visitorEmail))) {
				contactId = existingUser._id;
			} else {
				const userData = {
					username,
					ts: new Date(),
					token,
				};

				contactId = (await LivechatVisitors.insertOne(userData)).insertedId;
			}
		}

		const allowedCF = await getAllowedCustomFields();
		const livechatData: Record<string, string> = validateCustomFields(allowedCF, customFields, { ignoreAdditionalFields: true });

		const fieldsToRemove = {
			// if field is explicitely set to empty string, remove
			...(phone === '' && { phone: 1 }),
			...(visitorEmail === '' && { visitorEmails: 1 }),
			...(!contactManager?.username && { contactManager: 1 }),
		};

		const updateUser: { $set: MatchKeysAndValues<ILivechatVisitor>; $unset?: OnlyFieldsOfType<ILivechatVisitor> } = {
			$set: {
				token,
				name,
				livechatData,
				// if phone has some value, set
				...(phone && { phone: [{ phoneNumber: phone }] }),
				...(visitorEmail && { visitorEmails: [{ address: visitorEmail }] }),
				...(contactManager?.username && { contactManager: { username: contactManager.username } }),
			},
			...(Object.keys(fieldsToRemove).length && { $unset: fieldsToRemove }),
		};

		await LivechatVisitors.updateOne({ _id: contactId }, updateUser);

		const extraQuery = await callbacks.run('livechat.applyRoomRestrictions', {});
		const rooms: IOmnichannelRoom[] = await LivechatRooms.findByVisitorId(contactId, {}, extraQuery).toArray();

		if (rooms?.length) {
			for await (const room of rooms) {
				const { _id: rid } = room;

				const responses = await Promise.all([
					Rooms.setFnameById(rid, name),
					LivechatInquiry.setNameByRoomId(rid, name),
					Subscriptions.updateDisplayNameByRoomId(rid, name),
				]);

				if (responses[0]?.modifiedCount) {
					void notifyOnRoomChangedById(rid);
				}

				if (responses[1]?.modifiedCount) {
					void notifyOnLivechatInquiryChangedByRoom(rid, 'updated', { name });
				}

				if (responses[2]?.modifiedCount) {
					void notifyOnSubscriptionChangedByRoomId(rid);
				}
			}
		}

		return contactId;
	},
};

export function isSingleContactEnabled(): boolean {
	// The Single Contact feature is not yet available in production, but can already be partially used in test environments.
	return process.env.TEST_MODE?.toUpperCase() === 'TRUE';
}

export async function createContactFromVisitor(visitor: ILivechatVisitor): Promise<string> {
	if (visitor.contactId) {
		throw new Error('error-contact-already-exists');
	}

	const contactData: InsertionModel<ILivechatContact> = {
		name: visitor.name || visitor.username,
		emails: visitor.visitorEmails,
		phones: visitor.phone || undefined,
		unknown: true,
		channels: [],
		customFields: visitor.livechatData,
		createdAt: new Date(),
	};

	if (visitor.contactManager) {
		const contactManagerId = await Users.findOneByUsername<Pick<IUser, '_id'>>(visitor.contactManager.username, { projection: { _id: 1 } });
		if (contactManagerId) {
			contactData.contactManager = contactManagerId._id;
		}
	}

	const { insertedId: contactId } = await LivechatContacts.insertOne(contactData);

	await LivechatVisitors.updateOne({ _id: visitor._id }, { $set: { contactId } });

	return contactId;
}

export async function createContact(params: CreateContactParams): Promise<string> {
	const { name, emails, phones, customFields: receivedCustomFields = {}, contactManager, channels, unknown } = params;

	if (contactManager) {
		await validateContactManager(contactManager);
	}

	const allowedCustomFields = await getAllowedCustomFields();
	const customFields = validateCustomFields(allowedCustomFields, receivedCustomFields);

	const { insertedId } = await LivechatContacts.insertOne({
		name,
		emails: emails?.map((address) => ({ address })),
		phones: phones?.map((phoneNumber) => ({ phoneNumber })),
		contactManager,
		channels,
		customFields,
		unknown,
		createdAt: new Date(),
	});

	return insertedId;
}

export async function updateContact(params: UpdateContactParams): Promise<ILivechatContact> {
	const { contactId, name, emails, phones, customFields: receivedCustomFields, contactManager, channels } = params;

	const contact = await LivechatContacts.findOneById<Pick<ILivechatContact, '_id'>>(contactId, { projection: { _id: 1 } });

	if (!contact) {
		throw new Error('error-contact-not-found');
	}

	if (contactManager) {
		await validateContactManager(contactManager);
	}

	const customFields = receivedCustomFields && validateCustomFields(await getAllowedCustomFields(), receivedCustomFields);

	const updatedContact = await LivechatContacts.updateContact(contactId, {
		name,
		emails: emails?.map((address) => ({ address })),
		phones: phones?.map((phoneNumber) => ({ phoneNumber })),
		contactManager,
		channels,
		customFields,
	});

	return updatedContact;
}

export async function getContacts(params: GetContactsParams): Promise<PaginatedResult<{ contacts: ILivechatContact[] }>> {
	const { searchText, count, offset, sort } = params;

	const { cursor, totalCount } = LivechatContacts.findPaginatedContacts(searchText, {
		limit: count,
		skip: offset,
		sort: sort ?? { name: 1 },
	});

	const [contacts, total] = await Promise.all([cursor.toArray(), totalCount]);

	return {
		contacts,
		count,
		offset,
		total,
	};
}

export async function getContactHistory(
	params: GetContactHistoryParams,
): Promise<PaginatedResult<{ history: VisitorSearchChatsResult[] }>> {
	const { contactId, source, count, offset, sort } = params;

	const contact = await LivechatContacts.findOneById<Pick<ILivechatContact, 'channels'>>(contactId, { projection: { channels: 1 } });

	if (!contact) {
		throw new Error('error-contact-not-found');
	}

	const visitorsIds = new Set(contact.channels?.map((channel: ILivechatContactChannel) => channel.visitorId));

	if (!visitorsIds?.size) {
		return { history: [], count: 0, offset, total: 0 };
	}

	const options: FindOptions<IOmnichannelRoom> = {
		sort: sort || { ts: -1 },
		skip: offset,
		limit: count,
		projection: {
			fname: 1,
			ts: 1,
			v: 1,
			msgs: 1,
			servedBy: 1,
			closedAt: 1,
			closedBy: 1,
			closer: 1,
			tags: 1,
			source: 1,
		},
	};

	const { totalCount, cursor } = LivechatRooms.findPaginatedRoomsByVisitorsIdsAndSource({
		visitorsIds: Array.from(visitorsIds),
		source,
		options,
	});

	const [total, history] = await Promise.all([totalCount, cursor.toArray()]);

	return {
		history,
		count: history.length,
		offset,
		total,
	};
}

async function getAllowedCustomFields(): Promise<Pick<ILivechatCustomField, '_id' | 'label' | 'regexp' | 'required'>[]> {
	return LivechatCustomField.findByScope(
		'visitor',
		{
			projection: { _id: 1, label: 1, regexp: 1, required: 1 },
		},
		false,
	).toArray();
}

export function validateCustomFields(
	allowedCustomFields: AtLeast<ILivechatCustomField, '_id' | 'label' | 'regexp' | 'required'>[],
	customFields: Record<string, string | unknown>,
	options?: { ignoreAdditionalFields?: boolean },
): Record<string, string> {
	const validValues: Record<string, string> = {};

	for (const cf of allowedCustomFields) {
		if (!customFields.hasOwnProperty(cf._id)) {
			if (cf.required) {
				throw new Error(i18n.t('error-invalid-custom-field-value', { field: cf.label }));
			}
			continue;
		}
		const cfValue: string = trim(customFields[cf._id]);

		if (!cfValue || typeof cfValue !== 'string') {
			if (cf.required) {
				throw new Error(i18n.t('error-invalid-custom-field-value', { field: cf.label }));
			}
			continue;
		}

		if (cf.regexp) {
			const regex = new RegExp(cf.regexp);
			if (!regex.test(cfValue)) {
				throw new Error(i18n.t('error-invalid-custom-field-value', { field: cf.label }));
			}
		}

		validValues[cf._id] = cfValue;
	}

	if (!options?.ignoreAdditionalFields) {
		const allowedCustomFieldIds = new Set(allowedCustomFields.map((cf) => cf._id));
		for (const key in customFields) {
			if (!allowedCustomFieldIds.has(key)) {
				throw new Error(i18n.t('error-custom-field-not-allowed', { key }));
			}
		}
	}

	return validValues;
}

export async function validateContactManager(contactManagerUserId: string) {
	const contactManagerUser = await Users.findOneAgentById<Pick<IUser, '_id'>>(contactManagerUserId, { projection: { _id: 1 } });
	if (!contactManagerUser) {
		throw new Error('error-contact-manager-not-found');
	}
}

import { escapeRegExp } from '@rocket.chat/string-helpers';
import { sanitize } from 'dompurify';

export function truncate(str: string, length: number): string {
	return str.length > length ? `${str.slice(0, length - 3)}...` : str;
}

function makeString(object: unknown): string {
	if (!object) return '';
	return String(object);
}

function defaultToWhiteSpace(characters: unknown): string {
	if (!characters) return '\\s';
	if (typeof characters === 'object' && 'source' in characters) return (characters as { source: string }).source;
	return `[${escapeRegExp(makeString(characters))}]`;
}

const nativeTrim = String.prototype.trim;
const nativeTrimLeft = String.prototype.trimLeft;
const nativeTrimRight = String.prototype.trimRight;

export function trim(_str: unknown, _characters?: unknown): string {
	const str = makeString(_str);
	if (!_characters && nativeTrim) return nativeTrim.call(str);
	const characters = defaultToWhiteSpace(_characters);
	return str.replace(new RegExp(`^${characters}+|${characters}+$`, 'g'), '');
}

export function ltrim(_str: unknown, _characters: unknown): string {
	const str = makeString(_str);
	if (!_characters && nativeTrimLeft) return nativeTrimLeft.call(str);
	const characters = defaultToWhiteSpace(_characters);
	return str.replace(new RegExp(`^${characters}+`), '');
}

export function rtrim(_str: unknown, _characters: unknown): string {
	const str = makeString(_str);
	if (!_characters && nativeTrimRight) return nativeTrimRight.call(str);
	const characters = defaultToWhiteSpace(_characters);
	return str.replace(new RegExp(`${characters}+$`), '');
}

export function capitalize(_str: unknown, lowercaseRest: boolean): string {
	const str = makeString(_str);
	const remainingChars = !lowercaseRest ? str.slice(1) : str.slice(1).toLowerCase();

	return str.charAt(0).toUpperCase() + remainingChars;
}

export function stripTags(str: unknown): string {
	return sanitize(makeString(str)).replace(/<\/?[^>]+>/g, '');
}

export function strLeft(_str: unknown, _sep: unknown): string {
	const str = makeString(_str);
	const sep = makeString(_sep);
	const pos = !sep ? -1 : str.indexOf(sep);
	return ~pos ? str.slice(0, pos) : str;
}

export function strRight(_str: unknown, _sep: unknown): string {
	const str = makeString(_str);
	const sep = makeString(_sep);
	const pos = !sep ? -1 : str.indexOf(sep);
	return ~pos ? str.slice(pos + sep.length, str.length) : str;
}

export function strRightBack(_str: unknown, _sep: unknown): string {
	const str = makeString(_str);
	const sep = makeString(_sep);
	const pos = !sep ? -1 : str.lastIndexOf(sep);
	return ~pos ? str.slice(pos + sep.length, str.length) : str;
}

export function numberFormat(_number: number, dec: number, dsep?: string, tsep?: string): string {
	if (isNaN(_number) || _number === null || _number === undefined) return '';

	const number = _number.toFixed(~~dec);
	tsep = typeof tsep === 'string' ? tsep : ',';

	const parts = number.split('.');
	const fnums = parts[0];
	const decimals = parts[1] ? (dsep || '.') + parts[1] : '';

	return fnums.replace(/(\d)(?=(?:\d{3})+$)/g, `$1${tsep}`) + decimals;
}

export function pad(_str: unknown, _length: number, padStr?: string, type: 'right' | 'left' | 'both' = 'right') {
	const str = makeString(_str);
	const length = ~~_length;

	let padlen = 0;

	if (!padStr) padStr = ' ';
	else if (padStr.length > 1) padStr = padStr.charAt(0);

	switch (type) {
		case 'right':
			padlen = length - str.length;
			return str + padStr.repeat(padlen);
		case 'both':
			padlen = length - str.length;
			return padStr.repeat(Math.ceil(padlen / 2)) + str + padStr.repeat(Math.floor(padlen / 2));
		default: // 'left'
			padlen = length - str.length;
			return padStr.repeat(padlen) + str;
	}
}

export function lrpad(str: unknown, length: number, padStr?: string): string {
	return pad(str, length, padStr, 'both');
}

import type { RocketchatI18nKeys } from '@rocket.chat/i18n';
import type { TOptions } from 'i18next';
import i18next from 'i18next';
import sprintf from 'i18next-sprintf-postprocessor';

import { isObject } from '../../../lib/utils/isObject';

declare module 'i18next' {
	// eslint-disable-next-line @typescript-eslint/naming-convention
	interface TFunction {
		(key: RocketchatI18nKeys): string;
		(key: RocketchatI18nKeys, options: TOptions): string;
	}
}

export const i18n = i18next.use(sprintf);

export const addSprinfToI18n = function (t: (typeof i18n)['t']) {
	return function (key: string, ...replaces: any): string {
		if (replaces[0] === undefined) {
			return t(key);
		}

		if (isObject(replaces[0]) && !Array.isArray(replaces[0])) {
			return t(key, replaces[0]);
		}

		return t(key, {
			postProcess: 'sprintf',
			sprintf: replaces,
		});
	};
};

export const t = addSprinfToI18n(i18n.t.bind(i18n));

/**
 * Extract the translation keys from a flat object and group them by namespace
 *
 * Example:
 *
 * ```js
 * const source = {
 *   'core.key1': 'value1',
 *   'core.key2': 'value2',
 *   'onboarding.key1': 'value1',
 *   'onboarding.key2': 'value2',
 *   'registration.key1': 'value1',
 *   'registration.key2': 'value2',
 *   'cloud.key1': 'value1',
 *   'cloud.key2': 'value2',
 *   'subscription.key1': 'value1',
 *   'subscription.key2': 'value2',
 * };
 *
 * const result = extractTranslationNamespaces(source);
 *
 * console.log(result);
 *
 * // {
 * //   core: {
 * //     key1: 'value1',
 * //     key2: 'value2'
 * //   },
 * //   onboarding: {
 * //     key1: 'value1',
 * //     key2: 'value2'
 * //   },
 * //   registration: {
 * //     key1: 'value1',
 * //     key2: 'value2'
 * //   },
 * //   cloud: {
 * //     key1: 'value1',
 * //     key2: 'value2'
 * //   },
 * //   subscription: {
 * //     key1: 'value1',
 * //     key2: 'value2'
 * //   }
 * // }
 * ```
 *
 * @param source the flat object with the translation keys
 */
export const extractTranslationNamespaces = (source: Record<string, string>): Record<TranslationNamespace, Record<string, string>> => {
	const result: Record<TranslationNamespace, Record<string, string>> = {
		core: {},
		onboarding: {},
		registration: {},
		cloud: {},
		subscription: {},
	};

	for (const [key, value] of Object.entries(source)) {
		const prefix = availableTranslationNamespaces.find((namespace) => key.startsWith(`${namespace}.`));
		const keyWithoutNamespace = prefix ? key.slice(prefix.length + 1) : key;
		const ns = prefix ?? defaultTranslationNamespace;
		result[ns][keyWithoutNamespace] = value;
	}

	return result;
};

/**
 * Extract only the translation keys that match the given namespaces
 *
 * @param source the flat object with the translation keys
 * @param namespaces the namespaces to extract
 */
export const extractTranslationKeys = (source: Record<string, string>, namespaces: string | string[] = []): { [key: string]: any } => {
	const all = extractTranslationNamespaces(source);
	return Array.isArray(namespaces)
		? (namespaces as TranslationNamespace[]).reduce((result, namespace) => ({ ...result, ...all[namespace] }), {})
		: all[namespaces as TranslationNamespace];
};

export type TranslationNamespace =
	| (Extract<RocketchatI18nKeys, `${string}.${string}`> extends `${infer T}.${string}` ? (T extends Lowercase<T> ? T : never) : never)
	| 'core';

const namespacesMap: Record<TranslationNamespace, true> = {
	core: true,
	onboarding: true,
	registration: true,
	cloud: true,
	subscription: true,
};

export const availableTranslationNamespaces = Object.keys(namespacesMap) as TranslationNamespace[];
export const defaultTranslationNamespace: TranslationNamespace = 'core';

export const applyCustomTranslations = (
	i18n: typeof i18next,
	parsedCustomTranslations: Record<string, Record<string, string>>,
	{ namespaces, languages }: { namespaces?: string[]; languages?: string[] } = {},
) => {
	for (const [lng, translations] of Object.entries(parsedCustomTranslations)) {
		if (languages && !languages.includes(lng)) {
			continue;
		}

		for (const [key, value] of Object.entries(translations)) {
			const prefix = availableTranslationNamespaces.find((namespace) => key.startsWith(`${namespace}.`));
			const keyWithoutNamespace = prefix ? key.slice(prefix.length + 1) : key;
			const ns = prefix ?? defaultTranslationNamespace;

			if (namespaces && !namespaces.includes(ns)) {
				continue;
			}

			i18n.addResourceBundle(lng, ns, { [keyWithoutNamespace]: value }, true, true);
		}
	}
};

export function isObject(obj: unknown): obj is object {
	const type = typeof obj;
	return type === 'function' || (type === 'object' && !!obj);
}

import type { ILivechatVisitor, IOmnichannelRoom, IUser } from '@rocket.chat/core-typings';
import { LivechatVisitors, Users } from '@rocket.chat/models';

import { settings } from '../../../settings/server';
import type { CloseRoomParams } from './localTypes';

export const parseTranscriptRequest = async (
	room: IOmnichannelRoom,
	options: CloseRoomParams['options'],
	visitor?: ILivechatVisitor,
	user?: IUser,
): Promise<CloseRoomParams['options']> => {
	const visitorDecideTranscript = settings.get<boolean>('Livechat_enable_transcript');
	// visitor decides, no changes
	if (visitorDecideTranscript) {
		return options;
	}

	// send always is disabled, no changes
	const sendAlways = settings.get<boolean>('Livechat_transcript_send_always');
	if (!sendAlways) {
		return options;
	}

	const visitorData =
		visitor ||
		(await LivechatVisitors.findOneById<Pick<ILivechatVisitor, 'visitorEmails'>>(room.v._id, { projection: { visitorEmails: 1 } }));
	// no visitor, no changes
	if (!visitorData) {
		return options;
	}
	const visitorEmail = visitorData?.visitorEmails?.[0]?.address;
	// visitor doesnt have email, no changes
	if (!visitorEmail) {
		return options;
	}

	const defOptions = { projection: { _id: 1, username: 1, name: 1 } };
	const requestedBy =
		user ||
		(room.servedBy && (await Users.findOneById(room.servedBy._id, defOptions))) ||
		(await Users.findOneById('rocket.cat', defOptions));

	// no user available for backing request, no changes
	if (!requestedBy) {
		return options;
	}

	return {
		...options,
		emailTranscript: {
			sendToVisitor: true,
			requestData: {
				email: visitorEmail,
				requestedAt: new Date(),
				subject: '',
				requestedBy,
			},
		},
	};
};

import { Message } from '@rocket.chat/core-services';
import {
	type IUser,
	type MessageTypesValues,
	type IOmnichannelSystemMessage,
	type ILivechatVisitor,
	isFileAttachment,
	isFileImageAttachment,
} from '@rocket.chat/core-typings';
import colors from '@rocket.chat/fuselage-tokens/colors';
import { Logger } from '@rocket.chat/logger';
import { LivechatRooms, Messages, Uploads, Users } from '@rocket.chat/models';
import { check } from 'meteor/check';
import moment from 'moment-timezone';

import { callbacks } from '../../../../lib/callbacks';
import { i18n } from '../../../../server/lib/i18n';
import { FileUpload } from '../../../file-upload/server';
import * as Mailer from '../../../mailer/server/api';
import { settings } from '../../../settings/server';
import { MessageTypes } from '../../../ui-utils/lib/MessageTypes';
import { getTimezone } from '../../../utils/server/lib/getTimezone';

const logger = new Logger('Livechat-SendTranscript');

export async function sendTranscript({
	token,
	rid,
	email,
	subject,
	user,
}: {
	token: string;
	rid: string;
	email: string;
	subject?: string;
	user?: Pick<IUser, '_id' | 'name' | 'username' | 'utcOffset'> | null;
}): Promise<boolean> {
	check(rid, String);
	check(email, String);
	logger.debug(`Sending conversation transcript of room ${rid} to user with token ${token}`);

	const room = await LivechatRooms.findOneById(rid);

	const visitor = room?.v as ILivechatVisitor;
	if (token !== visitor?.token) {
		throw new Error('error-invalid-visitor');
	}

	const userLanguage = settings.get<string>('Language') || 'en';
	const timezone = getTimezone(user);
	logger.debug(`Transcript will be sent using ${timezone} as timezone`);

	if (!room) {
		throw new Error('error-invalid-room');
	}

	// allow to only user to send transcripts from their own chats
	if (room.t !== 'l') {
		throw new Error('error-invalid-room');
	}

	const showAgentInfo = settings.get<boolean>('Livechat_show_agent_info');
	const showSystemMessages = settings.get<boolean>('Livechat_transcript_show_system_messages');
	const closingMessage = await Messages.findLivechatClosingMessage(rid, { projection: { ts: 1 } });
	const ignoredMessageTypes: MessageTypesValues[] = [
		'livechat_navigation_history',
		'livechat_transcript_history',
		'command',
		'livechat-close',
		'livechat-started',
		'livechat_video_call',
		'omnichannel_priority_change_history',
	];
	const acceptableImageMimeTypes = ['image/jpeg', 'image/png', 'image/jpg'];
	const messages = await Messages.findVisibleByRoomIdNotContainingTypesBeforeTs(
		rid,
		ignoredMessageTypes,
		closingMessage?.ts ? new Date(closingMessage.ts) : new Date(),
		showSystemMessages,
		{
			sort: { ts: 1 },
		},
	);

	let html = '<div> <hr>';
	const InvalidFileMessage = `<div style="background-color: ${colors.n100}; text-align: center; border-color: ${
		colors.n250
	}; border-width: 1px; border-style: solid; border-radius: 4px; padding-top: 8px; padding-bottom: 8px; margin-top: 4px;">${i18n.t(
		'This_attachment_is_not_supported',
		{ lng: userLanguage },
	)}</div>`;

	for await (const message of messages) {
		let author;
		if (message.u._id === visitor._id) {
			author = i18n.t('You', { lng: userLanguage });
		} else {
			author = showAgentInfo ? message.u.name || message.u.username : i18n.t('Agent', { lng: userLanguage });
		}

		const isSystemMessage = MessageTypes.isSystemMessage(message);
		const messageType = isSystemMessage && MessageTypes.getType(message);

		let messageContent = messageType
			? `<i>${i18n.t(
					messageType.message,
					messageType.data
						? { ...messageType.data(message), interpolation: { escapeValue: false } }
						: { interpolation: { escapeValue: false } },
				)}</i>`
			: message.msg;

		let filesHTML = '';

		if (message.attachments && message.attachments?.length > 0) {
			messageContent = message.attachments[0].description || '';

			for await (const attachment of message.attachments) {
				if (!isFileAttachment(attachment)) {
					// ignore other types of attachments
					continue;
				}

				if (!isFileImageAttachment(attachment)) {
					filesHTML += `<div>${attachment.title || ''}${InvalidFileMessage}</div>`;
					continue;
				}

				if (!attachment.image_type || !acceptableImageMimeTypes.includes(attachment.image_type)) {
					filesHTML += `<div>${attachment.title || ''}${InvalidFileMessage}</div>`;
					continue;
				}

				// Image attachment can be rendered in email body
				const file = message.files?.find((file) => file.name === attachment.title);

				if (!file) {
					filesHTML += `<div>${attachment.title || ''}${InvalidFileMessage}</div>`;
					continue;
				}

				const uploadedFile = await Uploads.findOneById(file._id);

				if (!uploadedFile) {
					filesHTML += `<div>${file.name}${InvalidFileMessage}</div>`;
					continue;
				}

				const uploadedFileBuffer = await FileUpload.getBuffer(uploadedFile);
				filesHTML += `<div styles="color: ${colors.n700}; margin-top: 4px; flex-direction: "column";"><p>${file.name}</p><img src="data:${
					attachment.image_type
				};base64,${uploadedFileBuffer.toString(
					'base64',
				)}" style="width: 400px; max-height: 240px; object-fit: contain; object-position: 0;"/></div>`;
			}
		}

		const datetime = moment.tz(message.ts, timezone).locale(userLanguage).format('LLL');
		const singleMessage = `
			<p><strong>${author}</strong>  <em>${datetime}</em></p>
			<p>${messageContent}</p>
			<p>${filesHTML}</p>
		`;
		html += singleMessage;
	}

	html = `${html}</div>`;

	const fromEmail = settings.get<string>('From_Email').match(/\b[A-Z0-9._%+-]+@(?:[A-Z0-9-]+\.)+[A-Z]{2,4}\b/i);
	let emailFromRegexp = '';
	if (fromEmail) {
		emailFromRegexp = fromEmail[0];
	} else {
		emailFromRegexp = settings.get<string>('From_Email');
	}

	// Some endpoints allow the caller to pass a different `subject` via parameter.
	// IF subject is passed, we'll use that one and treat it as an override
	// IF no subject is passed, we fallback to the setting `Livechat_transcript_email_subject`
	// IF that is not configured, we fallback to 'Transcript of your livechat conversation', which is the default value
	// As subject and setting value are user input, we don't translate them
	const mailSubject =
		subject ||
		settings.get<string>('Livechat_transcript_email_subject') ||
		i18n.t('Transcript_of_your_livechat_conversation', { lng: userLanguage });

	await Mailer.send({
		to: email,
		from: emailFromRegexp,
		replyTo: emailFromRegexp,
		subject: mailSubject,
		html,
	});

	setImmediate(() => {
		void callbacks.run('livechat.sendTranscript', messages, email);
	});

	const requestData: IOmnichannelSystemMessage['requestData'] = {
		type: 'user',
		visitor,
		user,
	};

	if (!user?.username) {
		const cat = await Users.findOneById('rocket.cat', { projection: { _id: 1, username: 1, name: 1 } });
		if (cat) {
			requestData.user = cat;
			requestData.type = 'visitor';
		}
	}

	if (!requestData.user) {
		logger.error('rocket.cat user not found');
		throw new Error('No user provided and rocket.cat not found');
	}

	await Message.saveSystemMessage<IOmnichannelSystemMessage>('livechat_transcript_history', room._id, '', requestData.user, {
		requestData,
	});

	return true;
}

import type { IMessage, MessageTypesValues } from '@rocket.chat/core-typings';
import type { TranslationKey } from '@rocket.chat/ui-contexts';

export type MessageType = {
	id: MessageTypesValues;
	system?: boolean;
	/* deprecated */
	template?: (message: IMessage) => unknown;
	message: TranslationKey;
	data?: (message: IMessage) => Record<string, string>;
};

class MessageTypes {
	private types = new Map<MessageTypesValues, MessageType>();

	registerType(options: MessageType): MessageType {
		if ('render' in options) {
			console.warn('MessageType.render is deprecated. Use MessageType.message instead.', options.id);
		}
		if ('template' in options) {
			console.warn('MessageType.template is deprecated. Use MessageType.message instead.', options.id);
		}
		this.types.set(options.id, options);
		return options;
	}

	getType(message: Pick<IMessage, 't'>): MessageType | undefined {
		return message.t && this.types.get(message.t);
	}

	isSystemMessage(message: Pick<IMessage, 't'>): boolean {
		const type = this.getType(message);
		return Boolean(type?.system);
	}
}

const instance = new MessageTypes();

export { instance as MessageTypes };

import { Apps, AppEvents } from '@rocket.chat/apps';
import { api, Message } from '@rocket.chat/core-services';
import type { IMessage, IRoom, IUser } from '@rocket.chat/core-typings';
import type { ServerMethods } from '@rocket.chat/ddp-client';
import { Messages, EmojiCustom, Rooms, Users } from '@rocket.chat/models';
import { Meteor } from 'meteor/meteor';

import { callbacks } from '../../../lib/callbacks';
import { i18n } from '../../../server/lib/i18n';
import { canAccessRoomAsync } from '../../authorization/server';
import { hasPermissionAsync } from '../../authorization/server/functions/hasPermission';
import { emoji } from '../../emoji/server';
import { isTheLastMessage } from '../../lib/server/functions/isTheLastMessage';
import { notifyOnMessageChange } from '../../lib/server/lib/notifyListener';

export const removeUserReaction = (message: IMessage, reaction: string, username: string) => {
	if (!message.reactions) {
		return message;
	}

	const idx = message.reactions[reaction].usernames.indexOf(username);

	// user not found in reaction array
	if (idx === -1) {
		return message;
	}

	message.reactions[reaction].usernames.splice(idx, 1);
	if (!message.reactions[reaction].usernames.length) {
		delete message.reactions[reaction];
	}
	return message;
};

export async function setReaction(
	room: Pick<IRoom, '_id' | 'muted' | 'unmuted' | 'reactWhenReadOnly' | 'ro' | 'lastMessage' | 'federated'>,
	user: IUser,
	message: IMessage,
	reaction: string,
	userAlreadyReacted?: boolean,
) {
	await Message.beforeReacted(message, room);

	if (Array.isArray(room.muted) && room.muted.includes(user.username as string)) {
		throw new Meteor.Error('error-not-allowed', i18n.t('You_have_been_muted', { lng: user.language }), {
			rid: room._id,
		});
	}

	if (room.ro === true && !room.reactWhenReadOnly && !(await hasPermissionAsync(user._id, 'post-readonly', room._id))) {
		// Unless the user was manually unmuted
		if (!(room.unmuted || []).includes(user.username as string)) {
			throw new Error("You can't send messages because the room is readonly.");
		}
	}

	let isReacted;
	if (userAlreadyReacted) {
		const oldMessage = JSON.parse(JSON.stringify(message));
		removeUserReaction(message, reaction, user.username as string);
		if (Object.keys(message.reactions || {}).length === 0) {
			delete message.reactions;
			await Messages.unsetReactions(message._id);
			if (isTheLastMessage(room, message)) {
				await Rooms.unsetReactionsInLastMessage(room._id);
			}
		} else {
			await Messages.setReactions(message._id, message.reactions);
			if (isTheLastMessage(room, message)) {
				await Rooms.setReactionsInLastMessage(room._id, message.reactions);
			}
		}
		void callbacks.run('afterUnsetReaction', message, { user, reaction, shouldReact: false, oldMessage });

		isReacted = false;
	} else {
		if (!message.reactions) {
			message.reactions = {};
		}
		if (!message.reactions[reaction]) {
			message.reactions[reaction] = {
				usernames: [],
			};
		}
		message.reactions[reaction].usernames.push(user.username as string);
		await Messages.setReactions(message._id, message.reactions);
		if (isTheLastMessage(room, message)) {
			await Rooms.setReactionsInLastMessage(room._id, message.reactions);
		}

		void callbacks.run('afterSetReaction', message, { user, reaction, shouldReact: true });

		isReacted = true;
	}

	void Apps.self?.triggerEvent(AppEvents.IPostMessageReacted, message, user, reaction, isReacted);

	void notifyOnMessageChange({
		id: message._id,
	});
}

export async function executeSetReaction(
	userId: string,
	reaction: string,
	messageParam: IMessage['_id'] | IMessage,
	shouldReact?: boolean,
) {
	// Check if the emoji is valid before proceeding
	const reactionWithoutColons = reaction.replace(/:/g, '');
	reaction = `:${reactionWithoutColons}:`;

	if (!emoji.list[reaction] && (await EmojiCustom.countByNameOrAlias(reactionWithoutColons)) === 0) {
		throw new Meteor.Error('error-not-allowed', 'Invalid emoji provided.', {
			method: 'setReaction',
		});
	}

	const user = await Users.findOneById(userId);
	if (!user) {
		throw new Meteor.Error('error-invalid-user', 'Invalid user', { method: 'setReaction' });
	}

	const message = typeof messageParam === 'string' ? await Messages.findOneById(messageParam) : messageParam;
	if (!message) {
		throw new Meteor.Error('error-not-allowed', 'Not allowed', { method: 'setReaction' });
	}

	const userAlreadyReacted =
		message.reactions && Boolean(message.reactions[reaction]) && message.reactions[reaction].usernames.includes(user.username as string);

	// When shouldReact was not informed, toggle the reaction.
	if (shouldReact === undefined) {
		shouldReact = !userAlreadyReacted;
	}

	if (userAlreadyReacted === shouldReact) {
		return;
	}

	const room = await Rooms.findOneById<
		Pick<IRoom, '_id' | 'ro' | 'muted' | 'reactWhenReadOnly' | 'lastMessage' | 't' | 'prid' | 'federated'>
	>(message.rid, { projection: { _id: 1, ro: 1, muted: 1, reactWhenReadOnly: 1, lastMessage: 1, t: 1, prid: 1, federated: 1 } });
	if (!room) {
		throw new Meteor.Error('error-not-allowed', 'Not allowed', { method: 'setReaction' });
	}

	if (!(await canAccessRoomAsync(room, user))) {
		throw new Meteor.Error('not-authorized', 'Not Authorized', { method: 'setReaction' });
	}

	return setReaction(room, user, message, reaction, userAlreadyReacted);
}

declare module '@rocket.chat/ddp-client' {
	// eslint-disable-next-line @typescript-eslint/naming-convention
	interface ServerMethods {
		setReaction(reaction: string, messageId: IMessage['_id'], shouldReact?: boolean): boolean | undefined;
	}
}

Meteor.methods<ServerMethods>({
	async setReaction(reaction, messageId, shouldReact) {
		const uid = Meteor.userId();
		if (!uid) {
			throw new Meteor.Error('error-invalid-user', 'Invalid user', { method: 'setReaction' });
		}

		try {
			await executeSetReaction(uid, reaction, messageId, shouldReact);
		} catch (e: any) {
			if (e.error === 'error-not-allowed' && e.reason && e.details && e.details.rid) {
				void api.broadcast('notify.ephemeralMessage', uid, e.details.rid, {
					msg: e.reason,
				});

				return false;
			}

			throw e;
		}
	},
});

export const fileUploadMediaWhiteList = function (customWhiteList: string): string[] | undefined {
	const mediaTypeWhiteList = customWhiteList;

	if (!mediaTypeWhiteList || mediaTypeWhiteList === '*') {
		return;
	}
	return mediaTypeWhiteList.split(',').map((item) => {
		return item.trim();
	});
};

const fileUploadMediaBlackList = function (customBlackList: string): string[] | undefined {
	const blacklist = customBlackList;
	if (!blacklist) {
		return;
	}

	return blacklist.split(',').map((item) => item.trim());
};

const isTypeOnList = function (type?: string, list?: string[]): boolean {
	if (!type || !list) {
		return false;
	}

	if (list.includes(type)) {
		return true;
	}
	const wildCardGlob = '/*';
	const wildcards = list.filter((item) => {
		return item.indexOf(wildCardGlob) > 0;
	});
	if (wildcards.includes(type.replace(/(\/.*)$/, wildCardGlob))) {
		return true;
	}

	return false;
};

export const fileUploadIsValidContentTypeFromSettings = function (
	type: string | undefined,
	customWhiteList: string,
	customBlackList: string,
): boolean {
	const blackList = fileUploadMediaBlackList(customBlackList);
	const whiteList = fileUploadMediaWhiteList(customWhiteList);

	if (blackList && type && isTypeOnList(type, blackList)) {
		return false;
	}

	if (whiteList) {
		return isTypeOnList(type, whiteList);
	}

	if (!whiteList) {
		return true;
	}

	return false;
};

import { isMessageFromVisitor } from '@rocket.chat/core-typings';
import { Messages, Rooms, Users } from '@rocket.chat/models';
import { Random } from '@rocket.chat/random';

import { cachedFunction } from './cachedFunction';
import { transformMappedData } from './transformMappedData';

export class AppMessagesConverter {
	mem = new WeakMap();

	constructor(orch) {
		this.orch = orch;
	}

	async convertById(msgId) {
		const msg = await Messages.findOneById(msgId);

		return this.convertMessage(msg);
	}

	async convertMessageRaw(msgObj) {
		if (!msgObj) {
			return undefined;
		}

		const { attachments, ...message } = msgObj;
		const getAttachments = async () => this._convertAttachmentsToApp(attachments);

		const map = {
			id: '_id',
			threadId: 'tmid',
			reactions: 'reactions',
			parseUrls: 'parseUrls',
			text: 'msg',
			createdAt: 'ts',
			updatedAt: '_updatedAt',
			editedAt: 'editedAt',
			emoji: 'emoji',
			avatarUrl: 'avatar',
			alias: 'alias',
			file: 'file',
			customFields: 'customFields',
			groupable: 'groupable',
			token: 'token',
			blocks: 'blocks',
			roomId: 'rid',
			editor: 'editedBy',
			attachments: getAttachments,
			sender: 'u',
		};

		return transformMappedData(message, map);
	}

	async convertMessage(msgObj, cacheObj = msgObj) {
		if (!msgObj) {
			return undefined;
		}

		const cache =
			this.mem.get(cacheObj) ??
			new Map([
				['room', cachedFunction(this.orch.getConverters().get('rooms').convertById.bind(this.orch.getConverters().get('rooms')))],
				[
					'user.convertById',
					cachedFunction(this.orch.getConverters().get('users').convertById.bind(this.orch.getConverters().get('users'))),
				],
				[
					'user.convertToApp',
					cachedFunction(this.orch.getConverters().get('users').convertToApp.bind(this.orch.getConverters().get('users'))),
				],
			]);

		this.mem.set(cacheObj, cache);

		const map = {
			id: '_id',
			threadId: 'tmid',
			reactions: 'reactions',
			parseUrls: 'parseUrls',
			text: 'msg',
			createdAt: 'ts',
			updatedAt: '_updatedAt',
			editedAt: 'editedAt',
			emoji: 'emoji',
			avatarUrl: 'avatar',
			alias: 'alias',
			file: 'file',
			customFields: 'customFields',
			groupable: 'groupable',
			token: 'token',
			blocks: 'blocks',
			room: async (message) => {
				const result = await cache.get('room')(message.rid);
				delete message.rid;
				return result;
			},
			editor: async (message) => {
				const { editedBy } = message;
				delete message.editedBy;

				if (!editedBy) {
					return undefined;
				}

				return cache.get('user.convertById')(editedBy._id);
			},
			attachments: async (message) => {
				const result = await this._convertAttachmentsToApp(message.attachments);
				delete message.attachments;
				return result;
			},
			sender: async (message) => {
				if (!message.u || !message.u._id) {
					return undefined;
				}

				// When the message contains token, means the message is from the visitor(omnichannel)
				const user = await (isMessageFromVisitor(msgObj)
					? cache.get('user.convertToApp')(message.u)
					: cache.get('user.convertById')(message.u._id));

				delete message.u;

				/**
				 * Old System Messages from visitor doesn't have the `token` field, to not return
				 * `sender` as undefined, so we need to add this fallback here.
				 */

				return user || cache.get('user.convertToApp')(message.u);
			},
		};

		return transformMappedData(msgObj, map);
	}

	async convertAppMessage(message) {
		if (!message || !message.room) {
			return undefined;
		}

		const room = await Rooms.findOneById(message.room.id);

		if (!room) {
			throw new Error('Invalid room provided on the message.');
		}

		let u;
		if (message.sender && message.sender.id) {
			const user = await Users.findOneById(message.sender.id);

			if (user) {
				u = {
					_id: user._id,
					username: user.username,
					name: user.name,
				};
			} else {
				u = {
					_id: message.sender.id,
					username: message.sender.username,
					name: message.sender.name,
				};
			}
		}

		let editedBy;
		if (message.editor) {
			const editor = await Users.findOneById(message.editor.id);
			editedBy = {
				_id: editor._id,
				username: editor.username,
			};
		}

		const attachments = this._convertAppAttachments(message.attachments);

		const newMessage = {
			_id: message.id || Random.id(),
			...('threadId' in message && { tmid: message.threadId }),
			rid: room._id,
			u,
			msg: message.text,
			ts: message.createdAt || new Date(),
			_updatedAt: message.updatedAt || new Date(),
			...(editedBy && { editedBy }),
			...('editedAt' in message && { editedAt: message.editedAt }),
			...('emoji' in message && { emoji: message.emoji }),
			...('avatarUrl' in message && { avatar: message.avatarUrl }),
			...('alias' in message && { alias: message.alias }),
			...('customFields' in message && { customFields: message.customFields }),
			...('groupable' in message && { groupable: message.groupable }),
			...(attachments && { attachments }),
			...('reactions' in message && { reactions: message.reactions }),
			...('parseUrls' in message && { parseUrls: message.parseUrls }),
			...('blocks' in message && { blocks: message.blocks }),
			...('token' in message && { token: message.token }),
		};

		return Object.assign(newMessage, message._unmappedProperties_);
	}

	_convertAppAttachments(attachments) {
		if (typeof attachments === 'undefined' || !Array.isArray(attachments)) {
			return undefined;
		}

		return attachments.map((attachment) =>
			Object.assign(
				{
					collapsed: attachment.collapsed,
					color: attachment.color,
					text: attachment.text,
					ts: attachment.timestamp ? attachment.timestamp.toJSON() : attachment.timestamp,
					message_link: attachment.timestampLink,
					thumb_url: attachment.thumbnailUrl,
					author_name: attachment.author ? attachment.author.name : undefined,
					author_link: attachment.author ? attachment.author.link : undefined,
					author_icon: attachment.author ? attachment.author.icon : undefined,
					title: attachment.title ? attachment.title.value : undefined,
					title_link: attachment.title ? attachment.title.link : undefined,
					title_link_download: attachment.title ? attachment.title.displayDownloadLink : undefined,
					image_dimensions: attachment.imageDimensions,
					image_preview: attachment.imagePreview,
					image_url: attachment.imageUrl,
					image_type: attachment.imageType,
					image_size: attachment.imageSize,
					audio_url: attachment.audioUrl,
					audio_type: attachment.audioType,
					audio_size: attachment.audioSize,
					video_url: attachment.videoUrl,
					video_type: attachment.videoType,
					video_size: attachment.videoSize,
					fields: attachment.fields,
					button_alignment: attachment.actionButtonsAlignment,
					actions: attachment.actions,
					type: attachment.type,
					description: attachment.description,
				},
				attachment._unmappedProperties_,
			),
		);
	}

	async _convertAttachmentsToApp(attachments) {
		if (typeof attachments === 'undefined' || !Array.isArray(attachments)) {
			return undefined;
		}

		const map = {
			collapsed: 'collapsed',
			color: 'color',
			text: 'text',
			timestampLink: 'message_link',
			thumbnailUrl: 'thumb_url',
			imageDimensions: 'image_dimensions',
			imagePreview: 'image_preview',
			imageUrl: 'image_url',
			imageType: 'image_type',
			imageSize: 'image_size',
			audioUrl: 'audio_url',
			audioType: 'audio_type',
			audioSize: 'audio_size',
			videoUrl: 'video_url',
			videoType: 'video_type',
			videoSize: 'video_size',
			fields: 'fields',
			actionButtonsAlignment: 'button_alignment',
			actions: 'actions',
			type: 'type',
			description: 'description',
			author: (attachment) => {
				const { author_name: name, author_link: link, author_icon: icon } = attachment;

				delete attachment.author_name;
				delete attachment.author_link;
				delete attachment.author_icon;

				return { name, link, icon };
			},
			title: (attachment) => {
				const { title: value, title_link: link, title_link_download: displayDownloadLink } = attachment;

				delete attachment.title;
				delete attachment.title_link;
				delete attachment.title_link_download;

				return { value, link, displayDownloadLink };
			},
			timestamp: (attachment) => {
				const result = new Date(attachment.ts);
				delete attachment.ts;
				return result;
			},
		};

		return Promise.all(attachments.map(async (attachment) => transformMappedData(attachment, map)));
	}
}

export const cachedFunction = <F extends (...args: any[]) => any>(fn: F) => {
	const cache = new Map<string, unknown>();

	return ((...args) => {
		const cacheKey = JSON.stringify(args);

		if (cache.has(cacheKey)) {
			return cache.get(cacheKey) as ReturnType<F>;
		}

		const result = fn(...args);

		cache.set(cacheKey, result);

		return result;
	}) as F;
};

import cloneDeep from 'lodash.clonedeep';

/**
 * Transforms a `data` source object to another object,
 * essentially applying a to -> from mapping provided by
 * `map`. It does not mutate the `data` object.
 *
 * It also inserts in the `transformedObject` a new property
 * called `_unmappedProperties_` which contains properties from
 * the original `data` that have not been mapped to its transformed
 * counterpart. E.g.:
 *
 * ```javascript
 * const data = { _id: 'abcde123456', size: 10 };
 * const map = Object.freeze({ id: '_id' });
 *
 * transformMappedData(data, map);
 * // { id: 'abcde123456', _unmappedProperties_: { size: 10 } }
 * ```
 *
 * In order to compute the unmapped properties, this function will
 * ignore any property on `data` that has been named on the "from" part
 * of the `map`, and will consider properties not mentioned as unmapped.
 *
 * You can also define the "from" part as a function, so you can derive a
 * new value for your property from the original `data`. This function will
 * receive a copy of the original `data` for it to calculate the value
 * for its "to" field. Please note that in this case `transformMappedData`
 * will not be able to determine the source field from your map, so it won't
 * ignore any field you've used to derive your new value. For that, you're
 * going to need to delete the value from the received parameter. E.g:
 *
 * ```javascript
 * const data = { _id: 'abcde123456', size: 10 };
 *
 * // It will look like the `size` property is not mapped
 * const map = {
 *     id: '_id',
 *     newSize: (data) => data.size + 10
 * };
 *
 * transformMappedData(data, map);
 * // { id: 'abcde123456', newSize: 20, _unmappedProperties_: { size: 10 } }
 *
 * // You need to explicitly remove it from the original `data`
 * const map = Object.freeze({
 *     id: '_id',
 *     newSize: (data) => {
 *         const result = data.size + 10;
 *         delete data.size;
 *         return result;
 *     }
 * });
 *
 * transformMappedData(data, map);
 * // { id: 'abcde123456', newSize: 20, _unmappedProperties_: {} }
 * ```
 *
 * @param Object data The data to be transformed
 * @param Object map The map with transformations to be applied
 *
 * @returns Object The data after transformations have been applied
 */

export const transformMappedData = async <
	ResultType extends {
		-readonly [p in keyof MapType]: MapType[p] extends keyof DataType
			? DataType[MapType[p]]
			: MapType[p] extends (...args: any[]) => any
				? Awaited<ReturnType<MapType[p]>>
				: never;
	},
	DataType extends Record<string, any>,
	MapType extends { [p in string]: string | ((data: DataType) => Promise<unknown>) | ((data: DataType) => unknown) },
	UnmappedProperties extends {
		[p in keyof DataType as Exclude<p, MapType[keyof MapType]>]: DataType[p];
	},
>(
	data: DataType,
	map: MapType,
): Promise<ResultType & { _unmappedProperties_: UnmappedProperties }> => {
	const originalData: DataType = cloneDeep(data);
	const transformedData: Record<string, any> = {};

	for await (const [to, from] of Object.entries(map)) {
		if (typeof from === 'function') {
			const result = await from(originalData);

			if (typeof result !== 'undefined') {
				transformedData[to] = result;
			}
		} else if (typeof from === 'string') {
			if (typeof originalData[from] !== 'undefined') {
				transformedData[to] = originalData[from];
			}
			delete originalData[from];
		}
	}

	return {
		...(transformedData as ResultType),
		_unmappedProperties_: originalData as unknown as UnmappedProperties,
	};
};

import { isObject } from '../../../lib/utils/isObject';

export const normalizers = {
	// Set 'id' to '_id' for any sources that provide it
	_id(identity) {
		if (identity._id && !identity.id) {
			identity.id = identity._id;
		}
	},

	// Fix for Reddit
	redit(identity) {
		if (identity.result) {
			return identity.result;
		}
	},

	// Fix WordPress-like identities having 'ID' instead of 'id'
	wordpress(identity) {
		if (identity.ID && !identity.id) {
			identity.id = identity.ID;
		}
	},

	// Fix Auth0-like identities having 'user_id' instead of 'id'
	user_id(identity) {
		if (identity.user_id && !identity.id) {
			identity.id = identity.user_id;
		}
	},

	characterid(identity) {
		if (identity.CharacterID && !identity.id) {
			identity.id = identity.CharacterID;
		}
	},

	// Fix Dataporten having 'user.userid' instead of 'id'
	dataporten(identity) {
		if (identity.user && identity.user.userid && !identity.id) {
			if (identity.user.userid_sec && identity.user.userid_sec[0]) {
				identity.id = identity.user.userid_sec[0];
			} else {
				identity.id = identity.user.userid;
			}
			identity.email = identity.user.email;
		}
	},

	// Fix for Xenforo [BD]API plugin for 'user.user_id; instead of 'id'
	xenforo(identity) {
		if (identity.user && identity.user.user_id && !identity.id) {
			identity.id = identity.user.user_id;
			identity.email = identity.user.user_email;
		}
	},

	// Fix general 'phid' instead of 'id' from phabricator
	phabricator(identity) {
		if (identity.phid && !identity.id) {
			identity.id = identity.phid;
		}
	},

	// Fix Keycloak-like identities having 'sub' instead of 'id'
	kaycloak(identity) {
		if (identity.sub && !identity.id) {
			identity.id = identity.sub;
		}
	},

	// Fix OpenShift identities where id is in 'metadata' object
	openshift(identity) {
		if (!identity.id && identity.metadata && identity.metadata.uid) {
			identity.id = identity.metadata.uid;
			identity.name = identity.fullName;
		}
	},

	// Fix general 'userid' instead of 'id' from provider
	userid(identity) {
		if (identity.userid && !identity.id) {
			identity.id = identity.userid;
		}
	},

	// Fix Nextcloud provider
	nextcloud(identity) {
		if (!identity.id && identity.ocs && identity.ocs.data && identity.ocs.data.id) {
			identity.id = identity.ocs.data.id;
			identity.name = identity.ocs.data.displayname;
			identity.email = identity.ocs.data.email;
		}
	},

	// Fix when authenticating from a meteor app with 'emails' field
	meteor(identity) {
		if (!identity.email && identity.emails && Array.isArray(identity.emails) && identity.emails.length >= 1) {
			identity.email = identity.emails[0].address ? identity.emails[0].address : undefined;
		}
	},
};

const IDENTITY_PROPNAME_FILTER = /(\.)/g;
export const renameInvalidProperties = (input) => {
	if (Array.isArray(input)) {
		return input.map(renameInvalidProperties);
	}
	if (!isObject(input)) {
		return input;
	}

	return Object.entries(input).reduce(
		(result, [name, value]) => ({
			...result,
			[name.replace(IDENTITY_PROPNAME_FILTER, '_')]: renameInvalidProperties(value),
		}),
		{},
	);
};

export const getNestedValue = (propertyPath, source) =>
	propertyPath.split('.').reduce((prev, curr) => (prev ? prev[curr] : undefined), source);

// /^(.+)@/::email
const REGEXP_FROM_FORMULA = /^\/((?!\/::).*)\/::(.+)/;
export const getRegexpMatch = (formula, data) => {
	const regexAndPath = REGEXP_FROM_FORMULA.exec(formula);
	if (!regexAndPath) {
		return getNestedValue(formula, data);
	}
	if (regexAndPath.length !== 3) {
		throw new Error(`expected array of length 3, got ${regexAndPath.length}`);
	}

	const [, regexString, path] = regexAndPath;
	const nestedValue = getNestedValue(path, data);
	const regex = new RegExp(regexString);
	const matches = regex.exec(nestedValue);

	// regexp does not match nested value
	if (!matches) {
		return undefined;
	}

	// we only support regular expressions with a single capture group
	const [, value] = matches;

	// this could mean we return `undefined` (e.g. when capture group is empty)
	return value;
};

const templateStringRegex = /{{((?:(?!}}).)+)}}/g;
export const fromTemplate = (template, data) => {
	if (!templateStringRegex.test(template)) {
		return getNestedValue(template, data);
	}

	return template.replace(templateStringRegex, (fullMatch, match) => getRegexpMatch(match, data));
};

import { escapeRegExp } from '@rocket.chat/string-helpers';

const checkHighlightedWordsInUrls = (msg: string, urlRegex: RegExp) => msg.match(urlRegex);

const removeHighlightedUrls = (msg: string, highlight: string, urlMatches: string[]) => {
	const highlightRegex = new RegExp(highlight, 'gmi');

	return urlMatches.reduce((msg, match) => {
		const withTemplate = match.replace(highlightRegex, `<mark class="highlight-text">${highlight}</mark>`);
		const regexWithTemplate = new RegExp(withTemplate, 'i');
		return msg.replace(regexWithTemplate, match);
	}, msg);
};

const highlightTemplate = '$1<mark class="highlight-text">$2</mark>$3';

export const getRegexHighlight = (highlight: string) =>
	new RegExp(
		`(^|\\b|[\\s\\n\\r\\t.,'\\\"\\+!?:-])(${escapeRegExp(highlight)})($|\\b|[\\s\\n\\r\\t.,'\\\"\\+!?:-])(?![^<]*>|[^<>]*<\\/)`,
		'gmi',
	);

export const getRegexHighlightUrl = (highlight: string) =>
	new RegExp(
		`https?:\/\/(www\\.)?[-a-zA-Z0-9@:%._\\+~#=]{2,256}\\.[a-z]{2,6}\\b([-a-zA-Z0-9@:%_\\+.~#?&//=]*)(${escapeRegExp(
			highlight,
		)})\\b([-a-zA-Z0-9@:%_\\+.~#?&//=]*)`,
		'gmi',
	);

export const highlightWords = (msg: string, highlights: { highlight: string; regex: RegExp; urlRegex: RegExp }[]) =>
	highlights.reduce((msg: string, { highlight, regex, urlRegex }: { highlight: string; regex: RegExp; urlRegex: RegExp }) => {
		const urlMatches = checkHighlightedWordsInUrls(msg, urlRegex);
		if (!urlMatches) {
			return msg.replace(regex, highlightTemplate);
		}
		return removeHighlightedUrls(msg.replace(regex, highlightTemplate), highlight, urlMatches);
	}, msg);

/*
 * Markdown is a named function that will parse markdown syntax
 * @param {Object} message - The message object
 */
import { escapeHTML } from '@rocket.chat/string-helpers';
import { Meteor } from 'meteor/meteor';

import { filtered } from './parser/filtered/filtered';
import { code } from './parser/original/code';
import { original } from './parser/original/original';

const parsers = {
	original,
	filtered,
};

class MarkdownClass {
	parse(text) {
		const message = {
			html: escapeHTML(text),
		};
		return this.mountTokensBack(this.parseMessageNotEscaped(message)).html;
	}

	parseNotEscaped(text) {
		const message = {
			html: text,
		};
		return this.mountTokensBack(this.parseMessageNotEscaped(message)).html;
	}

	parseMessageNotEscaped(message) {
		const options = {
			rootUrl: Meteor.absoluteUrl(),
		};

		return parsers.original(message, options);
	}

	mountTokensBackRecursively(message, tokenList, useHtml = true) {
		const missingTokens = [];

		if (tokenList.length > 0) {
			for (const { token, text, noHtml } of tokenList) {
				if (message.html.indexOf(token) >= 0) {
					message.html = message.html.replace(token, () => (useHtml ? text : noHtml)); // Uses lambda so doesn't need to escape $
				} else {
					missingTokens.push({ token, text, noHtml });
				}
			}
		}

		// If there are tokens that were missing from the string, but the last iteration replaced at least one token, then go again
		// this is done because one of the tokens may have been hidden by another one
		if (missingTokens.length > 0 && missingTokens.length < tokenList.length) {
			this.mountTokensBackRecursively(message, missingTokens, useHtml);
		}
	}

	mountTokensBack(message, useHtml = true) {
		if (message.tokens) {
			this.mountTokensBackRecursively(message, message.tokens, useHtml);
		}

		return message;
	}

	code(...args) {
		return code(...args);
	}

	/** @param {string} message */
	filterMarkdownFromMessage(message) {
		return parsers.filtered(message);
	}
}

export const Markdown = new MarkdownClass();

/** @param {string} message */
export const filterMarkdown = (message) => Markdown.filterMarkdownFromMessage(message);

export const createMarkdownMessageRenderer = ({ ...options }) => {
	const markedParser = parsers.marked;
	return (message, useMarkedParser = false) => {
		if (!message?.html?.trim()) {
			return message;
		}

		return useMarkedParser ? markedParser(message, options) : parsers.original(message, options);
	};
};

export const createMarkdownNotificationRenderer = () => (message) => parsers.filtered(message);

/**
 * Filter markdown tags in message
 * Use case: notifications
 * @param {string} message
 */
export const filtered = (
	message,
	options = {
		supportSchemesForLink: 'http,https',
	},
) => {
	const schemes = options.supportSchemesForLink.split(',').join('|');

	// Remove block code backticks
	message = message.replace(/```/g, '');

	// Remove inline code backticks
	message = message.replace(new RegExp(/`([^`\r\n]+)\`/gm), (match) => match.substr(1, match.length - 2));

	// Filter [text](url), ![alt_text](image_url)
	message = message.replace(new RegExp(`!?\\[([^\\]]+)\\]\\((?:${schemes}):\\/\\/[^\\)]+\\)`, 'gm'), (match, title) => title);

	// Filter <http://link|Text>
	message = message.replace(
		new RegExp(`(?:<|&lt;)(?:${schemes}):\\/\\/[^\\|]+\\|(.+?)(?=>|&gt;)(?:>|&gt;)`, 'gm'),
		(match, title) => title,
	);

	// Filter headings
	message = message.replace(
		/(^#{1,4}) (([\S\w\d-_\/\*\.,\\][ \u00a0\u1680\u180e\u2000-\u200a\u2028\u2029\u202f\u205f\u3000\ufeff]?)+)/gm,
		'$2',
	);

	// Filter bold
	message = message.replace(/(^|>|[ >_~`])\*{1,2}([^\*\r\n]+)\*{1,2}([<_~`]|\B|\b|$)/gm, '$1$2$3');

	// Filter italics
	message = message.replace(/(^|>|[ >*~`])\_{1,2}([^\_\r\n]+)\_{1,2}([<*~`]|\B|\b|$)/gm, '$1$2$3');

	// Filter strike-through text
	message = message.replace(/(^|>|[ >_*`])\~{1,2}([^~\r\n]+)\~{1,2}([<_*`]|\B|\b|$)/gm, '$1$2$3');

	// Filter block quotes
	message = message.replace(/(?:>){3}\n+([\s\S]*?)\n+(?:<){3}/g, '$1');

	// Filter > quote
	message = message.replace(/^>(.*)$/gm, '$1');

	return message;
};

/*
 * code() is a named function that will parse `inline code` and ```codeblock``` syntaxes
 * @param {Object} message - The message object
 */
import { unescapeHTML } from '@rocket.chat/string-helpers';

import hljs, { register } from '../../hljs';
import { addAsToken } from './token';

const inlinecode = (message) => {
	// Support `text`
	message.html = message.html.replace(/\`([^`\r\n]+)\`([<_*~]|\B|\b|$)/gm, (match, p1, p2) =>
		addAsToken(
			message,
			`<span class=\"copyonly\">\`</span><span><code class=\"code-colors inline\">${p1}</code></span><span class=\"copyonly\">\`</span>${p2}`,
			'inlinecode',
			{ noHtml: match },
		),
	);
};

const codeblocks = (message) => {
	// Count occurencies of ```
	const count = (message.html.match(/```/gm) || []).length;

	if (count) {
		// Check if we need to add a final ```
		if (count % 2 > 0) {
			message.html = `${message.html}\n\`\`\``;
			message.msg = `${message.msg}\n\`\`\``;
		}

		// Separate text in code blocks and non code blocks
		const msgParts = message.html.split(/(^.*)(```(?:[a-zA-Z]+)?(?:(?:.|\r|\n)*?)```)(.*\n?)$/gm);

		for (let index = 0; index < msgParts.length; index++) {
			// Verify if this part is code
			const part = msgParts[index];
			const codeMatch = part.match(/^```[\r\n]*(.*[\r\n\ ]?)[\r\n]*([\s\S]*?)```+?$/);

			if (codeMatch) {
				// Process highlight if this part is code
				const singleLine = codeMatch[0].indexOf('\n') === -1;
				const lang = !singleLine && Array.from(hljs.listLanguages()).includes(codeMatch[1].trim()) ? codeMatch[1].trim() : '';
				const emptyLanguage = lang === '' ? unescapeHTML(codeMatch[1] + codeMatch[2]) : unescapeHTML(codeMatch[2]);
				const code = singleLine ? unescapeHTML(codeMatch[1]) : emptyLanguage;

				const result = (() => {
					if (lang) {
						try {
							register(lang);
							return hljs.highlight(lang, code);
						} catch (error) {
							console.error(error);
						}
					}
					return hljs.highlightAuto(lang + code);
				})();
				const token = addAsToken(
					message,
					`<pre><code class='code-colors hljs ${result.language}'><span class='copyonly'>\`\`\`<br></span>${result.value}<span class='copyonly'><br>\`\`\`</span></code></pre>`,
					'code',
					{
						noHtml: codeMatch[0],
						highlight: true,
					},
				);

				msgParts[index] = token;
			} else {
				msgParts[index] = part;
			}
		}

		// Re-mount message
		message.html = msgParts.join('');
	}
};

export const code = (message) => {
	if (message.html?.trim()) {
		codeblocks(message);
		inlinecode(message);
	}

	return message;
};

