/*
 * Markdown is a named function that will parse markdown syntax
 * @param {String} msg - The message html
 */
import type { IMessage, TokenType, TokenExtra } from '@rocket.chat/core-typings';
import { Random } from '@rocket.chat/random';

export const addAsToken = (message: IMessage, html: string, type: TokenType, extra?: TokenExtra): string => {
	if (!message.tokens) {
		message.tokens = [];
	}
	const token = `=!=${Random.id()}=!=`;
	message.tokens.push({
		token,
		type,
		text: html,
		...(extra && { ...extra }),
	});

	return token;
};

export const isToken = (msg: string): boolean => /=!=[.a-z0-9]{17}=!=/gim.test(msg.trim());

export const validateAllowedTokens = (message: IMessage, id: string, desiredTokens: TokenType[]): boolean => {
	const tokens: string[] = id.match(/=!=[.a-z0-9]{17}=!=/gim) || [];
	const tokensFound = message.tokens?.filter(({ token }) => tokens.includes(token)) || [];
	return tokensFound.length === 0 || tokensFound.every((token) => token.type && desiredTokens.includes(token.type));
};

/*
 * Markdown is a named function that will parse markdown syntax
 * @param {Object} message - The message object
 */
import { code } from './code.js';
import { markdown } from './markdown.js';

export const original = (
	message,
	options = {
		supportSchemesForLink: 'http,https',
		headers: true,
	},
) => {
	// Parse markdown code
	message = code(message);

	// Parse markdown
	message = markdown(message, options);

	// Replace linebreak to br
	message.html = message.html.replace(/\n/gm, '<br>');

	return message;
};

import { addAsToken, isToken, validateAllowedTokens } from './token';

const validateUrl = (url, message) => {
	// Don't render markdown inside links
	if (message?.tokens?.some((token) => url.includes(token.token))) {
		return false;
	}

	// Valid urls don't contain whitespaces
	if (/\s/.test(url.trim())) {
		return false;
	}

	try {
		new URL(url);
		return true;
	} catch (e) {
		return false;
	}
};

const endsWithWhitespace = (text) => text.substring(text.length - 1).match(/\s/);

const getParseableMarkersCount = (start, end) => {
	const usableMarkers = start.length > 1 ? 2 : 1;
	return end.length - usableMarkers >= 0 ? usableMarkers : 1;
};

const getTextWrapper = (marker, tagName) => (textPrepend, wrappedText, textAppend) =>
	`${textPrepend}<span class="copyonly">${marker}</span><${tagName}>${wrappedText}</${tagName}><span class="copyonly">${marker}</span>${textAppend}`;

const getRegexReplacer = (replaceFunction, getRegex) => (marker, tagName) => {
	const wrapper = getTextWrapper(marker, tagName);
	return (msg) => msg.replace(getRegex(marker), (...args) => replaceFunction(wrapper, ...args));
};

const getParserWithCustomMarker = getRegexReplacer(
	(wrapper, match, p1, p2, p3) => {
		if (endsWithWhitespace(p2)) {
			return match;
		}
		const finalMarkerCount = getParseableMarkersCount(p1, p3);
		return wrapper(p1.substring(finalMarkerCount), p2, p3.substring(finalMarkerCount));
	},
	(marker) => new RegExp(`(\\${marker}+(?!\\s))([^\\${marker}\\r\\n]+)(\\${marker}+)`, 'gm'),
);

const parseBold = getParserWithCustomMarker('*', 'strong');

const parseStrike = getParserWithCustomMarker('~', 'strike');

const parseItalic = getRegexReplacer(
	(wrapper, match, p1, p2, p3, p4, p5) => {
		if (p1 || p5 || endsWithWhitespace(p3)) {
			return match;
		}

		const finalMarkerCount = getParseableMarkersCount(p2, p4);
		return wrapper(p2.substring(finalMarkerCount), p3, p4.substring(finalMarkerCount));
	},
	() => new RegExp('([^\\r\\n\\s~*_]){0,1}(\\_+(?!\\s))([^\\_\\r\\n]+)(\\_+)([^\\r\\n\\s]){0,1}', 'gm'),
)('_', 'em');

const parseNotEscaped = (message, { supportSchemesForLink, headers, rootUrl }) => {
	let msg = message.html;
	if (!message.tokens) {
		message.tokens = [];
	}

	const schemes = (supportSchemesForLink || '').split(',').join('|');

	if (headers) {
		// Support # Text for h1
		msg = msg.replace(
			/^# (([\S\w\d-_\/\*\.,\\][ \u00a0\u1680\u180e\u2000-\u200a\u2028\u2029\u202f\u205f\u3000\ufeff]?)+)/gm,
			'<h1>$1</h1>',
		);

		// Support # Text for h2
		msg = msg.replace(
			/^## (([\S\w\d-_\/\*\.,\\][ \u00a0\u1680\u180e\u2000-\u200a\u2028\u2029\u202f\u205f\u3000\ufeff]?)+)/gm,
			'<h2>$1</h2>',
		);

		// Support # Text for h3
		msg = msg.replace(
			/^### (([\S\w\d-_\/\*\.,\\][ \u00a0\u1680\u180e\u2000-\u200a\u2028\u2029\u202f\u205f\u3000\ufeff]?)+)/gm,
			'<h3>$1</h3>',
		);

		// Support # Text for h4
		msg = msg.replace(
			/^#### (([\S\w\d-_\/\*\.,\\][ \u00a0\u1680\u180e\u2000-\u200a\u2028\u2029\u202f\u205f\u3000\ufeff]?)+)/gm,
			'<h4>$1</h4>',
		);
	}

	// Support *text* to make bold
	msg = parseBold(msg);

	// Support _text_ to make italics
	msg = parseItalic(msg);

	// // Support ~text~ to strike through text
	msg = parseStrike(msg);

	// Support for block quote
	// >>>
	// Text
	// <<<
	msg = msg.replace(
		/(?:&gt;){3}\n+([\s\S]*?)\n+(?:&lt;){3}/g,
		'<blockquote class="background-transparent-darker-before"><span class="copyonly">&gt;&gt;&gt;</span>$1<span class="copyonly">&lt;&lt;&lt;</span></blockquote>',
	);

	// Support >Text for quote
	msg = msg.replace(
		/^&gt;(.*)$/gm,
		'<blockquote class="background-transparent-darker-before"><span class="copyonly">&gt;</span>$1</blockquote>',
	);

	// Remove white-space around blockquote (prevent <br>). Because blockquote is block element.
	msg = msg.replace(
		/\s*<blockquote class="background-transparent-darker-before">/gm,
		'<blockquote class="background-transparent-darker-before">',
	);
	msg = msg.replace(/<\/blockquote>\s*/gm, '</blockquote>');

	// Remove new-line between blockquotes.
	msg = msg.replace(/<\/blockquote>\n<blockquote/gm, '</blockquote><blockquote');

	// Support ![alt text](http://image url)
	msg = msg.replace(new RegExp(`!\\[([^\\]]+)\\]\\(((?:${schemes}):\\/\\/[^\\s]+)\\)`, 'gm'), (match, title, url) => {
		if (!validateUrl(url, message)) {
			return match;
		}
		if (isToken(title) && !validateAllowedTokens(message, title, ['bold', 'italic', 'strike'])) {
			return match;
		}
		url = encodeURI(url);

		const target = url.indexOf(rootUrl) === 0 ? '' : '_blank';
		return addAsToken(
			message,
			`<a data-title="${url}" href="${url}" title="${title}" target="${target}" rel="noopener noreferrer"><div class="inline-image" style="background-image: url(${url});"></div></a>`,
			'link',
		);
	});

	// Support [Text](http://link)
	msg = msg.replace(new RegExp(`\\[([^\\]]+)\\]\\(((?:${schemes}):\\/\\/[^\\s]+)\\)`, 'gm'), (match, title, url) => {
		if (!validateUrl(url, message)) {
			return match;
		}
		if (isToken(title) && !validateAllowedTokens(message, title, ['bold', 'italic', 'strike'])) {
			return match;
		}
		const target = url.indexOf(rootUrl) === 0 ? '' : '_blank';
		title = title.replace(/&amp;/g, '&');

		const escapedUrl = encodeURI(url);

		return addAsToken(
			message,
			`<a data-title="${escapedUrl}" href="${escapedUrl}" target="${target}" rel="noopener noreferrer">${title}</a>`,
			'link',
		);
	});

	// Support <http://link|Text>
	msg = msg.replace(new RegExp(`(?:<|&lt;)((?:${schemes}):\\\/\\\/[^\\|]+)\\|(.+?)(?=>|&gt;)(?:>|&gt;)`, 'gm'), (match, url, title) => {
		if (!validateUrl(url, message)) {
			return match;
		}
		if (isToken(title) && !validateAllowedTokens(message, title, ['bold', 'italic', 'strike'])) {
			return match;
		}
		url = encodeURI(url);
		const target = url.indexOf(rootUrl) === 0 ? '' : '_blank';
		return addAsToken(message, `<a data-title="${url}" href="${url}" target="${target}" rel="noopener noreferrer">${title}</a>`, 'link');
	});
	return msg;
};

export const markdown = (message, options) => {
	message.html = parseNotEscaped(message, options);
	return message;
};

import type { IMessage } from '@rocket.chat/core-typings';
import { escapeHTML } from '@rocket.chat/string-helpers';

export type MentionsParserArgs = {
	pattern: () => string;
	useRealName?: () => boolean;
	me?: () => string;
	roomTemplate?: (args: { prefix: string; reference: string; mention: string; channel?: any }) => string;
	userTemplate?: (args: { prefix: string; className: string; mention: string; title?: string; label: string; type?: string }) => string;
};

const userTemplateDefault = ({
	prefix,
	className,
	mention,
	title = '',
	label,
	type = 'username',
}: {
	prefix: string;
	className: string;
	mention: string;
	title?: string;
	label?: string;
	type?: string;
}) => `${prefix}<a class="${className}" data-${type}="${mention}"${title ? ` title="${title}"` : ''}>${label}</a>`;

const roomTemplateDefault = ({ prefix, reference, mention }: { prefix: string; reference: string; mention: string }) =>
	`${prefix}<a class="mention-link mention-link--room" data-channel="${reference}">${`#${mention}`}</a>`;

export class MentionsParser {
	me: () => string;

	pattern: MentionsParserArgs['pattern'];

	userTemplate: (args: { prefix: string; className: string; mention: string; title?: string; label: string; type?: string }) => string;

	roomTemplate: (args: { prefix: string; reference: string; mention: string; channel?: any }) => string;

	useRealName: () => boolean;

	constructor({ pattern, useRealName, me, roomTemplate = roomTemplateDefault, userTemplate = userTemplateDefault }: MentionsParserArgs) {
		this.pattern = pattern;
		this.useRealName = useRealName || (() => false);
		this.me = me || (() => '');
		this.userTemplate = userTemplate;
		this.roomTemplate = roomTemplate;
	}

	get userMentionRegex() {
		return new RegExp(`(^|\\s|>)@(${this.pattern()}(@(${this.pattern()}))?(:([0-9a-zA-Z-_.]+))?)`, 'gm');
	}

	get channelMentionRegex() {
		return new RegExp(`(^|\\s|>)#(${this.pattern()}(@(${this.pattern()}))?)`, 'gm');
	}

	replaceUsers = (msg: string, { mentions, temp }: IMessage, me: string) =>
		msg.replace(this.userMentionRegex, (match, prefix, mention) => {
			const classNames = ['mention-link'];

			if (mention === 'all') {
				classNames.push('mention-link--all');
				classNames.push('mention-link--group');
			} else if (mention === 'here') {
				classNames.push('mention-link--here');
				classNames.push('mention-link--group');
			} else if (mention === me) {
				classNames.push('mention-link--me');
				classNames.push('mention-link--user');
			} else {
				classNames.push('mention-link--user');
			}

			const className = classNames.join(' ');

			if (mention === 'all' || mention === 'here') {
				return this.userTemplate({ prefix, className, mention, label: mention, type: 'group' });
			}

			const filterUser = ({ username, type }: { username?: string; type?: string }) => (!type || type === 'user') && username === mention;
			const filterTeam = ({ name, type }: { name?: string; type?: string }) => type === 'team' && name === mention;

			const [mentionObj] = (mentions || []).filter((m) => m && (filterUser(m) || filterTeam(m)));

			const label = temp
				? mention && escapeHTML(mention)
				: mentionObj && escapeHTML((mentionObj.type === 'team' || this.useRealName() ? mentionObj.name : mentionObj.username) || '');

			if (!label) {
				return match;
			}

			return this.userTemplate({
				prefix,
				className,
				mention,
				label,
				type: mentionObj?.type === 'team' ? 'team' : 'username',
				title: this.useRealName() ? mention : label,
			});
		});

	replaceChannels = (msg: string, { temp, channels }: IMessage) =>
		msg.replace(/&#39;/g, "'").replace(this.channelMentionRegex, (match, prefix, mention) => {
			if (
				!temp &&
				!channels?.find((c) => {
					return c.name === mention;
				})
			) {
				return match;
			}

			const channel = channels?.find(({ name }) => {
				return name === mention;
			});
			const reference = channel ? channel._id : mention;
			return this.roomTemplate({ prefix, reference, channel, mention });
		});

	getUserMentions(str: string) {
		return (str.match(this.userMentionRegex) || []).map((match) => match.trim());
	}

	getChannelMentions(str: string) {
		return (str.match(this.channelMentionRegex) || []).map((match) => match.trim());
	}

	parse(message: IMessage) {
		let msg = message?.html || '';
		if (!msg.trim()) {
			return message;
		}
		msg = this.replaceUsers(msg, message, this.me());
		msg = this.replaceChannels(msg, message);
		message.html = msg;
		return message;
	}
}

/*
 * Mentions is a named function that will process Mentions
 * @param {Object} message - The message object
 */
import { isE2EEMessage, type IMessage, type IRoom, type IUser } from '@rocket.chat/core-typings';

import { type MentionsParserArgs, MentionsParser } from '../lib/MentionsParser';

type MentionsServerArgs = MentionsParserArgs & {
	messageMaxAll: () => number;
	getChannels: (c: string[]) => Promise<Pick<IRoom, '_id' | 'name' | 'fname' | 'federated'>[]>;
	getUsers: (u: string[]) => Promise<{ type: 'team' | 'user'; _id: string; username?: string; name?: string }[]>;
	getUser: (u: string) => Promise<IUser | null>;
	getTotalChannelMembers: (rid: string) => Promise<number>;
	onMaxRoomMembersExceeded: ({ sender, rid }: { sender: IMessage['u']; rid: string }) => Promise<void>;
};

export class MentionsServer extends MentionsParser {
	messageMaxAll: MentionsServerArgs['messageMaxAll'];

	getChannels: MentionsServerArgs['getChannels'];

	getUsers: MentionsServerArgs['getUsers'];

	getUser: MentionsServerArgs['getUser'];

	getTotalChannelMembers: MentionsServerArgs['getTotalChannelMembers'];

	onMaxRoomMembersExceeded: MentionsServerArgs['onMaxRoomMembersExceeded'];

	constructor(args: MentionsServerArgs) {
		super(args);

		this.messageMaxAll = args.messageMaxAll;
		this.getChannels = args.getChannels;
		this.getUsers = args.getUsers;
		this.getUser = args.getUser;
		this.getTotalChannelMembers = args.getTotalChannelMembers;
		this.onMaxRoomMembersExceeded =
			args.onMaxRoomMembersExceeded ||
			(() => {
				/* do nothing */
			});
	}

	async getUsersByMentions(message: IMessage): Promise<IMessage['mentions']> {
		const { msg, rid, u: sender, e2eMentions }: Pick<IMessage, 'msg' | 'rid' | 'u' | 't' | 'e2eMentions'> = message;

		const mentions =
			isE2EEMessage(message) && e2eMentions?.e2eUserMentions && e2eMentions?.e2eUserMentions.length > 0
				? e2eMentions?.e2eUserMentions
				: this.getUserMentions(msg);
		const mentionsAll: { _id: string; username: string }[] = [];
		const userMentions = [];

		for await (const m of mentions) {
			const mention = m.trim().substr(1);
			if (mention !== 'all' && mention !== 'here') {
				userMentions.push(mention);
				continue;
			}
			if (this.messageMaxAll() > 0 && (await this.getTotalChannelMembers(rid)) > this.messageMaxAll()) {
				await this.onMaxRoomMembersExceeded({ sender, rid });
				continue;
			}
			mentionsAll.push({
				_id: mention,
				username: mention,
			});
		}

		return [...mentionsAll, ...(userMentions.length ? await this.getUsers(userMentions) : [])];
	}

	async getChannelbyMentions(message: IMessage) {
		const { msg, e2eMentions }: Pick<IMessage, 'msg' | 't' | 'e2eMentions'> = message;

		const channels =
			isE2EEMessage(message) && e2eMentions?.e2eChannelMentions && e2eMentions?.e2eChannelMentions.length > 0
				? e2eMentions?.e2eChannelMentions
				: this.getChannelMentions(msg);
		return this.getChannels(channels.map((c) => c.trim().substr(1)));
	}

	async execute(message: IMessage) {
		const mentionsAll = await this.getUsersByMentions(message);
		const channels = await this.getChannelbyMentions(message);

		message.mentions = mentionsAll;
		message.channels = channels;

		return message;
	}
}

import UAParser from 'ua-parser-js';

const mergeDeep = (target, source) => {
	if (!(typeof target === 'object' && typeof source === 'object')) {
		return target;
	}

	for (const key in source) {
		if (source[key] === null && (target[key] === undefined || target[key] === null)) {
			target[key] = null;
		} else if (source[key] instanceof Array) {
			if (!target[key]) {
				target[key] = [];
			}
			target[key] = target[key].concat(source[key]);
		} else if (typeof source[key] === 'object') {
			if (!target[key]) {
				target[key] = {};
			}
			mergeDeep(target[key], source[key]);
		} else {
			target[key] = source[key];
		}
	}

	return target;
};

export const UAParserMobile = {
	appName: 'RC Mobile',
	device: 'mobile-app',
	uaSeparator: ';',
	props: {
		os: {
			list: ['name', 'version'],
		},
		app: {
			list: ['version', 'bundle'],
			get: (prop, value) => {
				if (prop === 'bundle') {
					return value.replace(/([()])/g, '');
				}

				if (prop === 'version') {
					return value.replace(/^v/g, '');
				}

				return value;
			},
		},
	},

	isMobileApp(uaString) {
		if (!uaString || typeof uaString !== 'string') {
			return false;
		}

		const splitUA = uaString.split(this.uaSeparator);
		return splitUA && splitUA[0] && splitUA[0].trim() === this.appName;
	},

	/**
	 *
	 * @param {string} uaString
	 * @returns { device: { type: '' }, app: { name: '', version: '' } }
	 */
	uaObject(uaString) {
		if (!this.isMobileApp(uaString)) {
			return {};
		}

		const splitUA = uaString.split(this.uaSeparator);

		let obj = {
			device: {
				type: this.device,
			},
			app: {
				name: splitUA[0],
			},
		};

		splitUA.shift(); // remove first element
		if (splitUA.length === 0) {
			return obj;
		}

		splitUA.forEach((element, index) => {
			const splitProps = element.trim().split(' ');
			const key = Object.keys(this.props)[index];
			if (!key) {
				return;
			}

			const props = this.props[key];
			if (!props.list || !Array.isArray(props.list) || props.list.length === 0) {
				return;
			}

			const subProps = {};
			splitProps.forEach((value, idx) => {
				if (props.list.length > idx) {
					const propName = props.list[idx];
					subProps[propName] = props.get ? props.get(propName, value) : value;
				}
			});

			const prop = {};
			prop[key] = subProps;
			obj = mergeDeep(obj, prop);
		});

		return obj;
	},
};

export const UAParserDesktop = {
	device: 'desktop-app',

	isDesktopApp(uaString) {
		if (!uaString || typeof uaString !== 'string') {
			return false;
		}

		return uaString.includes(' Electron/');
	},

	/**
	 *
	 * @param {string} uaString
	 * @returns { device: { type: '' }, os: '' || {}, app: { name: '', version: '' } }
	 */
	uaObject(uaString) {
		if (!this.isDesktopApp(uaString)) {
			return {};
		}

		const ua = new UAParser(uaString);
		const uaParsed = ua.getResult();

		const [, name, version] = uaString.match(/(Rocket\.Chat)\/(\d+(\.\d+)+)/) || [];

		return {
			device: {
				type: this.device,
			},
			os: uaParsed.os,
			app: {
				name,
				version,
			},
		};
	},
};

import { escapeRegExp } from '@rocket.chat/string-helpers';

import { isURL } from '../../../lib/utils/isURL';
import { ltrim, rtrim, trim } from '../../../lib/utils/stringUtils';

function getCloudUrl(
	path: string,
	// eslint-disable-next-line @typescript-eslint/naming-convention
	_site_url: string,
	cloudRoute: string,
	cloudParams: Record<string, string> = {},
	deeplinkUrl = '',
): string {
	const cloudBaseUrl = deeplinkUrl.replace(/\/+$/, '');

	const siteUrl = rtrim(_site_url, '/');

	// Remove the protocol
	const host = siteUrl.replace(/https?\:\/\//i, '');
	path = ltrim(path, '/');

	Object.assign(cloudParams, {
		host,
		path,
	});

	if (siteUrl.includes('http://')) {
		cloudParams.secure = 'no';
	}

	const params = Object.entries(cloudParams)
		.map(([key, value]) => `${key}=${encodeURIComponent(value)}`)
		.join('&');

	return `${cloudBaseUrl}/${cloudRoute}?${params}`;
}

export const _getURL = (
	path: string,
	// eslint-disable-next-line @typescript-eslint/naming-convention
	{ cdn, full, cloud, cloud_route, cloud_params, _cdn_prefix, _root_url_path_prefix, _site_url }: Record<string, any>,
	deeplinkUrl?: string,
): string => {
	if (isURL(path)) {
		return path;
	}

	const [_path, _query] = path.split('?');
	path = _path;
	const query = _query ? `?${_query}` : '';

	const siteUrl = rtrim(trim(_site_url || ''), '/');
	const cloudRoute = trim(cloud_route || '');
	const cdnPrefix = rtrim(trim(_cdn_prefix || ''), '/');
	const pathPrefix = rtrim(trim(_root_url_path_prefix || ''), '/');

	const finalPath = ltrim(trim(path), '/');

	const url = rtrim(`${pathPrefix}/${finalPath}`, '/') + query;

	if (cloud) {
		const cloudParams = cloud_params || {};
		return getCloudUrl(url, siteUrl, cloudRoute, cloudParams, deeplinkUrl);
	}

	if (cdn && cdnPrefix !== '') {
		return cdnPrefix + url;
	}

	if (full) {
		return siteUrl.replace(new RegExp(`${escapeRegExp(pathPrefix)}$`), '') + url;
	}

	return url;
};

export const getURLWithoutSettings = (
	path: string,
	// eslint-disable-next-line @typescript-eslint/naming-convention
	{
		cdn = true,
		full = false,
		cloud = false,
		cloud_route = '',
		cloud_params = {},
	}: {
		cdn?: boolean;
		full?: boolean;
		cloud?: boolean;
		cloud_route?: string;
		cloud_params?: Record<string, string>;
	},
	cdnPrefix: string,
	siteUrl: string,
	cloudDeepLinkUrl?: string,
): string =>
	_getURL(
		path,
		{
			cdn,
			full,
			cloud,
			cloud_route,
			cloud_params,
			_cdn_prefix: cdnPrefix,
			_root_url_path_prefix: __meteor_runtime_config__.ROOT_URL_PATH_PREFIX,
			_site_url: siteUrl,
		},
		cloudDeepLinkUrl,
	);

