export const isURL = (str: string): boolean => /^(https?:\/\/|data:)/.test(str);

import type { IUser, IRoom, IOmnichannelRoom } from '@rocket.chat/core-typings';
import { isOmnichannelRoom } from '@rocket.chat/core-typings';
import { LivechatRooms, Subscriptions } from '@rocket.chat/models';

import { hasPermissionAsync } from '../../../authorization/server/functions/hasPermission';
import { Livechat } from '../../../livechat/server/lib/LivechatTyped';
import type { CloseRoomParams } from '../../../livechat/server/lib/localTypes';
import { notifyOnSubscriptionChanged } from '../lib/notifyListener';

export const closeLivechatRoom = async (
	user: IUser,
	roomId: IRoom['_id'],
	{
		comment,
		tags,
		generateTranscriptPdf,
		transcriptEmail,
	}: {
		comment?: string;
		tags?: string[];
		generateTranscriptPdf?: boolean;
		transcriptEmail?:
			| {
					sendToVisitor: false;
			  }
			| {
					sendToVisitor: true;
					requestData: Pick<NonNullable<IOmnichannelRoom['transcriptRequest']>, 'email' | 'subject'>;
			  };
	},
): Promise<void> => {
	const room = await LivechatRooms.findOneById(roomId);
	if (!room || !isOmnichannelRoom(room)) {
		throw new Error('error-invalid-room');
	}

	if (!room.open) {
		const { deletedCount } = await Subscriptions.removeByRoomId(roomId, {
			async onTrash(doc) {
				void notifyOnSubscriptionChanged(doc, 'removed');
			},
		});
		if (deletedCount) {
			return;
		}
		throw new Error('error-room-already-closed');
	}

	const subscription = await Subscriptions.findOneByRoomIdAndUserId(roomId, user._id, { projection: { _id: 1 } });
	if (!subscription && !(await hasPermissionAsync(user._id, 'close-others-livechat-room'))) {
		throw new Error('error-not-authorized');
	}

	const options: CloseRoomParams['options'] = {
		clientAction: true,
		tags,
		...(generateTranscriptPdf && { pdfTranscript: { requestedBy: user._id } }),
		...(transcriptEmail && {
			...(transcriptEmail.sendToVisitor
				? {
						emailTranscript: {
							sendToVisitor: true,
							requestData: {
								email: transcriptEmail.requestData.email,
								subject: transcriptEmail.requestData.subject,
								requestedAt: new Date(),
								requestedBy: user,
							},
						},
					}
				: {
						emailTranscript: {
							sendToVisitor: false,
						},
					}),
		}),
	};

	await Livechat.closeRoom({
		room,
		user,
		options,
		comment,
	});
};

export const getModifiedHttpHeaders = (httpHeaders: Record<string, any>) => {
	const modifiedHttpHeaders = { ...httpHeaders };

	if ('x-auth-token' in modifiedHttpHeaders) {
		modifiedHttpHeaders['x-auth-token'] = '[redacted]';
	}

	if (modifiedHttpHeaders.cookie) {
		const cookies = modifiedHttpHeaders.cookie.split('; ');
		const modifiedCookies = cookies.map((cookie: string) => {
			if (cookie.startsWith('rc_token=')) {
				return 'rc_token=[redacted]';
			}
			return cookie;
		});
		modifiedHttpHeaders.cookie = modifiedCookies.join('; ');
	}

	return modifiedHttpHeaders;
};

import type { IMessage } from '@rocket.chat/core-typings';
import { escapeRegExp } from '@rocket.chat/string-helpers';

/**
 * Checks if a message contains a user highlight
 *
 * @param {string} message
 * @param {array|undefined} highlights
 *
 * @returns {boolean}
 */
export function messageContainsHighlight(message: Pick<IMessage, 'msg'>, highlights: string[] | undefined): boolean {
	if (!highlights || highlights.length === 0) {
		return false;
	}

	return highlights.some((highlight: string) => {
		const hl = escapeRegExp(highlight);
		const regexp = new RegExp(`(?<!:)\\b${hl}\\b:|:\\b${hl}(?!:)\\b|\\b(?<!:)${hl}(?!:)\\b`, 'i');
		return regexp.test(message.msg);
	});
}

