import type { IRole } from '@rocket.chat/core-typings';

type PriorityRoleName =
	| 'custom-role'
	| 'admin'
	| 'livechat-manager'
	| 'livechat-monitor'
	| 'livechat-agent'
	| 'user'
	| 'app'
	| 'bot'
	| 'guest'
	| 'anonymous';

const order = [
	'admin',
	'livechat-manager',
	'livechat-monitor',
	'livechat-agent',
	'custom-role',
	'user',
	'app',
	'bot',
	'guest',
	'anonymous',
] as const;

const rolesToConsiderAsUser = ['auditor', 'auditor-log'];

export function getMostImportantRole(roles: IRole['_id'][] = []): 'no-role' | PriorityRoleName {
	if (!roles.length) {
		return 'no-role';
	}

	roles = roles.map((r) => (rolesToConsiderAsUser.includes(r) ? 'user' : r));

	if (roles.length === 1) {
		if (!(order as readonly string[]).includes(roles[0])) {
			return 'custom-role';
		}
		return roles[0] as PriorityRoleName;
	}

	const newRoles: PriorityRoleName[] = [];
	for (const role of roles) {
		if ((order as readonly string[]).includes(role)) {
			newRoles.push(role as PriorityRoleName);
		} else if (!newRoles.includes('custom-role')) {
			newRoles.push('custom-role');
		}
	}

	for (const item of order) {
		if (newRoles.includes(item)) {
			return item;
		}
	}

	return 'no-role';
}

export function parseCSV(csv: string, removeEmptyItems = true): string[] {
	const items = csv.split(',').map((item) => item.trim());

	if (removeEmptyItems) {
		return items.filter(Boolean);
	}

	return items;
}

function split(parameters: string, ignoreExtraUnquotedSpaces = true): string[] {
	// Replace \n\r with a single \n
	const line = parameters.replace(/\n\r/gm, '\n');

	const items = line.split(/[ \n\r\t]/);

	if (ignoreExtraUnquotedSpaces) {
		return items.filter(Boolean);
	}

	return items;
}

export function parseParameters(parameters: string, ignoreExtraUnquotedSpaces = true): string[] {
	if (!parameters.trim()) {
		return [];
	}

	const match = parameters.match(/((["'])(?:(?=(\\?))\3.)*?\2)/gs);
	let line = parameters;

	if (!match) {
		return split(line, ignoreExtraUnquotedSpaces);
	}

	match.forEach((item) => {
		const newItem = item
			// Replace start quote with SSA character
			.replace(/(^['"])/g, '\u0086')
			// Replace end quote with ESA character
			.replace(/['"]$/g, '\u0087')
			// Replace spaces inside the quotes with a Punctuation Space character
			.replace(/ /g, '\u2008')
			// Replace new lines inside the quotes with a PLD character
			.replace(/\n/gm, '\u008B')
			// Replace carriage returns inside the quotes with a PLU character
			.replace(/\r/gm, '\u008C')
			// Replace tabs inside the quotes with a VTS character
			.replace(/\t/g, '\u008A');

		line = line.replace(item, newItem);
	});

	// If two quoted parameters are not separated by a space, add one automatically
	line = line.replace(/\u0087\u0086/g, '\u0087 \u0086');

	const items = split(line, ignoreExtraUnquotedSpaces);

	return items.map((item) =>
		item
			// Convert back the spaces from inside quotes
			.replace(/\u2008/g, ' ')
			// Convert back the new lines from inside quotes
			.replace(/\u008B/g, '\n')
			// Convert back the carriage returns from inside quotes
			.replace(/\u008C/g, '\r')
			// Convert back the tabs from inside quotes
			.replace(/\u008A/g, '\t')
			// Remove SSA and ESA characters
			.replace(/[\u0086\u0087]/g, '')
			// Unescape quotes
			.replace(/\\\"/g, '"'),
	);
}

import { expect } from 'chai';
import { describe, it } from 'mocha';
import proxyquire from 'proxyquire';
import sinon from 'sinon';

const mocks = {
	utils: {
		userCanAccessAvatar: sinon.stub(),
		renderSVGLetters: sinon.stub(),
	},
};

const { protectAvatarsWithFallback } = proxyquire.noCallThru().load('./auth.ts', {
	'../utils': mocks.utils,
});

describe('#protectAvatarsWithFallback()', () => {
	const response = {
		setHeader: sinon.spy(),
		writeHead: sinon.spy(),
		write: sinon.spy(),
		end: sinon.spy(),
	};
	const next = sinon.spy();

	afterEach(() => {
		response.setHeader.resetHistory();
		response.writeHead.resetHistory();
		response.end.resetHistory();
		next.resetHistory();

		Object.values(mocks.utils).forEach((mock) => mock.reset());
	});

	it(`should write 404 to head if no url provided`, async () => {
		await protectAvatarsWithFallback({}, response, next);
		expect(next.called).to.be.false;
		expect(response.setHeader.called).to.be.false;
		expect(response.writeHead.calledWith(404)).to.be.true;
		expect(response.end.calledOnce).to.be.true;
	});

	it(`should write 200 to head and write fallback to body (user avatar)`, async () => {
		mocks.utils.renderSVGLetters.returns('fallback');

		await protectAvatarsWithFallback({ url: '/jon' }, response, next);
		expect(next.called).to.be.false;
		expect(response.setHeader.called).to.be.false;

		expect(response.writeHead.calledWith(200, { 'Content-Type': 'image/svg+xml' })).to.be.true;
		expect(mocks.utils.renderSVGLetters.calledWith('jon')).to.be.true;
		expect(response.write.calledWith('fallback')).to.be.true;

		expect(response.end.calledOnce).to.be.true;
	});

	it(`should write 200 to head and write fallback to body (user avatar)`, async () => {
		mocks.utils.renderSVGLetters.returns('fallback');

		await protectAvatarsWithFallback({ url: '/jon' }, response, next);
		expect(next.called).to.be.false;
		expect(response.setHeader.called).to.be.false;
		expect(response.writeHead.calledWith(200, { 'Content-Type': 'image/svg+xml' })).to.be.true;
		expect(response.write.calledWith('fallback')).to.be.true;
		expect(response.end.calledOnce).to.be.true;
	});

	it(`should write 200 to head and write fallback to body (room avatar)`, async () => {
		mocks.utils.renderSVGLetters.returns('fallback');

		await protectAvatarsWithFallback({ url: '/room/jon' }, response, next);
		expect(next.called).to.be.false;
		expect(response.setHeader.called).to.be.false;
		expect(response.writeHead.calledWith(200, { 'Content-Type': 'image/svg+xml' })).to.be.true;
		expect(response.write.calledWith('fallback')).to.be.true;
		expect(response.end.calledOnce).to.be.true;
	});

	it(`should call next if user can access avatar`, async () => {
		mocks.utils.userCanAccessAvatar.returns(true);
		const request = { url: '/jon' };

		await protectAvatarsWithFallback(request, response, next);
		expect(mocks.utils.userCanAccessAvatar.calledWith(request)).to.be.true;
		expect(next.called).to.be.true;
	});
});

import type { IncomingMessage, ServerResponse } from 'http';

import type { IIncomingMessage } from '@rocket.chat/core-typings';
import type { NextFunction } from 'connect';

import { userCanAccessAvatar, renderSVGLetters } from '../utils';

const renderFallback = (req: IncomingMessage, res: ServerResponse) => {
	if (!req.url) {
		res.writeHead(404);
		res.end();
		return;
	}

	let roomOrUsername;

	if (req.url.startsWith('/room')) {
		roomOrUsername = req.url.split('/')[2] || 'Room';
	} else {
		roomOrUsername = req.url.split('/')[1] || 'Anonymous';
	}

	res.writeHead(200, { 'Content-Type': 'image/svg+xml' });
	res.write(renderSVGLetters(roomOrUsername, 200));
	res.end();
};

const getProtectAvatars = (callback?: typeof renderFallback) => async (req: IncomingMessage, res: ServerResponse, next: NextFunction) => {
	if (!(await userCanAccessAvatar(req as IIncomingMessage))) {
		if (callback) {
			callback(req, res);
			return;
		}

		res.writeHead(404);
		res.end();
		return;
	}

	return next();
};

// If unauthorized returns the SVG fallback (letter avatar)
export const protectAvatarsWithFallback = getProtectAvatars(renderFallback);

