export const getExternalMessageSender = ({
	message,
	bridge,
	internalFileAdapter,
	internalMessageAdapter,
	internalUserAdapter,
	isThreadedMessage,
}: {
	message: IMessage;
	isThreadedMessage: boolean;
	bridge: IFederationBridge;
	internalFileAdapter: RocketChatFileAdapter;
	internalMessageAdapter: RocketChatMessageAdapter;
	internalUserAdapter: RocketChatUserAdapter;
}): IExternalMessageSender => {
	if (isThreadedMessage) {
		return message.files
			? new ThreadFileExternalMessageSender(bridge, internalFileAdapter, internalMessageAdapter)
			: new ThreadTextExternalMessageSender(bridge, internalMessageAdapter, internalUserAdapter);
	}
	return message.files
		? new FileExternalMessageSender(bridge, internalFileAdapter, internalMessageAdapter)
		: new TextExternalMessageSender(bridge, internalMessageAdapter, internalUserAdapter);
};

import type { IMessage, IUser } from '@rocket.chat/core-typings';
import { isMessageFromMatrixFederation } from '@rocket.chat/core-typings';

import { Federation } from '../../../../Federation';
import { FederatedUser } from '../../../../domain/FederatedUser';
import type { IFederationBridge } from '../../../../domain/IFederationBridge';
import type { RocketChatMessageAdapter } from '../../../../infrastructure/rocket-chat/adapters/Message';
import type { RocketChatRoomAdapter } from '../../../../infrastructure/rocket-chat/adapters/Room';
import type { RocketChatSettingsAdapter } from '../../../../infrastructure/rocket-chat/adapters/Settings';
import type { RocketChatUserAdapter } from '../../../../infrastructure/rocket-chat/adapters/User';

export class FederationMessageServiceSender {
	constructor(
		protected internalRoomAdapter: RocketChatRoomAdapter,
		protected internalUserAdapter: RocketChatUserAdapter,
		protected internalSettingsAdapter: RocketChatSettingsAdapter,
		protected internalMessageAdapter: RocketChatMessageAdapter,
		protected bridge: IFederationBridge,
	) {}

	public async sendExternalMessageReaction(internalMessage: IMessage, internalUser: IUser, reaction: string): Promise<void> {
		if (!internalMessage || !internalUser || !internalUser._id || !internalMessage.rid) {
			return;
		}
		const federatedSender = await this.internalUserAdapter.getFederatedUserByInternalId(internalUser._id);
		if (!federatedSender) {
			return;
		}

		const federatedRoom = await this.internalRoomAdapter.getFederatedRoomByInternalId(internalMessage.rid);
		if (!federatedRoom) {
			return;
		}

		if (!isMessageFromMatrixFederation(internalMessage)) {
			return;
		}

		const isUserFromTheSameHomeServer = FederatedUser.isOriginalFromTheProxyServer(
			this.bridge.extractHomeserverOrigin(federatedSender.getExternalId()),
			this.internalSettingsAdapter.getHomeServerDomain(),
		);
		if (!isUserFromTheSameHomeServer) {
			return;
		}

		const eventId = await this.bridge.sendMessageReaction(
			federatedRoom.getExternalId(),
			federatedSender.getExternalId(),
			internalMessage.federation?.eventId as string,
			reaction,
		);
		federatedSender.getUsername() &&
			(await this.internalMessageAdapter.setExternalFederationEventOnMessageReaction(
				federatedSender.getUsername() as string,
				internalMessage,
				reaction,
				eventId,
			));
	}

	public async sendExternalMessageUnReaction(internalMessage: IMessage, internalUser: IUser, reaction: string): Promise<void> {
		if (!internalMessage || !internalUser || !internalUser._id || !internalMessage.rid) {
			return;
		}
		const federatedSender = await this.internalUserAdapter.getFederatedUserByInternalId(internalUser._id);
		if (!federatedSender) {
			return;
		}

		const federatedRoom = await this.internalRoomAdapter.getFederatedRoomByInternalId(internalMessage.rid);
		if (!federatedRoom) {
			return;
		}

		if (!isMessageFromMatrixFederation(internalMessage)) {
			return;
		}

		const isUserFromTheSameHomeServer = FederatedUser.isOriginalFromTheProxyServer(
			this.bridge.extractHomeserverOrigin(federatedSender.getExternalId()),
			this.internalSettingsAdapter.getHomeServerDomain(),
		);
		if (!isUserFromTheSameHomeServer) {
			return;
		}
		// TODO: leaked business logic, move this to the domain layer
		const externalEventId = Object.keys(internalMessage.reactions?.[reaction].federationReactionEventIds || {}).find(
			(key) => internalMessage.reactions?.[reaction].federationReactionEventIds?.[key] === internalUser.username,
		);
		if (!externalEventId) {
			return;
		}
		const normalizedEventId = Federation.unescapeExternalFederationEventId(externalEventId);
		await this.bridge.redactEvent(federatedRoom.getExternalId(), federatedSender.getExternalId(), normalizedEventId);
		await this.internalMessageAdapter.unsetExternalFederationEventOnMessageReaction(externalEventId, internalMessage, reaction);
	}
}

import type { IRoom, IUser, ValueOf } from '@rocket.chat/core-typings';
import { isRoomFederated, isDirectMessageRoom } from '@rocket.chat/core-typings';
import { Subscriptions } from '@rocket.chat/models';

import { RoomMemberActions, RoomSettingsEnum } from '../../../definition/IRoomTypeConfig';
import {
	escapeExternalFederationEventId,
	unescapeExternalFederationEventId,
} from './infrastructure/rocket-chat/adapters/federation-id-escape-helper';

const allowedActionsInFederatedRooms: ValueOf<typeof RoomMemberActions>[] = [
	RoomMemberActions.REMOVE_USER,
	RoomMemberActions.SET_AS_OWNER,
	RoomMemberActions.SET_AS_MODERATOR,
	RoomMemberActions.INVITE,
	RoomMemberActions.JOIN,
	RoomMemberActions.LEAVE,
];

const allowedActionsForModerators: ValueOf<typeof RoomMemberActions>[] = allowedActionsInFederatedRooms.filter(
	(action) => action !== RoomMemberActions.SET_AS_OWNER,
);

const allowedRoomSettingsChangesInFederatedRooms: ValueOf<typeof RoomSettingsEnum>[] = [RoomSettingsEnum.NAME, RoomSettingsEnum.TOPIC];

export class Federation {
	public static async actionAllowed(room: IRoom, action: ValueOf<typeof RoomMemberActions>, userId?: IUser['_id']): Promise<boolean> {
		if (!isRoomFederated(room)) {
			return false;
		}
		if (isDirectMessageRoom(room)) {
			return false;
		}
		if (!userId) {
			return true;
		}

		const userSubscription = await Subscriptions.findOneByRoomIdAndUserId(room._id, userId);
		if (!userSubscription) {
			return true;
		}

		if (action === RoomMemberActions.LEAVE) {
			return true;
		}

		if (userSubscription.roles?.includes('owner')) {
			return allowedActionsInFederatedRooms.includes(action);
		}

		if (userSubscription.roles?.includes('moderator')) {
			return allowedActionsForModerators.includes(action);
		}

		return false;
	}

	public static isAFederatedUsername(username: string): boolean {
		return username.includes('@') && username.includes(':');
	}

	public static escapeExternalFederationEventId(externalEventId: string): string {
		return escapeExternalFederationEventId(externalEventId);
	}

	public static unescapeExternalFederationEventId(externalEventId: string): string {
		return unescapeExternalFederationEventId(externalEventId);
	}

	public static isRoomSettingAllowed(room: Partial<IRoom>, setting: ValueOf<typeof RoomSettingsEnum>): boolean {
		if (!isRoomFederated(room)) {
			return false;
		}

		if (isDirectMessageRoom(room)) {
			return false;
		}
		return allowedRoomSettingsChangesInFederatedRooms.includes(setting);
	}
}

