		const federatedUser =
			federatedRoom.getCreatorId() && (await this.internalUserAdapter.getFederatedUserByInternalId(federatedRoom.getCreatorId() as string));
		if (!federatedUser) {
			return;
		}

		const isRoomFromTheSameHomeServer = FederatedRoom.isOriginalFromTheProxyServer(
			this.bridge.extractHomeserverOrigin(federatedRoom.getExternalId()),
			this.internalSettingsAdapter.getHomeServerDomain(),
		);
		if (!isRoomFromTheSameHomeServer) {
			return;
		}

		const externalRoomTopic = await this.bridge.getRoomTopic(federatedRoom.getExternalId(), federatedUser.getExternalId());
		if (!federatedRoom.shouldUpdateRoomTopic(externalRoomTopic || '')) {
			return;
		}

		await this.bridge.setRoomTopic(federatedRoom.getExternalId(), federatedUser.getExternalId(), internalRoomTopic);
	}

	private async rollbackRoomRoles(
		federatedRoom: FederatedRoom,
		targetFederatedUser: FederatedUser,
		fromUser: FederatedUser,
		rolesToAdd: ROCKET_CHAT_FEDERATION_ROLES[],
		rolesToRemove: ROCKET_CHAT_FEDERATION_ROLES[],
	): Promise<void> {
		await this.internalRoomAdapter.applyRoomRolesToUser({
			federatedRoom,
			targetFederatedUser,
			fromUser,
			rolesToAdd,
			rolesToRemove,
			notifyChannel: false,
		});
		this.internalNotificationAdapter.notifyWithEphemeralMessage(
			'Federation_Matrix_error_applying_room_roles',
			fromUser.getInternalId(),
			federatedRoom.getInternalId(),
		);
	}
}

import type { IFederationBridge } from '../../../domain/IFederationBridge';
import type { RocketChatFileAdapter } from '../../../infrastructure/rocket-chat/adapters/File';
import type { RocketChatNotificationAdapter } from '../../../infrastructure/rocket-chat/adapters/Notification';
import type { RocketChatRoomAdapter } from '../../../infrastructure/rocket-chat/adapters/Room';
import type { RocketChatSettingsAdapter } from '../../../infrastructure/rocket-chat/adapters/Settings';
import type { RocketChatUserAdapter } from '../../../infrastructure/rocket-chat/adapters/User';
import { AbstractFederationApplicationService } from '../../AbstractFederationApplicationService';
import type { FederationUserTypingStatusEventDto } from '../../room/input/UserReceiverDto';

export class FederationUserServiceReceiver extends AbstractFederationApplicationService {
	private usersTypingByRoomIdCache: Map<string, Record<string, string>[]> = new Map();

	constructor(
		protected internalRoomAdapter: RocketChatRoomAdapter,
		protected internalUserAdapter: RocketChatUserAdapter,
		protected internalFileAdapter: RocketChatFileAdapter,
		protected internalNotificationAdapter: RocketChatNotificationAdapter,
		protected internalSettingsAdapter: RocketChatSettingsAdapter,
		protected bridge: IFederationBridge,
	) {
		super(bridge, internalUserAdapter, internalFileAdapter, internalSettingsAdapter);
	}

	private handleUsersWhoStoppedTyping(externalRoomId: string, internalRoomId: string, externalUserIdsTyping: string[]): void {
		const isTyping = false;
		const notTypingAnymore = this.usersTypingByRoomIdCache
			.get(externalRoomId)
			?.filter((user) => !externalUserIdsTyping.includes(user.externalUserId));

		const stillTyping = this.usersTypingByRoomIdCache
			.get(externalRoomId)
			?.filter((user) => externalUserIdsTyping.includes(user.externalUserId));

		notTypingAnymore?.forEach((user) => this.internalNotificationAdapter.notifyUserTypingOnRoom(internalRoomId, user.username, isTyping));
		this.usersTypingByRoomIdCache.set(externalRoomId, stillTyping || []);
	}

	public async onUserTyping(userTypingInput: FederationUserTypingStatusEventDto): Promise<void> {
		const { externalUserIdsTyping, externalRoomId } = userTypingInput;
		const federatedRoom = await this.internalRoomAdapter.getFederatedRoomByExternalId(externalRoomId);
		if (!federatedRoom) {
			return;
		}
		if (this.usersTypingByRoomIdCache.has(externalRoomId)) {
			this.handleUsersWhoStoppedTyping(externalRoomId, federatedRoom.getInternalId(), externalUserIdsTyping);
		}

		if (externalUserIdsTyping.length === 0) {
			return;
		}

		const federatedUsers = await this.internalUserAdapter.getFederatedUsersByExternalIds(externalUserIdsTyping);
		if (federatedUsers.length === 0) {
			return;
		}

		const isTyping = true;

		this.usersTypingByRoomIdCache.set(
			externalRoomId,
			federatedUsers.map((federatedUser) => {
				void this.internalNotificationAdapter.notifyUserTypingOnRoom(
					federatedRoom.getInternalId(),
					federatedUser.getUsername() as string,
					isTyping,
				);

				return {
					externalUserId: federatedUser.getInternalId(),
					username: federatedUser.getUsername() as string,
				};
			}),
		);
	}
}

import type { IFederationBridge } from '../../../domain/IFederationBridge';
import type { RocketChatFileAdapter } from '../../../infrastructure/rocket-chat/adapters/File';
import type { RocketChatRoomAdapter } from '../../../infrastructure/rocket-chat/adapters/Room';
import type { RocketChatSettingsAdapter } from '../../../infrastructure/rocket-chat/adapters/Settings';
import type { RocketChatUserAdapter } from '../../../infrastructure/rocket-chat/adapters/User';
import { AbstractFederationApplicationService } from '../../AbstractFederationApplicationService';

export class FederationUserServiceSender extends AbstractFederationApplicationService {
	constructor(
		protected internalRoomAdapter: RocketChatRoomAdapter,
		protected internalUserAdapter: RocketChatUserAdapter,
		protected internalFileAdapter: RocketChatFileAdapter,
		protected internalSettingsAdapter: RocketChatSettingsAdapter,
		protected bridge: IFederationBridge,
	) {
		super(bridge, internalUserAdapter, internalFileAdapter, internalSettingsAdapter);
	}

	public async afterUserAvatarChanged(internalUsername: string): Promise<void> {
		const federatedUser = await this.internalUserAdapter.getFederatedUserByInternalUsername(internalUsername);
		if (!federatedUser) {
			const internalUser = await this.internalUserAdapter.getInternalUserByUsername(internalUsername);
			if (!internalUser) {
				return;
			}
			await this.createFederatedUserIncludingHomeserverUsingLocalInformation(internalUser._id);
			return;
		}

		if (federatedUser.isRemote()) {
			return;
		}

		await this.updateUserAvatarExternally(federatedUser.getInternalReference(), federatedUser);
	}

	public async afterUserRealNameChanged(internalUserId: string, name: string): Promise<void> {
		const federatedUser = await this.internalUserAdapter.getFederatedUserByInternalId(internalUserId);
		if (!federatedUser) {
			const internalUser = await this.internalUserAdapter.getInternalUserById(internalUserId);
			if (!internalUser) {
				return;
			}
			await this.createFederatedUserIncludingHomeserverUsingLocalInformation(internalUser._id);
			return;
		}

		if (federatedUser.isRemote()) {
			return;
		}
		const externalUserProfileInformation = await this.bridge.getUserProfileInformation(federatedUser.getExternalId());
		if (!federatedUser.shouldUpdateDisplayName(externalUserProfileInformation?.displayName || '')) {
			return;
		}

		await this.bridge.setUserDisplayName(federatedUser.getExternalId(), name);
	}

	public async onUserTyping(internalUsername: string, internalRoomId: string, isTyping: boolean): Promise<void> {
		if (!this.internalSettingsAdapter.isTypingStatusEnabled()) {
			return;
		}
		const federatedUser = await this.internalUserAdapter.getFederatedUserByInternalUsername(internalUsername);
		if (!federatedUser) {
			return;
		}

		const federatedRoom = await this.internalRoomAdapter.getFederatedRoomByInternalId(internalRoomId);
		if (!federatedRoom) {
			return;
		}

		await this.bridge.notifyUserTyping(federatedRoom.getExternalId(), federatedUser.getExternalId(), isTyping);
	}
}

